/* ==UserStyle==
@name         User style for Gmail Notes by CloudHQ
@description  Changed: the background color and border color for the general edit field, 
 Added: a Cancel button (instead of a Cancel link), 
 Added: the word 'Note' before a note, 
 Changed: the background of a general note, 
 Added: Edit and Delete buttons (instead of links), 
 Changed: the icon plus colour for 'inline notes disabled', 
 Changed: the icon plus colour for 'inline notes enabled', 
 Changed: border of the mail body in inline notes mode, 
 Changed: background of the text that has an inline note attached, 
 Added/Changed: a yellow note icon that is displayed in your Gmail mail list to make mails with notes more notable
@namespace    ocor61.blogspot
@version      1.0.0
@author       OmeCor <ocor61@gmail.com> (ocor61.blogspot.com)
@homepageURL  https://ocor61.blogspot.com/2019/06/user-style-gmail-notes-by-cloudhq.html
@license      unlicense
@preprocessor stylus
==/UserStyle== */

@-moz-document url-prefix("https://gmail"), url-prefix("https://www.gmail.com"), url-prefix("https://mail.google.com"), domain("gmail.com"), domain("mail.google.com"), domain("google.com/mail") {
  /* CloudHQ Gmail Notes */
  .TO[data-tooltip^="-Notes"] .TN.aY7xie.NEehkc.aHS-bnr .qj:before,
  .TO[data-tooltip^="-Notes"] .TN.aY7xie.NEehkc.aHS-bnr .qj:after {
    visibility: hidden;
  }
  .TO[data-tooltip^="-Notes"] .TN.aY7xie.NEehkc.aHS-bnr .qj {
    background-image: url("data:image/png;base64,");
    background-size: 20px 20px;
  }

  .chq-notes-bar-container {
    background: beige;
    border: 1px solid beige;
    border-bottom-width: 2px;
  }
  .chq-notes-bar input {
    background: lightyellow;
    border: none;
    border-bottom: 1px solid rgba(0,0,0,0.0);
    border-radius: 3px 3px 0px 0px;
    padding: 0 0 0 10px;
  }
  .chq-notes-bar input:focus {
    border-bottom: 1px solid cornflowerblue;
    color: #222!important;
    cursor: text;
    opacity: 1!important;
    outline: none;
  }
  .chq-notes-bar .left [type="text"]::-webkit-input-placeholder {
    color: #444!important;
    font-weight: 400;
    opacity: .8!important;
  }
  .chq-notes-bar .left [type="text"]:active::-webkit-input-placeholder,
  .chq-notes-bar .left [type="text"]:focus::-webkit-input-placeholder {
    color: transparent!important;
    opacity: 1!important;
  }
  .chq-notes-bar-button.T-I-atl, .chq-notes-bar-button.T-I-ax7 {
    border-radius: 3px!important;
    font-family: 'Roboto Condensed',Roboto,RobotoDraft,Helvetica,Arial,sans-serif!important;
    font-weight: 400;
  }
  .chq-notes-bar-button.T-I-atl {
    height: 23px;
  }
  .chq-notes-bar-button.T-I.T-I-ax7 {
    background-color: rgb(247, 247, 247);
    border: 1px solid #ddd!important;
  }
  .chq-notes-bar-button.T-I.T-I-ax7:hover {
    background-color: rgb(247, 247, 247)!important;
    box-shadow: 0 1px 2px 0 rgba(221, 221, 221 ,0.651), 0 1px 3px 1px rgba(221, 221, 221 ,0.402)!important;
  }
  .chq-notes-bar-button[data-action="enable-inline"] {
    color: #C8151C!important;
    line-height: 1.7em;
    vertical-align: middle;
  }
  .chq-notes-bar-button[data-action="enable-inline"] span {
    display: none;
  }
  .chq-notes-bar-button[data-action="enable-inline"]:before {
    content: '☒'!important;
    font-size: 144%;
    line-height: 1.3em;
    margin-right: 1pt!important;
    vertical-align: middle;
  }
  .chq-notes-bar-button[data-action="disable-inline"] {
    color: green!important;
    line-height: 1.7em;
    padding-right: 3px;
    vertical-align: middle;
  }
  .chq-notes-bar-button[data-action="disable-inline"] span {
    margin-right: 2px!important;
  }
  .chq-notes-bar-button span {
    vertical-align: bottom;
  }
  .chq-notes-inline-mode {
    border: 1px dashed green;
    padding: 8px!important;
  }
  span[class$="chq-sel-comment"], span[class$="chq-full-width-comment"] {
    background: #FFFF8D!important;
    border: 1px solid #FFEB86;
    box-shadow: none!important;
    color: royalblue!important;
    font-family: 'Roboto Condensed',Roboto,RobotoDraft,Helvetica,Arial,sans-serif!important;
    font-weight: 400!important;
    letter-spacing: .2pt;
  }
  span[class$="chq-sel-comment"] {
    line-height: 16px!important;
  }
  span[class$="chq-full-width-comment"] {
    border: 1px solid #FFEB86;
    border-radius: 3px!important;
    font-family: Roboto,RobotoDraft,Helvetica,Arial,sans-serif!important;
    font-weight: 300!important;
    line-height: 1.3em!important;
    padding: 5px 12px 3px 75px!important;
    width: calc(100% - 90px)!important;
  }
  span[class$="chq-full-width-comment"]:before {
    background: #FFEB86;
    border-radius: 3px 0 0 3px!important;
    color: black;
    content: 'NOTE';
    cursor: text;
    font-weight: 700!important;
    letter-spacing: 1px;
    margin-right: 4px;
    margin-left: -79px;
    padding: 5px 10px 5px 13px;
    text-decoration: underline #444;
    height: 100%!important;
  }
  /* Gmail Notes Edit Note button: */
  span[class$="chq-sel-actions"] {
    padding-left: 10px;
  }
  span[class$="chq-sel-actions"] span {
    visibility: hidden;
  }
  span[class$="chq-sel-actions"] a {
    margin-right: -6px;
    margin-left: -8px;
    padding: 0 2px;
  }
  span[class$="chq-sel-actions"] a:nth-child(2) small {
    font-family: 'Roboto Condensed',Roboto,RobotoDraft,Helvetica,Arial,sans-serif!important;
    background: rgba(33, 150, 243, .7)!important;
    border-radius: 2px 0 0 2px;
    border-right: 1px dotted hotpink!important;
    color: darkblue!important;
    font-weight: 500!important;
    letter-spacing: .6pt!important;
    opacity: .9;
    padding: 1px 4px;
  }
  span[class$="chq-sel-actions"] a:nth-child(2) small:hover {
    background: rgba(33, 150, 243, 1)!important;
    color: white!important;
    opacity: 1;
  }
  /* Gmail Notes Delete Note button (×): */
  span[class$="chq-sel-actions"] a:nth-child(4) small {
    display: none;
  }
  span[class$="chq-sel-actions"] a:nth-child(4):after {
    background: orangered;
    border-radius: 0 2px 2px 0;
    color: white;
    content: '×';
    display: inline-flex;
    font-size: 1.3em;
    font-weight: 700;
    max-height: .75em;
    line-height: 1em;
    margin-right: 3px;
    opacity: .8;
    padding: 0 3px 3px;
    vertical-align: text-top;
    max-width: .95em;
  }
  span[class$="chq-sel-actions"] a:nth-child(4):hover:after {
    background: red;
    color: white;
    opacity: 1;
  }

  /* Gmail Notes Deletion Confirmation: */
  .inboxsdk__modal_fullscreen {
    z-index: 9503!important;
  }
  div.Kj-JD {
    z-index: 9501!important;
  }
  .Kj-JD-Jh.inboxsdk__modal_overlay {
    z-index: 9502!important;
  }

  /* Notes label in mail: */
  .hN[name="-Notes"] {
    font-size: 0px!important;
  }
  .at[title="-Notes"],
  .hN[name="-Notes"]:before {
    background-color: white!important;
    background-image: url("data:image/png;base64,");
    background-repeat: no-repeat;
    background-size: 15px;
    content: '       '!important;
    font-size: 13px!important;
    height: 15px!important;
    padding: 0px!important;
    vertical-align: middle!important;
    width: 15px!important;
  }
  /* Notes label in de maillijst */
  .at[title="-Notes"] {
    background-color: transparent!important;
  }
  .at[title="-Notes"] .av {
    font-size: 0px;
  }
  /*.hT.hU.hM[style="background-color: #fbe983"],
  .hU.hM[style="background-color: #fbe983; color: #594c05"],
  .hU.hM[style="background-color: rgb(251,233,131); color: rgb(89,76,5);"] {
    background: white!important;
    background-color: white!important;
    border-width: 0px!important;
  }
  .hU.hM.MiECge[style="background-color: #594c05; color: #fbe983"],
  .hU.hM.MiECge[style="background-color: rgb(89,76,5); color: rgb(251,233,131);"] {
    background: white!important;
    background-color: white!important;
    border-width: 0px!important;
    filter: grayscale(1);
  }*/
  /* Notes label Remove button: */
  /*.hV.hM[style="background-color: #fbe983; color: #594c05"],
  .hV.hM[style="background-color: rgb(251,233,131); color: rgb(89,76,5)"],
  .hW.hV.hM[style="background-color: #fbe983"],
  .hW.hV.hM[style="background-color: #fbe983; color: #594c05;"],
  .hW.hV.hM[style="background-color: rgb(251,233,131); color: rgb(89,76,5);"],
  .hV.hM.MiECge[style="background-color: rgb(89,76,5); color: rgb(251,233,131);"],
  .hW.hV.hM.MiECge[style="background-color: rgb(89,76,5); color: rgb(251,233,131);"] {
    background-color: white!important;
    color: white!important;
    display: none!important;
    margin-right: -10px!important;
    padding: 0px!important;
    visibility: hidden;
  }*/
  .hO[name="-Notes"],
  .hO[name="-Notes"]:before {
    display: none!important;
    margin: 0px!important;
    padding: 0px!important;
    visibility: hidden;
    width: 0px!important;
  }
}
