css:
@-moz-document domain("startpage.com") {


/***Hide Ad entries at top of results ***/ 
#adBlock{
    
     display: none !important;
    height: 0 !important;
    color:black !important;
     flex: none !important;
    background-color: #fcfcfd !important;
}


.columns, body{
     background-color: #fcfcfd !important;
}

.theme--default{
    --midnight: #2A2F41; 
    --midnight-light:  #75787C !important;
    --midnight-medium: #5C606F !important;
}

.button--link[disabled]{
    
    color: #fcfcfd!important;
}

/*
 Can never tell between purple and blue, change visited color to greenish
*/  
.w-gl__result a.w-gl__result-title:visited{
    color: #009688 !important;
}
a.w-gl__result-title h3 {
    
    background: #DFE2ED; 
    border: 1px solid #C9CAD6;
    border-radius: 14px 14px 14px 14px; 
    padding: 8px 7px 8px 12px; 
    margin: 0px 0px 4px 1px;
    box-shadow: inset white 0px 0px 1px 0px;
    display: inline-flex;
}

    
/****  All Original Settins For This Theme ****
    
    --color-main: var(--brand-blue);
    --color-highlight: var(--brand-blue-dark);

    --logo-effect: none;
    --image-effect: none;

    --text: var(--midnight);
    --text-title: var(--brand-blue-medium);
    --text-inverted: var(--white);
    --text-faded: var(--midnight-medium);
    --text-disabled: var(--midnight-light);
    --text-figcaption: var(--white);

    --background-base: var(--white);
    --background-main: var(--color-main);
    --background-main-focus: var(--brand-blue-light);
    --background-secondary: var(--whisper-dark);
    --background-alt: var(--whisper);
    --background-header: var(--whisper-light);
    --background-quotes: var(--midnight-dark);
    --background-overlay: var(--midnight);
    --background-disabled: var(--whisper);
    --background-hero: var(--whisper-light);
    --background-card: var(--whisper-light);
    --background-block: var(--color-main);
    --background-figcaption: rgba(0, 0, 0, 0.5);

    --border-default: 1px solid var(--whisper-dark);
    --border-separator: 2px solid var(--whisper-dark);

    --box-shadow-default: 0 2px 4px var(--whisper-dark);
    --box-shadow-medium: 0 2px 4px var(--midnight-light);
    --box-shadow-heavy: 0 2px 4px rgba(0,0,0, .25);

    --focus: var(--brand-blue);
    --focus-main: var(--brand-blue-light);
    --focus-secondary: var(--whisper);

    --link-text: var(--color-main);

    --form-control-background: var(--white);
    --form-control-border: 1px solid var(--midnight-light);
    --form-control-border-focus: 1px solid var(--brand-blue-light);

    --button-text: var(--whisper);

    --search-main: var(--brand-blue-dark);
    --search-secondary: var(--search-casal);
    --search-visited: var(--search-vivid-violet);

    --status-valid-main: var(--status-valid);
    --status-valid-focus: var(--status-valid-light);
    --status-valid-secondary: var(--status-valid-dark);

    --status-invalid-main: var(--status-invalid);
    --status-invalid-focus: var(--status-invalid-light);
    --status-invalid-secondary: var(--status-invalid-dark);
    
****End of Theme's Original settings ****/  
    
/****  All Original 'colors.css' For This Theme ****
    :root {
    --brand-blue: #6573ff;
    --brand-blue-alt: #8591f9;
    --brand-black: #202945;
    --brand-black-rgb: rgb(32, 41, 69);
    /* --brand-grey: #707070; */
    /* --brand-off-white: #f8f8f6; */
    /* --brand-white: #f1f1eb; */
    /*--white: #ffffff;
    --black: #000000;

    --brand-blue-light: #A3B1FF;
    --brand-blue-dark: #2E39B3;
    --brand-blue-medium: #374677;
    --midnight: #3B435C; /* Input text 
    --midnight-light: #CED1DD; /* input border 
    --midnight-medium: #7F869F;
    --midnight-dark: #202945; /* active text - black 

    --whisper: #F4F5FB; /* page background 
    --whisper-light: #FAFAFF; /* page background 
    --whisper-dark: #EBECF7; /* input lines; box-shadow select 

    --status-valid: #31B06E;
    --status-valid-dark: #0D6E49;
    --status-valid-light: #BAE8D0;

    --status-invalid: #C93038;
    --status-invalid-dark: #8F0C12;
    --status-invalid-light: #FFE3E3;

    --search-vivid-violet: #813A94;
    --search-vivid-violet-dark: #5C256B;
    --search-vivid-violet-light: #AD71BC;

    --search-casal: #0d6e5c;
    --search-casal-dark: #014c38;
    --search-casal-light: #d3ede6;
    }
****End of Theme's OriginalRoot Colors ****/
}
