css:
@-moz-document domain("lazyfoo.net") {
body
{
    background-color: #e8e6e3;
    color: #444;
    font-family: Helvetica Neue, Arial, sans serif;
}

h1, h2, h3, h4, h5, h6
{
    color: #444;
}

a, a.nav
{
    color: #0040A0 !important;
}

table.tutToC
{
    border: 1px solid #A0A0A0;
}

td.ToCTitle, td.tutLink, div.tutFooter
{
    color: #444;
    border: none;
}

td.ToCTitle, td.tutLink, div.tutFooter, div.tutPreface, div.tutText
{
    border-color: #A0A0A0;
    background-color: #f2f1ed;
}

div.tutCode
{
    border-color: #80B0E0;
    background-color: #e6eef2;
    color: #406080;
    font-family: Consolas, Lucida Sans Console, monospace;
}

div.tutText, div.tutCode
{
    padding: 10px;
}
}
