css:
@-moz-document domain(soundcloud.com) {

	
	/* Scrollbarr */
	::-webkit-scrollbar {
		width: 10px;
	}
	::-webkit-scrollbar-track {
		background: #353535;
	}
	::-webkit-scrollbar-thumb {
		background: #c24700;
		border-radius: 15px;
	}
	::-webkit-scrollbar-thumb:hover {
		background: #555;
	}

	body, .l-top.sc-border-box, .userMain .dropbar .dropbar__content, div#content, .commentForm__wrapper, .soundActions {
		background: rgb(10, 10, 10) !important;
		color: #fFffff;
		border: rgb(19, 19, 19);
	}

	#app {
		background: rgb(10, 10, 10);
	}
    
/* Home Button (Logo removal) */
a.header__logoLink {
    background-image: none !important;
    width: 150px !important;
}
a.header__logoLink:after {
    content: "SC MadDark";
    display: block;
    color: #ff5500;
    font-weight: 700;
    position: relative;
    top: 50%;
    left: 50%;
    transform: translate(-50%,-50%);
    text-align: center;
    font-size: 18px;
    font-family: Interstate,Lucida Grande,Arial,sans-serif;
}

/* Buttons */
	.sc-border-light-right, .sc-border-light-bottom, .sc-border-light-top, .sc-border-light-left {
		border-color: rgb(0 0 0);
	}
	.sc-button {
		transition: all 0.3s;
	}
	.sc-button:hover {
		color: rgb(0, 0, 0);
	}
	.sc-button.sc-button-like.sc-button-selected:hover {
		background-color: #000;
		color: #fff;
	}
	.l-sidebar-right .streamSidebar {
		transition: all 0.3s;
		top: -253px;
	}

	.l-sidebar-right .l-fixed {
		transition: all 0.3s;
		top: 80px !important;
	}

	.trackItem.active {
		background: rgb(255, 51, 0);
	}

	.trackItem:not(.m-disabled).active a.trackItem__trackTitle, .trackItem:not(.m-disabled).active .trackItem__numberWrapper {
		color: #fff;
	}

	.trackItem .trackItem__additional .soundActions button:hover {
		transition: all 0.3s;
		background: rgb(255, 255, 255);
	}

	.trackItem:not(.m-disabled).hover:hover a.trackItem__username, .trackItem:not(.m-disabled).hover:hover a.trackItem__trackTitle {
		transition: all 0.3s;
		color: #fff;
	}

	.trackItem:not(.m-disabled).active a.trackItem__username:hover, .trackItem:not(.m-disabled).active a.trackItem__trackTitle:hover, .trackItem:not(.active) a.trackItem__trackTitle:hover, .trackItem:not(.active) a.trackItem__username:hover {
		transition: all 0.3s;
		color: #000;
	}

	.trackItem:not(.m-disabled).active .trackItem__additional, .trackItem:not(.m-disabled).active .trackItem__additional .soundActions, .trackItem:not(.m-disabled).active .trackItem__actions .sc-button, .trackItem .trackItem__additional, .trackItem .trackItem__additional .soundActions, .trackItem .trackItem__actions .sc-button, .trackItem:not(.m-disabled).hover .trackItem__additional, .trackItem:not(.m-disabled).hover .trackItem__additional .soundActions, .trackItem:not(.m-disabled).hover .trackItem__actions .sc-button {
		background: none;
	}

	.trackItem:not(.m-disabled).active .trackItem__additional .soundActions button {
		min-width: 22px;
	}

	.trackItem:not(.m-disabled).active .trackItem__additional .soundActions button:hover, .trackItem:not(.m-disabled).hover .trackItem__additional .soundActions button:hover {
		transition: all 0.3s;
		background: rgb(255, 255, 255);
	}

	.trackItem:not(.m-disabled).active .trackItem__additional .soundActions .sc-button-active {
		background-color: rgb(255, 255, 255);
	}

	.trackItem:not(.active):hover {
		transition: all 0.1s;
		background: #ff5500;
	}

	.trackItem:not(.m-disabled).active .trackItem__actions, .trackItem:not(.m-disabled).hover .trackItem__actions {
		background: #fff;
	}

	/* Player controls */
	.paging-eof:before {
		background-color: #ff5500;
		border-radius: 5px;
	}

	.playControls__inner, .playControls__bg {
		background: #0a0a0a;
		border-top: none;
	}

	.playControls__queue .queue.m-visible {
		background: #000;
		color: #fff;
		border: none;
	}

	.playControls__queue .queue__scrollable .queue__itemWrapper {
		background: #000;
	}

	.playbackSoundBadge__actions button:hover {
		background: #fff;
	}

	/*comment form*/
	.commentForm .commentForm__wrapper .commentForm__input {
		background: #000000;
		border: none;
		outline: none;
		color: #fff !important;
	}

	.soundContext__usernameLink, .soundTitle__title span {
		color: #ff5000 !important;
	}

	.sc-button-pause {
		background: rgb(255, 51, 0);
	}

	.commentForm.m-active .commentForm__wrapper {
		background: none;
		border: none;
	}

	.commentForm.m-active .commentForm__wrapper .commentForm__input {
		transition: all 0.3s;
		border: 1px solid #ff5000;
		padding: 0px 0px 0px 8px;
	}

	/*menu*/
	.g-tabs-link {
		color: #fff !important;
		transition: all 0.2s;
	}

	.g-tabs-link:hover, .g-tabs-link:focus {
		border: none;
		color: #999999 !important;
	}

	.g-tabs-link.active {
		color: #ff5500 !important;
	}

	/* listening page */
	.listenEngagement {
		border: none;
		border-color: transparent;
	}

	.sc-button-like, .sc-button-repost, .sc-button-share, .sc-button-more {
		background-color: #fff;
		color: #000;
	}

	.sc-button-like:hover, .sc-button-repost:hover, .sc-button-share:hover, .sc-button-more:hover {
		background-color: #000;
		color: #fff;
		border-color: #ff5500;
	}

	.sc-button-group .soundActions__purchaseLink, .soundStats li .sc-ministats, .sc-ministats-group li .sc-ministats, .userBadge__usernameLink .sc-truncate, .playbackSoundBadge__titleLink, .playableTile__descriptionContainer .playableTile__mainHeading, .userBadgeListItem__title .userBadgeListItem__heading {
		transition: all 0.2s;
		color: #fff;
	}

	.sc-button-group .soundActions__purchaseLink:hover, .soundStats li .sc-ministats:hover, .sc-ministats-group li .sc-ministats:hover, .userBadge__usernameLink .sc-truncate:hover, .sc-truncate.playbackSoundBadge__lightLink:hover, .playableTile__usernameHeadingContainer .playableTile__usernameHeading:hover {
		transition: all 0.2s;
		color: #ff5500;
	}

	.truncatedAudioInfo.m-overflow.m-collapsed .truncatedAudioInfo__wrapper::after {
		background: rgb(0, 0, 0);
		background: linear-gradient(rgba(19, 19, 19, 0), rgba(19, 19, 19, 1)90%, rgb(19, 19, 19));
	}

	.g-link-problem {
		padding: 4px 10px 10px 10px;
	}

	.reportCopyright.sc-link-medium:hover, .playbackSoundBadge__titleLink:hover, .playbackTimeline__duration span, .playableTile__descriptionContainer .playableTile__mainHeading:hover, .userBadgeListItem__title .userBadgeListItem__heading:hover {
		color: #ff5500;
	}

	/* description */
	.truncatedAudioInfo__content p {
		color: #fff;
	}

	.removeFromNextUp, .removeFromNextUp:hover {
		background-color: #fff;
	}

	li.g-tabs-item a.g-tabs-link:not(.active) {
		color: rgb(255, 85, 0) !important;
	}

	.g-tabs-link.active, .g-tabs-link.active:hover, .g-tabs-link:hover {
		border-color: rgb(255, 85, 0);
		color: rgb(255, 85, 0);
		border-bottom: solid 1px;
		cursor: default;
	}

	.g-tabs-link:hover {
		cursor: pointer;
	}

	.l-hero-content {
		margin: 25px;
	}

	/*User Added*/
	.header__navMenu a.header__navMenuItem[data-menu-name="home"], .header__navMenu a.header__navMenuItem[data-menu-name="home"]:hover, .header__navMenu a.header__navMenuItem.selected[data-menu-name="home"], .header__navMenu a.header__navMenuItem.selected[data-menu-name="home"]:hover, .header__navMenu a.header__navMenuItem[data-menu-name="stream"], .header__navMenu a.header__navMenuItem[data-menu-name="stream"]:hover, .header__navMenu a.header__navMenuItem.selected[data-menu-name="stream"], .header__navMenu a.header__navMenuItem.selected[data-menu-name="stream"]:hover {
		color: transparent !important;
		transition: all 0.2s;
	}

	a.header__navMenuItem[data-menu-name="home"]:after {
		content: "Discover";
	}

	a.header__navMenuItem[data-menu-name="stream"]:after {
		content: "Home";
	}

	a.header__navMenuItem[data-menu-name="home"]:after, a.header__navMenuItem[data-menu-name="stream"]:after {
		transition: all 0.2s;
		font-size: 17px;
		position: absolute;
		top: 50%;
		left: 50%;
		transform: translate(-50%, -50%);
		color: rgb(255, 116, 0);
		font-weight: 500;
		font-family: "Interstate", "Lucida Grande", "Lucida Sans Unicode", "Lucida Sans", Garuda, Verdana, Tahoma, sans-serif;
	}

	.header__navMenu a.header__navMenuItem:hover::after, .header__navMenu a.header__navMenuItem.selected:hover::after {
		color: rgb(51, 51, 51) !important;
	}

	.header__navMenu a.header__navMenuItem:hover, .header__navMenu a.header__navMenuItem:hover {
		background: rgb(255, 116, 0);
		transition: all 0.2s;
	}

	.queueItemView:hover, .queueItemView.m-active {
		background: rgb(80, 80, 80) !important;
	}

	.queueItemView__meta a {
		color: rgb(255, 255, 255) !important;
		opacity: 1;
		font-size: 14px;
	}

	.queueItemView__title a {
		color: rgb(255, 255, 255);
	}

	.queueItemView__meta a:hover, .queueItemView__title a:hover {
		color: rgb(255, 85, 0) !important;
	}

	.queueItemView:hover, .queueItemView.m-active {
		background: rgb(80, 80, 80) !important;
	}

	button.sc-button-like[title="Unlike"]:hover {
		background: rgb(0, 0, 0);
	}

	.queue__panel {
		background-color: rgb(255, 52, 0);
		color: rgb(255, 255, 255);
		background-image: linear-gradient(to top, rgb(255, 52, 0), rgb(255, 118, 0));
	}

	.modal.modalWhiteout {
		background-color: rgba(45, 45, 45, 0.9);
	}

	.tabs .tabs__tabs,.tabs .tabs__content .g-modal-section,.inboxItem.sc-border-box.active {
		background: rgb(10, 10, 10);
	}

	.tabs .tabs__content ul li a, .conversationMessages__item .conversationMessage .sc-media-content div {
		color: rgb(255, 255, 255);
	}

	article.infoStats {
		color: rgb(255, 255, 255);
	}

	a.sc-link-dark, .sidebarHeader__more {
		color: rgb(255, 85, 0);
	}

	a.sc-link-dark:hover,.sc-truncate a:hover {
		color: #fff;
		transition: all 0.3s;
	}

	.truncatedUserDescription.m-overflow.m-collapsed .truncatedUserDescription__wrapper::after {
		background: linear-gradient(rgba(255, 255, 255, 0), rgb(19, 19, 19));
	}

	.audibleEditForm__form {
		background-color: rgb(8, 8, 8);
	}

	.audibleEditForm__form div.tokenInput__wrapper {
		background: rgb(105, 105, 105) !important;
		border: 1px solid rgb(204, 204, 204);
	}

	.linkMenu .linkMenu__list {
		background: rgb(23, 23, 23);
	}

	button.sc-button[aria-label="More"] {
		background-color: rgb(255, 87, 34);
	}

	.modalWhiteout .modal__modal {
		background: rgb(23, 23, 23);
	}

	.commentItem.m-isReply.m-creatorComment {
		background-color: rgb(56, 56, 56);
	}

	.sc-background-light {
		background-color: rgb(31, 31, 31);
	}

	.autoTagger__wrapper .tokenInput__wrapper.tagInput__wrapper.sc-input.sc-input-large {
		background-color: rgb(19, 19, 19);
	}

	.tagInput__inputWrapper input#tokenInput__tags {
		background: rgb(31, 31, 31);
		border-radius: 10px;
		color: rgb(255, 85, 0);
	}

	li.suggestedTags__listItem, li.suggestedTags__listItem.selected {
		background-color: rgb(70, 70, 70) !important;
	}

	.visualSound__wrapper.g-all-transitions-300 {
		background: rgb(10, 10, 10);
	}

	.container {
		background: rgb(35, 35, 35);
	}

	.quotaMeterWrapper.quotaMeter__noSidePadding,.conversation__actions.sc-border-box.g-z-index-content {
		background: rgba(0, 0, 0, 0);
	}

	.streamSidebar article.dashbox {
		position: absolute;
		opacity: 0;
		width: 0;
	}

	.inboxItem.active, .inboxItem.unread, .inboxItem:focus, .inboxItem:hover {
		background: rgb(255,87,34);
	}
	.commentItem.m-creatorComment, .commentItem.m-isReply {
		background-color: rgb(47, 47, 47);
		border-top: 2px solid rgb(19, 19, 19);
		color: rgb(0, 0, 0) !important;
		border-bottom: 2px solid rgb(19, 19, 19);
	}
	.commentItem.m-isReply a {color:#fff;}

	.modal.showBackground .image.m-sound.readOnly .sc-artwork:hover {
		cursor: pointer;
	}
	.dropdownMenu .dropdownContent__header, 
	.dropdownMenu .dropdownContent__main, 
	.dropdownMenu .dropdownContent__footerLink {
		background-color: rgba(10, 10, 10, 1);
		border:0px;
	}
	.dropdownMenu .dropdownContent__main ul .dropdownContent__listItem {
		background-color: rgba(10, 10, 10, 1);
		transition: all 0.3s;
		border: 0;
	}
	.dropdownMenu .dropdownContent__main ul .dropdownContent__listItem div .notificationBadge--unread {background-color: rgba(255, 85, 0, 1);}
	.dropdownMenu .dropdownContent__main ul .dropdownContent__listItem div .notificationBadge--unread * {color: rgba(255, 255, 255, 1);}
	.dropdownMenu .dropdownContent__main ul .dropdownContent__listItem:hover {background-color: rgba(255, 85, 0, 0.5); color: #fff;}
	.dropdownMenu .dropdownContent__main ul .dropdownContent__listItem:hover * {color: #fff;}
	.dropdownMenu .dropdownContent__main ul .dropdownContent__listItem:hover button.sc-button.sc-button-small.sc-button-follow {color:#000;}
	.uploadMain div {
		background: #0a0a0a;
		color: #fff;
	}
	.sc-classic .modal.modalWhiteout {background-color: rgba(10, 10, 10,0.95);}

	.playControls__wrapper button.skipControl, .playControls__wrapper button.playControl  {
		background-size: 50px;
		padding: 0 30px;
	}

	.playControls__soundBadge {
		position: absolute;
		left: 0;
		bottom: 4em;
		background-color: #c04403;
	}
	.playControls__wrapper button.skipControl, .playControls__wrapper button.playControl  {
		background-size: 50px;
		padding: 0 30px;
	}

	.playControls__soundBadge {
		position: absolute;
		left: 0;
		bottom: 4em;
		width: 100% !important;
		background-color: rgba(16, 11, 8, 0.9);
	}

	.playControls .playControls__queue {
		position: absolute;
		right: 0;
		bottom: 8em;
	}
	.volume__sliderWrapper {
		z-index: 10000;
	}
.audibleEditForm__form, .tabs__tabs, .editTrackItem__additional {
    background-color: transparent !important;
    background: transparent !important;
}
.editTrackItem.m-active {
    background-color: #313131 !important;
}
.editTrackItem.m-hover {
    background-color: rgb(255, 85, 0) !important;    
}
/* Removal of annoying, not saving cookies */
#onetrust-consent-sdk {
  display: none !important;
}
}
