css:
@-moz-document domain("www.twitch.tv") {
.tw-root--theme-dark .chat-room {
    /*    background: rgba(42, 193, 22, .88)!important;
*/
    background: none!important;
}
/*.tw-root--theme-dark .channel-root__right-column {
    background: rgba(252, 255, 3, .09)!important;
}*/
.whispers--theatre-mode.whispers--right-column-expanded {
    right: 46rem!important;
}
.tw-root--theme-dark .pl-controls-top {
    background: linear-gradient(180deg, rgba(0, 0, 0, .75), rgba(0, 255, 0, 0) 100%),
    linear-gradient(185deg, rgba(22, 0, 75, .5), rgba(255, 247, 0, 0) 33%),
    linear-gradient(175deg, rgba(64, 0, 75, .5), rgba(0, 0, 255, 0) 33%);
}
.tw-root--theme-dark .channel-page__right-column {
    background-color: rgba(145, 217, 145, .67)!important;
}
.tw-theme--dark .channel-page__right-column {
    background-color: rgba(192, 191, 120, .85)!important;
}
.tw-theme--dark .tw-c-background-alt-2 {
    background-color: rgba(42, 214, 47, .76)!important;
}
.tw-theme--dark .c-background-alt-2 {
    background-color: rgba(65, 22, 205, .78)!important;
}
.tw-theme--dark .chat__pane {
    background-color: rgba(223, 50, 50, .84)!important;
}
.tw-theme--dark .chat-room__pane {
    background-color: rgba(206, 109, 19, .9)!important;
    border: none!important;
}
.tw-theme--dark .chat__header {
    background-color: rgba(203, 17, 17, .95)!important;
    border: none!important;
}
.tw-theme--dark .chat-room__header {
    background-color: rgba(218, 20, 179, .88)!important;
    border: none!important;
}
.tw-theme--dark .chat__pane {
    background-color: rgba(214, 138, 13, .89)!important;
    border: none!important;
}
.tw-theme--dark .tw-textarea {
    background-color: rgba(193, 186, 56, .78);
}
.tw-theme--dark .pinned-cheer__bounding-box {
    background-color: rgba(191, 180, 24, .78);
    border: none!important;
}



.theme--dark .channel-page__right-column {
    background-color: rgb(56, 152, 54)!important;
}
.theme--dark .tw-c-background-alt-2 {
    background-color: rgba(220, 137, 7, .78)!important;
}
.theme--dark .c-background-alt-2 {
    background-color: rgba(229, 59, 59, .76)!important;
}
.theme--dark .chat__pane {
    background-color: rgba(43, 26, 213, .83)!important;
}
.theme--dark .chat-room__pane {
    background-color: rgba(30, 191, 183, .71)!important;
    border: none!important;
}
.channel-page__video-player--theatre-mode {
    width: 100%!important;
}
.theme--dark .chat__header {
    background-color: rgba(198, 142, 142, .9)!important;
    border: none!important;
}
.theme--dark .chat-room__header {
    background-color: rgba(207, 4, 4, .84)!important;
    border: none!important;
}
.theme--dark .chat__pane {
    border: none!important;
}
.theme--dark .tw-textarea {
    background-color: rgba(235, 182, 255, .75);
}
/*.right-column--theatre .simplebar-content {
    text-align: right;
}*/
.video-player--theatre .player-buttons-right {
    right: 40rem!important;
}
.video-player--theatre.video-player--logged-in .video-player__container {
    bottom: 0!important;
}
.persistent-player.persistent-player--theatre {
    width: 100%!important;
}
.theme--dark .pinned-cheer__bounding-box {
    background-color: rgba(10, 223, 213, .77);
    border: none!important;
}
/* settings panels */
.tw-root--theme-dark .tw-c-background-base {
    background-color: rgba(25, 0, 48, .8)!important;
}
.tw-root--theme-dark .tw-border-l {
    background: none;
}
.tw-theme--dark .tw-border-l {
    border-left: none !important;
}
/*.tw-theme--dark .pl-controls-top{
    background: none!important;
}*/
.tw-theme--dark .tw-c-text {
    text-shadow: black 1px 1px 0, black -1px -1px 0, black -1px 1px 0, black 1px -1px 0;
}

.tw-theme--dark .tw-c-background {
    background: none!important;
}

.tw-theme--dark .vod-message.vod-message--focused,
.tw-theme--dark .vod-message:hover {
    background: rgba(244, 5, 5, .83)!important;
}
.tw-root--theme-dark .tw-c-background-alt-2 {
    background-color: rgba(198, 219, 10, .81)!important;
}

.tw-root--theme-dark .tw-border-l {
    border-left: 1px solid rgba(68, 7, 175, 0)!important;
}

/* text entry */
.tw-root--theme-dark .tw-textarea {
    background: rgba(45, 9, 78, .25);
}

/* smiley settings bottom bit */
.tw-root--theme-dark .tw-c-background-alt {
    background-color: rgba(0, 0, 0, .05)!important;
}
.tw-root--theme-dark .tw-c-background-base.whispers-thread,
.whispers-thread--focused {
    background-color: rgba(68, 7, 175, .81)!important;
}

/* Whispers background */
.tw-root--theme-dark .tw-c-background-base.whispers-threads-box__container,
.whispers-threads-box__container--open {
    background-color: rgba(37, 0, 92, .75)!important;
}
#default-player > div > div.hover-display div.pl-controls-bottom {
    padding-right: 1rem!important;
}
.tw-theme--dark .vod-message.vod-message--focused,
.tw-theme--dark .vod-message,
.tw-root--theme-dark .tw-c-text-base {
    color: rgba(21, 228, 10, .5)!important;
    text-align: right;
}
.tw-root--theme-dark .chat-settings {
    background-color: rgba(116, 9, 227, .75)!important;
}
#root div.tw-flex div.right-column.right-column--theatre {
    text-align: right; 
    background: linear-gradient(270deg, rgba(0, 0, 0, .75), rgba(0, 255, 0, 0) 100%),
    linear-gradient(252deg, rgba(22, 0, 75, .9), rgba(255, 247, 0, 0) 55%),
    linear-gradient(295deg, rgba(64, 0, 75, .9), rgba(0, 0, 255, 0) 45%);
}
}
