css:
@-moz-document domain("support.discordapp.com") {
html, body, input, footer
{background: #171717;
border-color: black;
color: #bcbcbc}


/* {color: inherit} */
.section-tree-title a, .wysiwyg-color-black
{color: #ccc}

.article-list-item a, .wysiwyg-color-blue

{color: rgba(114, 137, 218, 1)}

.flex-item
{background-color: inherit;
    box-shadow: 0px 12px 30px rgba(0, 0, 0, 0.33);
}

.flex-item:hover {
    box-shadow: 0 9px 30px rgba(114, 137, 218, .2);}


.search input[type="search"] 
{border: 1px solid #333}

.search input[type="search"]:focus, .article-return-to-top a
{color: inherit}

textarea#community_comment_body,
#hc-wysiwyg, 
#new_request input, #new_request textarea, .nesty-input
{
    border: 1px solid #333;
    background: inherit;
    color: inherit
}

#hc-wysiwyg [role="toolbar"] {
    background: #131313;
    border-bottom: 1px solid #333;}

.comment-overview,
.search-results-list > li:first-child,
.search-results-list > li
,
.article-sidebar

{   border-bottom: 1px solid #333;
    border-top: 1px solid #333}

 
/* {   border-bottom: 1px solid #333;} */

 .article-relatives, .article-votes, .article-return-to-top
{   border-top: 1px solid #333;}


.comment, .article-list-item, .striped-list-item, .topic-header
{border-bottom: 1px solid #333;}

.pagination li:hover:not(.pagination-current)
{background-color: #111;}

.pagination li a:hover:not(.pagination-current) {color: #bfbfbf}

#hc-wysiwyg [role="separator"]
{background: #333}

.search::before
{background-color: inherit}

.container-divider, .topic-header .dropdown {
    border-top: 1px solid #333;}

.logo a, li::before, .avatar img[src*="default_avatar.png" ]
{filter: invert(.83);}

.breadcrumbs li a:hover 
{
    color: #bbb;}

.dropdown-menu{
    background: #131313dd;
    border: 1px solid #333;
    border-radius: 3px;
    box-shadow: 0 2px 12px rgba(0, 0, 0, .3);
    display: none;}

.sidenav-item {border: 1px solid #333; color: inherit}
.sidenav-item:hover {
    box-shadow: 0 7px 18px rgba(0, 0, 0, 0.8);}
    
.dropdown-menu [role="menuitem"] {
    color: inherit}

.dropdown-menu [role="menuitem"]:hover {
    background: #0006;
    color: #dedede;
    }


.article-body h3 {background: #252b2980;
    border: 2px solid #b4e1cd26;}


/* scrollbar */

/* width */
::-webkit-scrollbar
    {width: .9rem;}

/* Track */
::-webkit-scrollbar-track
    {background: inherit;
    opacity: .4}
 
/* Handle/bar */
::-webkit-scrollbar-thumb 
	{background: rgba(112, 112, 112, .3);
        border-radius: .33rem;}

/* Handle on hover */
::-webkit-scrollbar-thumb:hover 
	{background: rgba(114, 137, 218, 1);}

