css:
@-moz-document domain(skyscrapercity.com) {

    body{
        background: #aaaaaa;
        padding: 30px 0 0 0;
    }

    center > table {
        width: 820px !important;
    }
    
    center > table > tbody > tr > td > a > img{
        content: url('https://upload.wikimedia.org/wikipedia/commons/thumb/3/34/SkycraperCity_logo.svg/1920px-SkycraperCity_logo.svg.png');
        width: 200px;
    }
    
    center > table > tbody > tr > td table{
        border-width: 0px !important;
    }
    
    center > center > table > tbody > tr > td > img{
        display: none !important;
    }
    
    center > center > table {
        width: 820px !important;
    }

    ::selection {
        background: #ffd885
    }

    ::-moz-selection {
        background: #ffd885
    }

    #topanchor {
        position: absolute;
        top: 0;
        left: 0;
        visibility: hidden
    }

    html,
    body {
        height: 100%
    }

    body {
        background: #ebebeb;
        color: #454545;
        font: 13px "Hind", sans-serif;
        min-width: 1024px;
        margin: 0;
        padding: 145px 0 0 0;
        -webkit-font-smoothing: subpixel-antialiased;
        text-shadow: 1px 0 1px rgba(43, 43, 43, 0.004);
        -webkit-text-size-adjust: none
    }

    body.forumdisplaycategory {
        background-color: #fdfdfd
    }

    .no-min-width {
        min-width: 0
    }

    .min-width-728 {
        min-width: 728px
    }

    .min-width-728.mobile-body {
        min-width: 0;
        padding-top: 110px
    }

    .fixed-container {
        max-width: 1170px;
        margin: 0 auto
    }

    a.ad-free-tag {
        background-color: #2e2e2e;
        color: white
    }

    .responsive-text-show {
        display: none;
        visibility: hidden
    }

    H1,
    H2,
    H3,
    H4,
    H5,
    H6 {
        font: normal 900 20px 'Open Sans', sans-serif
    }

    a {
        color: #de7300;
        text-decoration: none
    }

    a:hover,
    a:active,
    a:focus {
        color: #454545;
        text-decoration: none;
        transition: all .15s ease-in-out
    }

    TD,
    TH,
    P,
    LI {
        font: 14px 'Hind', sans-serif
    }

    STRONG,
    B {
        font-weight: 600
    }

    SELECT {
        font: 11px 'Hind', sans-serif;
        border: 1px solid #d8d8d8;
        color: #454545;
        padding: 8px 0 8px 10px;
        cursor: pointer;
        outline: 0
    }

    OPTION,
    OPTGROUP {
        font: 11px 'Hind', sans-serif;
        cursor: pointer
    }

    INPUT[type="text"],
    INPUT[type="password"] {
        padding: 7px
    }

    IMG {
        border: 0
    }

    A:hover {
        text-decoration: none;
        -webkit-transition: all .15s ease-in-out;
        transition: all .15s ease-in-out
    }

    LEGEND {
        color: #454545;
        font: 900 13px 'Open Sans', sans-serif
    }

    FIELDSET {
        border: 1px solid #d8d8d8
    }

    PRE {
        max-width: 600px
    }

    .ui-state-hover,
    .ui-widget-content .ui-state-hover,
    .ui-widget-header .ui-state-hover,
    .ui-state-focus,
    .ui-widget-content .ui-state-focus,
    .ui-widget-header .ui-state-focus {
        background-image: none !important;
        background-color: #a9a9a9
    }

    .tborder {
        background-color: #fbfbfb;
        color: #454545;
        border: 1px solid #d8d8d8;
        -moz-box-shadow: 0 3px 1px 0 rgba(0, 0, 0, 0.02);
        -webkit-box-shadow: 0 3px 1px 0 rgba(0, 0, 0, 0.02);
        -o-box-shadow: 0 3px 1px 0 rgba(0, 0, 0, 0.02);
        box-shadow: 0 3px 1px 0 rgba(0, 0, 0, 0.02);
        clear: left !important
    }

    .tab_list .tborder {
        clear: none !important;
        padding: 4px !important;
        text-align: center
    }

    .tcat {
        background: #f9f9f9;
        color: #454545;
        font: normal 900 18px 'Open Sans', sans-serif;
        border-bottom: 1px solid #d8d8d8;
        position: relative;
        -moz-box-shadow: 0 3px 3px 0 rgba(0, 0, 0, 0.05);
        -webkit-box-shadow: 0 3px 3px 0 rgba(0, 0, 0, 0.05);
        -o-box-shadow: 0 3px 3px 0 rgba(0, 0, 0, 0.05);
        box-shadow: 0 3px 3px 0 rgba(0, 0, 0, 0.05);
        padding: 15px 20px
    }
    
    .tcat a:visited, .tcat_avisited, .tcat a:link, .tcat_alink  {
        color: #22229C !important;
    }
    

    .thead {
        background: #d8d8d8;
        color: #454545;
        font: normal 900 14px 'Open Sans', sans-serif;
        padding: 5px 10px;
        -moz-text-shadow: 0 1px 0 rgba(255, 255, 255, 0.5);
        -webkit-text-shadow: 0 1px 0 rgba(255, 255, 255, 0.5);
        -o-text-shadow: 0 1px 0 rgba(255, 255, 255, 0.5);
        text-shadow: 0 1px 0 rgba(255, 255, 255, 0.5)
    }

    .alt1,
    .alt1Active,
    .alt2,
    .alt2Active {
        background: #f9f9f9;
        color: #454545;
        border-bottom: 1px solid #d8d8d8;
        padding: 15px 20px
    }

    .inlinemod {
        background: #FFC;
        color: #454545
    }

    .wysiwyg {
        background: #f9f9f9;
        color: #454545;
        font: 13px 'Hind', sans-serif;
        margin: 5px 10px 10px;
        padding: 0
    }

    INPUT {
        outline: 0
    }

    TEXTAREA,
    .bginput {
        font: 15px 'Hind', sans-serif;
        outline: none !important;
        padding: 3px
    }

    TEXTAREA {
        resize: vertical
    }

    .bginput OPTION,
    .bginput OPTGROUP {
        font: 13px 'Hind', sans-serif
    }

    .button {
        font: 12px 'Hind', sans-serif
    }

    .smallfont {
        font: normal normal 12px/12px 'Hind', sans-serif
    }

    .time {
        color: #a9a9a9
    }

    .navbits {
        padding: 12px 10px 8px 20px;
        background-color: #f9f9f9;
        font-size: 14px;
        border-top: 1px solid rgba(0, 0, 0, 0.06);
        border-bottom: 1px solid rgba(0, 0, 0, 0.06);
        position: absolute;
        z-index: 499;
        right: 0;
        left: 0;
        top: 90px
    }

    .navbits a {
        text-decoration: underline;
        font-weight: normal;
        color: #000
    }

    .navbits .firstbreadcrumb {
        position: relative;
        top: 2px;
        font-size: 19px;
        margin-right: 5px
    }

    .highlight {
        color: #F00;
        font-weight: bold
    }

    .fjsel {
        background: #3e5c92;
        color: #e0e0f6
    }

    .fjdpth0 {
        background: #f7f7f7;
        color: #454545
    }

    .panel {
        background: #f9f9f9;
        color: #454545;
        padding: 5px
    }

    .panelsurround,
    .content {
        background: #f9f9f9;
        color: #454545
    }

    .vbmenu_control {
        background: inherit;
        color: #454545;
        font: bold 12px 'Hind', sans-serif;
        padding: 3px 6px;
        white-space: nowrap
    }

    #vB_Editor_QE_1_controls,
    #vB_Editor_QE_2_controls,
    #vB_Editor_QE_3_controls,
    #vB_Editor_QE_4_controls,
    #vB_Editor_QE_5_controls,
    #vB_Editor_QE_6_controls,
    #vB_Editor_QE_7_controls,
    #vB_Editor_QE_8_controls,
    #vB_Editor_QE_9_controls,
    #vB_Editor_QE_10_controls {
        position: relative
    }

    div#vB_Editor_QE_1_popup_forecolor_menu,
    div#vB_Editor_QE_2_popup_forecolor_menu,
    div#vB_Editor_QE_3_popup_forecolor_menu,
    div#vB_Editor_QE_4_popup_forecolor_menu,
    div#vB_Editor_QE_5_popup_forecolor_menu,
    div#vB_Editor_QE_6_popup_forecolor_menu,
    div#vB_Editor_QE_7_popup_forecolor_menu,
    div#vB_Editor_QE_8_popup_forecolor_menu,
    div#vB_Editor_QE_9_popup_forecolor_menu,
    div#vB_Editor_QE_10_popup_forecolor_menu {
        top: 32px !important;
        left: 100px !important
    }

    .vbmenu_control A:link,
    .vbmenu_control_alink {
        color: #454545;
        text-decoration: none
    }

    .vbmenu_control A:visited,
    .vbmenu_control_avisited {
        color: #454545;
        text-decoration: none
    }

    .vbmenu_control A:hover,
    .vbmenu_control A:active,
    .vbmenu_control_ahover {
        color: #454545;
        text-decoration: underline
    }

    .vbmenu_popup,
    .xda-popup {
        background: #f9f9f9;
        color: #454545;
        border: 1px solid #d8d8d8;
        padding: 0;
        -moz-box-shadow: 3px 5px 5px rgba(0, 0, 0, 0.4);
        -webkit-box-shadow: 3px 5px 5px rgba(0, 0, 0, 0.4);
        -o-box-shadow: 3px 5px 5px rgba(0, 0, 0, 0.4);
        box-shadow: 3px 5px 5px rgba(0, 0, 0, 0.4);
        clip: auto !important;
        z-index: 50;
        line-height: 1em;
        font-size: 14px
    }

    .vbmenu_popup TABLE,
    .xda_popup TABLE {
        min-width: 150px
    }

    .vbmenu_option {
        background: #f9f9f9;
        color: #454545;
        font: 13px 'Hind', sans-serif;
        white-space: nowrap;
        cursor: pointer;
        padding: 5px
    }

    .vbmenu_option A:link,
    .vbmenu_option_alink {
        color: #454545;
        text-decoration: none
    }

    .vbmenu_option A:visited,
    .vbmenu_option_avisited {
        color: #454545;
        text-decoration: none
    }

    .vbmenu_option A:hover,
    .vbmenu_option A:active,
    .vbmenu_option_ahover {
        color: #454545;
        text-decoration: none
    }

    .vbmenu_hilite {
        background: #f9f9f9;
        color: #de7300;
        font: 13px 'Hind', sans-serif;
        white-space: nowrap;
        cursor: pointer;
        padding: 5px
    }

    .vbmenu_hilite A:link,
    .vbmenu_hilite_alink {
        color: #de7300;
        text-decoration: none
    }

    .vbmenu_hilite A:visited,
    .vbmenu_hilite_avisited {
        color: #de7300;
        text-decoration: none
    }

    .vbmenu_hilite A:hover,
    .vbmenu_hilite A:active,
    .vbmenu_hilite_ahover {
        color: #de7300;
        text-decoration: none
    }

    .vbulletin_editor_table {
        width: 100%
    }

    .vBulletin_editor {
        background-color: #f9f9f9 !important;
        border: 0 !important;
        padding: 0 !important
    }

    .postbit-qacomment .vBulletin_editor {
        border: none !important
    }

    .controlbar {
        padding: 0 !important;
        margin: 0 !important
    }

    .controlbar,
    .controlbar TD {
        line-height: 5px !important
    }

    .controlbar .fieldset {
        margin-top: 15px
    }

    .vbulletin-editor-text-area {
        margin-right: 125px;
        margin-top: 8px
    }

    .vbulletin-editor-text-area TEXTAREA {
        width: 100%;
        height: 315px;
        padding: 0;
        margin: 0
    }

    .vbulletin-editor-smilies {
        float: right;
        width: 110px;
        height: 330px
    }

    fieldset#vB_Editor_001_smiliebox {
        border: 0
    }

    fieldset#vB_Editor_001_smiliebox legend {
        margin-left: 10px
    }

    .vbmenu_searchhits {
        display: block;
        float: right;
        font-size: 10px;
        padding-left: 2em
    }

    .vbmenu_searchkeys {
        display: block;
        float: left;
        font-size: 11px
    }

    #wrap {
        margin: 0;
        padding: 10px 25px 0;
        float: left
    }

    .page {
        margin: 0 10px 5px 10px;
        display: block;
        background: none;
    }

    .forumdisplaycategory .page {
        margin: 0 0 0 335px
    }

    .page.no-sidebar {
        margin-right: 20px
    }

    .content {
        background: #f9f9f9;
        color: #454545;
        border: 1px solid #d8d8d8;
        padding: 5px;
        clear: left;
        margin-top: 15px;
        margin-bottom: 3px
    }

    #deviceInfo-specs .content {
        clear: none
    }

    .boxShadow,
    .box-shadow {
        -moz-box-shadow: 0 3px 1px 0 rgba(0, 0, 0, 0.02);
        -webkit-box-shadow: 0 3px 1px 0 rgba(0, 0, 0, 0.02);
        -o-box-shadow: 0 3px 1px 0 rgba(0, 0, 0, 0.02);
        box-shadow: 0 3px 1px 0 rgba(0, 0, 0, 0.02)
    }

    .new-button {
        font-size: 13px;
        display: inline-block;
        max-width: 135px;
        text-align: left;
        float: left;
        margin-right: 10px;
        white-space: nowrap
    }

    #search-input,
    .devdbSearch,
    #gsc-input,
    .search-input,
    #gsc-input-top {
        background: transparent no-repeat 10px 7px;
        color: #a9a9a9;
        float: left;
        padding: 7px 7px 7px 32px !important;
        font-size: 13px;
        outline: 0;
        margin-top: 14px;
        margin-left: 15px;
        border: 1px solid #d8d8d8;
        width: 185px;
        height: 16px
    }

    #gsc-input-top {
        float: inherit;
        background-color: #f9f9f9;
        padding: 8px 8px 8px 35px !important
    }

    #search-input:focus,
    #gsc-input:focus {
        border: 1px solid #a9a9a9
    }

    .new-button,
    INPUT[type="submit"],
    INPUT[type="reset"],
    INPUT.button,
    BUTTON {
        border: 1px solid #d8d8d8;
        background-color: #f9f9f9;
        color: #454545 !important;
        padding: 8px 10px;
        font-size: 13px;
        cursor: pointer
    }

    .new-button:hover,
    INPUT[type="submit"]:hover,
    INPUT[type="reset"]:hover,
    INPUT.button:hover,
    BUTTON:hover {
        border: 1px solid #d8d8d8;
        color: #454545;
        background-color: #ebebeb;
        -webkit-transition: all .15s ease-in-out;
        transition: all .15s ease-in-out
    }

    div.page-register {
        margin-top: 10px
    }

    .leaderboard {
        float: left;
        width: calc(100% - 335px);
        margin: 10px auto
    }

    .page .leaderboard,
    .mobile-body .leaderboard {
        width: 100%
    }

    .leaderboard .a728-90 {
        margin: 0 auto
    }

    .popup-notable A {
        display: block;
        color: #454545;
        padding: 5px 10px;
        white-space: nowrap
    }

    .popup-notable A:hover {
        color: #ffbd2d
    }

    .popup-notable A I {
        font-size: 16px;
        width: 16px;
        vertical-align: middle
    }

    .sort-button .fa-sort-asc {
        position: relative;
        top: 3px
    }

    .sort-button .fa-sort-desc {
        position: relative;
        top: -3px
    }

    .table-sub-header {
        padding: 10px;
        border-bottom: 1px solid #d8d8d8
    }

    .table-footer {
        border-top: 1px solid #d8d8d8;
        padding: 5px 10px;
        font-size: 12px
    }

    .table-message {
        font-weight: 900;
        padding: 10px
    }

    .faux-table {
        display: table;
        width: 100%;
        margin: 0;
        padding: 0
    }

    .faux-row {
        display: table-row
    }

    .faux-header {
        display: table-cell;
        font-weight: 700;
        padding: 5px;
        border: 1px solid #d8d8d8
    }

    .faux-cell {
        display: table-cell;
        border: 1px solid #d8d8d8;
        padding: 5px
    }

    .emoji {
        height: 22px !important;
        width: 22px !important;
        line-height: 0;
        position: relative;
        top: 5px;
        margin-top: -8px
    }

    #sb-title-inner,
    #sb-info-inner,
    #sb-loading-inner,
    div.sb-message {
        font-family: "HelveticaNeue-Light", "Helvetica Neue", Helvetica, Arial, sans-serif;
        font-weight: 200;
        color: #fff
    }

    #sb-container {
        position: fixed;
        margin: 0;
        padding: 0;
        top: 0;
        left: 0;
        z-index: 999;
        text-align: left;
        visibility: hidden;
        display: none
    }

    #sb-overlay {
        position: relative;
        height: 100%;
        width: 100%
    }

    #sb-wrapper {
        position: absolute;
        visibility: hidden;
        width: 100px
    }

    #sb-wrapper-inner {
        position: relative;
        border: 1px solid #303030;
        overflow: hidden;
        height: 100px
    }

    #sb-body {
        position: relative;
        height: 100%
    }

    #sb-body-inner {
        position: absolute;
        height: 100%;
        width: 100%
    }

    #sb-player.html {
        height: 100%;
        overflow: auto
    }

    #sb-body img {
        border: 0
    }

    #sb-loading {
        position: relative;
        height: 100%
    }

    #sb-loading-inner {
        position: absolute;
        font-size: 14px;
        line-height: 24px;
        height: 24px;
        top: 50%;
        margin-top: -12px;
        width: 100%;
        text-align: center
    }

    #sb-loading-inner span {
        padding-left: 34px;
        display: inline-block
    }

    #sb-body,
    #sb-loading {
        background-color: #060606
    }

    #sb-title,
    #sb-info {
        position: relative;
        margin: 0;
        padding: 0
    }

    #sb-title,
    #sb-title-inner {
        height: 26px;
        line-height: 26px
    }

    #sb-title-inner {
        font-size: 16px
    }

    #sb-info,
    #sb-info-inner {
        height: 20px;
        line-height: 20px
    }

    #sb-info-inner {
        font-size: 12px
    }

    #sb-nav {
        float: right;
        height: 16px;
        padding: 2px 0;
        width: 45%
    }

    #sb-nav a {
        display: block;
        float: right;
        height: 16px;
        width: 16px;
        margin-left: 3px;
        cursor: pointer;
        background-repeat: no-repeat
    }


    #sb-counter {
        float: left;
        width: 45%
    }

    #sb-counter a {
        padding: 0 4px 0 0;
        text-decoration: none;
        cursor: pointer;
        color: #fff
    }

    #sb-counter a.sb-counter-current {
        text-decoration: underline
    }

    div.sb-message {
        font-size: 12px;
        padding: 10px;
        text-align: center
    }

    div.sb-message a:link,
    div.sb-message a:visited {
        color: #fff;
        text-decoration: underline
    }

    #sb-title-inner {
        width: 90%
    }

    #sb-nav-close {
        float: right;
        width: 46px !important;
        height: 16px;
        display: block;
        cursor: pointer
    }

    .enhanced-header .device-image-wrapper img {
        width: 215px;
        height: 222px
    }

    .enhanced-header div.device-image-wrapper {
        padding: 0
    }

    .enhanced-header.device-info-wrapper {
        min-height: 230px
    }

    .enhanced-header .device-image-wrapper,
    .device-info-wrapper {
        box-sizing: border-box;
        border: 1px solid #d8d8d8;
        background: #fff;
        padding: 30px
    }

    #device_info_wrapper.enhanced-header div.device-info-wrapper {
        margin-left: 230px
    }

    .tag {
        background: #ddd;
        border-radius: 3px 0 0 3px;
        display: inline-block;
        height: 20px;
        line-height: 20px;
        padding-left: 5px;
        padding-right: 5px;
        position: relative;
        text-decoration: none;
        margin-bottom: 5px
    }

    .thread-title-cell .tag {
        margin-right: 5px
    }

    #tag_list_cell .tag {
        margin-right: 10px
    }

    .tag::after {
        background: transparent;
        border-bottom: 10px solid transparent;
        border-left: 10px solid #ddd;
        border-top: 10px solid transparent;
        content: '';
        position: absolute;
        right: -10px;
        top: 0
    }

    .tag:hover {
        background-color: #12911d;
        color: white;
        transition: none;
        -webkit-transition: none
    }

    .tag:hover::after {
        border-left: 10px solid #12911d;
        transition: none;
        -webkit-transition: none
    }

    .title-container {
        color: #454545;
        clear: left
    }

    #forumhome-general {
        margin-bottom: 15px;
        border: 1px solid #d8d8d8
    }

    .forum-head {
        background: #f9f9f9;
        color: #454545;
        font: normal 900 16px/16px 'Open Sans', sans-serif;
        border-bottom: 1px solid #d8d8d8;
        position: relative;
        -moz-box-shadow: 0 3px 3px #efefef;
        -webkit-box-shadow: 0 3px 3px #efefef;
        -o-box-shadow: 0 3px 3px #efefef;
        box-shadow: 0 3px 3px #efefef;
        padding: 0 0 0 20px;
        margin: 0;
        clear: left;
        overflow: hidden
    }

    .logged-in .forum-head {
        padding-left: 5px
    }

    .forum-head #search-input {
        border: 2px solid #ffbd2d;
        padding: 6px 6px 6px 32px !important;
        float: right;
        position: relative;
        top: 6px;
        right: 3px
    }

    .forumdisplay-tools {
        float: right;
        position: relative;
        top: 12px;
        margin-bottom: 10px
    }

    .forum-head-cell {
        margin: 0;
        padding: 0
    }

    .forum-head H2 {
        margin: 14px 0;
        padding: 0;
        font: 16px / 16px 'Open Sans', sans-serif;
        display: inline-block
    }

    .forum-head A,
    .forum-head H2 {
        color: #454545;
