    .widget .widget_news_content .widget_item_thumb .thumb_icon {
        z-index: 5;
        opacity: 0;
        position: absolute;
        margin-left: -13px;
        margin-top: -13px;
        top: 50%;
        left: 50%;
        -webkit-transition: all .2s;
        -moz-transition: all .2s;
        -o-transition: all .2s;
        transition: all .2s
    }

    .widget .widget_news_content .widget_item .widget_item_thumb .thumb_icon a {
        font-size: 10px;
        color: #FFF;
        width: 26px;
        height: 26px;
        line-height: 26px;
        display: inline-block;
        text-align: center;
