css:
@-moz-document domain("weathernerds.org") {
body {
	background-image: none;
	background-color: #333;
}

body > *,
table {
	margin-left: auto !important;
	margin-right: auto !important;
}

h1,
h2,
footer,
td.footer_text,
font.footer_text,
b.tlabel,
b.label a:link,
b.tlabel a:visited,
#modvars center {
	color: #eee;
}

footer a {
	color: lightblue;
}

b.tlabel a::before {
	content: 'For '
}

b.tlabel a::after {
	content: ' Devices'
}

b.tlabel a:visited {
	color: #eee;
}

.img {
	border: 5px solid lightblue !important;
	border-radius: 40px;
}

.model_buttons,
.mod_dropdown,
.mod_dropbtn {
	background-color: #aaa !important;
	color: #eee !important;
}

.mod_dropbtn::after {
	content: 'â–¼';
}

#headerDiv {
	background-color: #555 !important;
}

#patreon1 {
	color: rgb(231, 70, 43) !important;
}
}
