css:
@-moz-document url-prefix("https://gmiclotte.github.io/gear-optimizer/") {
body { /*General*/
    color:white;
    background:#333;
}

.nav-bar-list { /*Header BG*/
    background-color: #444;
}

.content__container { /*Main BG*/
    background-color: #333;
}

.item-section {
    background: #444;
    padding: 0px 4px; /*Better margin for everything*/
}

.item-table {
    background:#2F2F2F;
}

html body div#app div.app_container div.app_body div.content__container div.item-table div.item-section span {
    margin-left: 4px; /*Better margin for text*/
}

.item { /*Black item outlines*/
    outline: 2px solid #222;
    outline-offset: -2px;
    border: none;
}

/**/
/*Buttons*/
/**/

div button,
html body.ReactModal__Body--open div.ReactModalPortal div.ReactModal__Overlay.ReactModal__Overlay--after-open div.ReactModal__Content.ReactModal__Content--after-open form input { /*Buttons*/
    background-color: #666;
    border: 1px solid black;
    color: white;
    text-align: center;
}

html body.ReactModal__Body--open div.ReactModalPortal div.ReactModal__Overlay.ReactModal__Overlay--after-open div.ReactModal__Content.ReactModal__Content--after-open form input { /*Update button manual fix*/
    width: 56px;
    margin-right: 5px;
}

html body div#app div.app_container div.app_body div.content__container div.button-section div button { /*Button size partial fix*/
    width: 30px;
}

html body div#app div.app_container div.app_body div.content__container div.button-section button { /*Button size 2*/
    height: 20px;
}

div.item-table:nth-child(1) > div:nth-child(3) > button:nth-child(1), div.item-table:nth-child(1) > div:nth-child(3) > button:nth-child(2) { /*Small minus button particular fix*/
    width: 25px;
}

/**/
/*Dropdowns*/
/**/
select { /*Dropdowns*/
    border: solid 1px black;
    color: white;
    
    background: #666 url(https://i.imgur.com/qynoLld.png) no-repeat right center;
    -webkit-appearance: none;
    -moz-appearance: none;
}

html body div#app div.app_container div.app_body div.content__container div.button-section div label select {
    width: 160px;
}

.center > form:nth-child(1) > label:nth-child(6) > select:nth-child(1) {
    width: 75px;
}

html body div#app div.app_container div div.center form div div label select {
    width: 200px;
}

/**/
/*Popup thing*/
/**/
html body.ReactModal__Body--open div.ReactModalPortal div.ReactModal__Overlay.ReactModal__Overlay--after-open.edit-item-overlay div.ReactModal__Content.ReactModal__Content--after-open.edit-item-modal {
    background:#444;
    border: solid 1px black;
}

html body div#app div.app_container div div.center form table.center tbody tr td label input {
    background: #666;
}

/**/
/*Text*/
/**/

/*Stat comparison colors*/
.same-stat {
    color: #AAA;
}
.decrease-stat {
    color: #B11;
}
.increase-stat {
    color: #191;
}

a:link { /*A link*/
    color: #9AB;
}

/* A link that has been visited */
a:visited {
    color: #678;
}

input { /*Dark Input fields*/
    background: #AAA;
    border: 1px solid #111;
}

/**/
/**/
/**/

/* FF Scrollbar */
* {
	scrollbar-color: #444 #252525!important;
}
}
