css:
@-moz-document domain("journey.cloud") {
body {
    font-size: 100%;
    background: #333;
}
p {
    font-size: 1rem;
    line-height: 1.6rem;
}
.bg-white {
    background: #222
}
.horizontal-app-menu .menu-bar > ul > li,
.horizontal-app-menu .menu-bar > ul > li > ul {
    background: #222 !important;
    color: #EEE
}

.timelinex-card {
    background: #222 !important;
    color: #EEE;
    border: none;
    font-size: 1rem;
}
.timelinex-card.status2 {
    background: #111 !important;
}
.bootstrap-tagsinput input {
    background: #222 !important;
    border: 1px solid #333 !important;
    color: #EEE !important;
}
hr {
    border-color: #444;
}
.tool {
    border-color: #444 !important;
    background: #444 !important;
}

.markdown h1,
.markdown h2,
.markdown h3,
.markdown h4,
.markdown h5,
.markdown h6 {
    font-size: 1rem;
    font-weight: bold;
    color: #00BFE6;
    margin-top: 20px;
}
.markdown h1,
.markdown h2 {
    font-size: 1.4rem;
    border-bottom: 1px solid #333;
    padding-bottom: 5px;
    margin-bottom: 5px;
}
.markdown h2 {
    font-size: 1.2rem;
}
.cardTags > div {
    background: #333 !important;
    color: #999 !important;
}
.card-description {
    padding: 30px !important;
    color: #EEE;
}
.card-footer {
    background: #1E1E1E !important;
    padding-top: 10px !important;
    color: #777;
    border-radius: 0 0 10px 10px !important;
}
.dropdown-menu {
    background: #444;
    color: #EEE;
}
.dropdown-menu:after {
    display: none;
}
.dropdown-menu:before {
    border-bottom-color: #444 !important;
}
a.dropdown-item {
    padding: 5px 20px !important;
}
a.dropdown-item:hover {
    background: #00BFE6 !important;
    color: #FFF
}
.timelinex-card {
    width: 100% !important
}
#cards {
    max-width: 700px;
}
.btn.btn-default {
    background: #00BFE6 !important;
    border-color: #00BFE6 !important;
    color: #FFF;
}
#internalFooter *,
#internalFooter a {
    color: #777 !important;
}
}
