css:
@-moz-document domain("play.google.com") {
.paper-toolbar-1 {
   display: block;
   position: relative;
   box-sizing: border-box;
   -moz-box-sizing: border-box;
   height: 64px;
   background: #222121;
   color: #26df17;
   text-shadow: 3px 3px 7px #000;
}
.qp #material-one-middle .material-search {
   color: #26df17;
   background-color: #2d2c2c;
}
#player {
   height: 90px!important;
   background-color: #2d2c2c;
   color: #26df17;
   z-index: 2;
   box-shadow: 0 0 8px rgba(0, 0, 0, .4);
   display: flex;
   position: relative;
   text-shadow: 3px 3px 7px #000
}
div#music-content {
   background-color: #101010;
   color: #26df17;
}
.sj-card-0 #details.sj-card > [slot="title"] {
   display: block;
   overflow: hidden;
   -webkit-box-orient: vertical;
   -webkit-line-clamp: 2;
   max-height: 36px;
   font-size: 14px;
   line-height: 18px;
   color: white;
}
.gpm-card-grid-0 > [slot="title"] {
   margin: 24px 0;
   font-size: 18px;
   font-weight: 500;
   line-height: 24px;
   color: white;
   cursor: default;
}
.paper-header-panel-0[mode=waterfall] #mainContainer.paper-header-panel {
   background-color: #101010;
}
.song-table th {
   height: 36px;
   text-transform: uppercase;
   font-size: 12px;
   font-weight: 600;
   color: white;
   text-indent: 8px;
   padding: 0;
   text-align: left;
   white-space: nowrap;
   overflow: hidden;
   text-overflow: ellipsis;
}
.song-table {
   cursor: pointer;
   border-spacing: 0;
   width: 100%;
   table-layout: fixed;
   outline: none;
   padding: 16px 0;
   -webkit-user-select: none;
   -moz-user-select: none;
   -ms-user-select: none;
   text-align: left;
   background-color: #222;
   color: white;
}
.song-table.tight .song-row td {
   height: 40px;
   line-height: 40px;
   max-height: 40px;
   min-height: 40px;
   font-size: 14px;
   border-bottom: 1px solid rgba(0, 0, 0, 0.06);
   background-color: #222;
   color: #26df17;
}
.song-table.tight .song-row .column-content {
   height: 40px;
   background-color: #222;
}
.song-table [data-col="title"],
.song-table.mini [data-col="song-details"] .song-title {
   color: #26df17;
}
a,
.simple-dialog a {
   color: #26df17;
}
.song-row.selected-song-row td,
.song-row.currently-playing td {
   background-color: #222!important;
}
.paper-header-panel-0 #mainContainer.paper-header-panel {
   -ms-flex: 1 1 0.000000001px;
   -webkit-flex: 1;
   flex: 1;
   -webkit-flex-basis: 0.000000001px;
   flex-basis: 0.000000001px;
   position: relative;
   overflow-y: auto;
   overflow-x: hidden;
   -webkit-overflow-scrolling: touch;
   background-color: #222;
   color: #26df17;
}
.nav-item-container {
   font-size: 13px;
   color: #26df17;
   cursor: pointer;
   white-space: nowrap;
   overflow: hidden;
   height: 24px;
   line-height: 24px;
   display: block;
   text-decoration: none;
   outline: none;
   padding: 10px 0 10px 18px;
   font-weight: 500;
   -webkit-font-smoothing: antialiased;
   -moz-osx-font-smoothing: grayscale;
}
.nav-item-container {
   font-size: 13px;
   color: #26df17;
   background-color: #222;
   cursor: pointer;
   white-space: nowrap;
   overflow: hidden;
   height: 24px;
   line-height: 24px;
   display: block;
   text-decoration: none;
   outline: none;
   padding: 10px 0 10px 18px;
   font-weight: 500;
   -webkit-font-smoothing: antialiased;
   -moz-osx-font-smoothing: grayscale;
}
#nav-container .nav-toolbar {
   background-color: #222;
}
body.qp {
   background-color: #222;
}
#player:not([style-scope]):not(.style-scope) paper-icon-button[data-id="play-pause"]:not([style-scope]):not(.style-scope):not([disabled]) {
   color: #26df17;
}
#player:not([style-scope]):not(.style-scope) paper-icon-button[data-id="repeat"].active:not([style-scope]):not(.style-scope),
#player:not([style-scope]):not(.style-scope) paper-icon-button[data-id="shuffle"].active:not([style-scope]):not(.style-scope),
#player:not([style-scope]):not(.style-scope) #exp-menu-shuffle.active:not([style-scope]):not(.style-scope),
#player:not([style-scope]):not(.style-scope) #exp-menu-repeat.active:not([style-scope]):not(.style-scope),
#player:not([style-scope]):not(.style-scope) #exp-menu-queue.opened:not([style-scope]):not(.style-scope),
#player:not([style-scope]):not(.style-scope) paper-icon-button[data-id="queue"].opened:not([style-scope]):not(.style-scope),
#player:not([style-scope]):not(.style-scope) paper-icon-button:not([style-scope]):not(.style-scope) #exp-menu-queue.opened:not([style-scope]):not(.style-scope) {
   color: #26df17;
}
iron-icon[icon="av:explicit"] {
   color: #23b717;
}
.iron-icon-0 {
   display: -ms-flexbox;
   display: -webkit-inline-flex;
   display: inline-flex;
   -ms-flex-align: center;
   -webkit-align-items: center;
   align-items: center;
   -ms-flex-pack: center;
   -webkit-justify-content: center;
   justify-content: center;
   position: relative;
   vertical-align: middle;
   fill: currentcolor;
   stroke: none;
   width: 24px;
   height: 24px;
   color: #26df17;
}
.gpm-quick-nav-item-0 #label.gpm-quick-nav-item {
   display: block;
   overflow: hidden;
   -webkit-box-orient: vertical;
   -webkit-line-clamp: 2;
   margin-top: 4px;
   max-width: 60px;
   max-height: 20px;
   font-size: 10px;
   line-height: 10px;
   text-align: center;
   hyphens: auto;
   color: #26df17;
}
.paper-progress-2 #primaryProgress.paper-progress {
   background: #26df17;
}
.paper-slider-0 .slider-knob-inner.paper-slider {
   margin: 10px;
   width: calc(100% - 20px);
   height: calc(100% - 20px);
   background-color: #26df17;
   border: 2px solid #26df17;
   border-radius: 50%;
   -moz-box-sizing: border-box;
   box-sizing: border-box;
   transition-property: -webkit-transform, background-color, border;
   transition-property: transform, background-color, border;
   transition-duration: 0.18s;
   transition-timing-function: ease;
}
.paper-progress-2 #progressContainer.paper-progress,
.paper-progress-2 .indeterminate.paper-progress::after {
   background: #2d2c2c!important;
}
goog-menu .goog-menuitem {
   position: relative;
   padding: 0 24px;
   background-color: #222;
   color: #26df17;
}
.goog-menu .goog-menuitem .goog-menuitem-content {
   font-size: 15px;
   height: 32px;
   line-height: 32px;
   margin: 0!important;
   color: #26df17;
   overflow: hidden;
   text-overflow: ellipsis;
}
.paper-dialog-0 {
   display: block;
   margin: 24px 40px;
   background: #222;
   color: #26df17;
   font-family: 'Roboto', 'Noto', sans-serif;
   -webkit-font-smoothing: antialiased;
   font-size: 14px;
   font-weight: 400;
   line-height: 20px;
   box-shadow: 0 16px 24px 2px rgba(0, 0, 0, 0.14), 0 6px 30px 5px rgba(0, 0, 0, 0.12), 0 8px 10px -5px rgba(0, 0, 0, 0.4);
}
.top-charts-view:not([style-scope]):not(.style-scope) .song-row:not([style-scope]):not(.style-scope) [data-col="index"]:not([style-scope]):not(.style-scope) .column-content:not([style-scope]):not(.style-scope),
paper-toast:not([style-scope]):not(.style-scope) paper-button[data-action="button"]:not([style-scope]):not(.style-scope),
.ups.light:not([style-scope]):not(.style-scope) iron-icon:not([style-scope]):not(.style-scope),
.upload-dialog-dragover:not([style-scope]):not(.style-scope) .upload-dialog-description:not([style-scope]):not(.style-scope),
paper-dialog:not([style-scope]):not(.style-scope) .buttons:not([style-scope]):not(.style-scope) paper-button:not([style-scope]):not(.style-scope):not([disabled]) {
   color: #26df17;
}
.goog-menu {
   border: 0px solid transparent!important;
   border-radius: 2px;
   padding: 16px 0px!important;
   box-sizing: border-box;
   box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.37)!important;
   max-width: 320px;
   max-height: 90vh;
   background-color: #222;
}
.song-table.tight .song-row td {
   height: 40px;
   line-height: 40px;
   max-height: 40px;
   min-height: 40px;
   font-size: 14px;
   border-bottom: 1px solid rgba(0, 0, 0, 0.06);
   background-color: #222;
   color: #26df17;
}
.nav-item-container.selected:not([style-scope]):not(.style-scope),
.nav-item-container.selected:not([style-scope]):not(.style-scope) iron-icon:not([style-scope]):not(.style-scope) {
   color: #26df17;
   background-color: #222;
}
#nav_collections .nav-item-container:focus,
.nav-item-container:focus,
.nav-item-container:hover {
   background-color: #222;
}
.upload-dialog-visual-content-wrapper {
   display: table-cell;
   vertical-align: middle;
   width: 25%;
   background-color: #222;
   color: #26df17;
}
.upload-dialog-description {
   color: #26df17;
   display: block;
   margin: 0 auto;
   padding-top: 15px;
   text-align: center;
   width: 345px;
}
.upload-dialog-graphic {
   background: url(https://i.imgur.com/J8t6dnQ.png);
   display: inline-block;
   height: 160px;
   width: 160px;
   margin-top: 25px;
}
.upload-dialog-title:not([style-scope]):not(.style-scope) {
   background-color: #222;
   color: #26df17;
}
.now-playing-menu .goog-menuitem,
.now-playing-menu .goog-submenu,
.now-playing-menu .goog-submenu .goog-submenu-arrow,
.goog-menuitem {
   background-color: #222;
   border: none;
   padding: 0;
   margin: 0;
   height: auto;
}
}
