css:
@-moz-document domain("www.elefant.ro") {
.exponea-intershop, .exponea-banner{
    display: none!important;
}

body, header, footer, .wrapper, .product-carousel-container.recent,
.account-login-page .login-tile, .reset-password-page .login-tile, .create-account-page .login-tile,.account-login-page h2, .reset-password-page h2, .create-account-page h2,.account-login-page h3, .reset-password-page h3, .create-account-page h3,.account-wrapper .account-main .col-md-9
{
	background-color: #333;
    color:white;
}

header .top-header .utils{
	    color: #7ed321;
        border-bottom: 1px solid #7ed321;
}


.dropdown-menu{
    background-color: #a15d43;
}

.footer-top, .main-container,.footer-mid{
    border-bottom:1px solid #7ed321;
}

.footer-top{
       border-top:1px solid #7ed321;
}

.main-navigation ul.main-navigation-list{
	background-color: #a15d43;
    border-radius: 12px;
}
.main-navigation ul.main-navigation-list li a{
	color: wheat;
}

.main-navigation ul.main-navigation-list li.dropdown:hover{
	transition-delay: .5s;
    background-color: #ff9067b8;
    border-radius: 20px;

}

.main-navigation ul.category-level1>li li a,.main-navigation ul.category-level1>li a{
    color:white;
}

.main-navigation ul.category-level1>li li a:hover{
	color:#ff8152;
}

a, h1, h2, h3, h4, h5, h6, .h1, .h2, .h3, .h4, .h5, .h6, .footer-columns li a, .footer-top .list-inline a, .footer-bottom a, .category-links-container .enhanced-image-container .enhanced-image-text h2, .product-detail-page .product-description p{
    color: white;
}

a:hover, .footer-columns li a:hover{
 color: #7ed321;
}

.product-list-container{
        padding-bottom: 20px;
}

.product-detail-page .product-description h1, .product-detail-page .product-description .h1,.product-list a.product-title{
    color:white;
}

.product-list .product-tile{
	background-color: #ff81528a;
}

.product-list a.product-title {
    font-size:14px;
}
.product-list a.product-manufacturer{
    color: white;
    padding-bottom: 5px;
    padding-top: 5px;
}
.product-detail-img .carousel{
	    width: 50%;
    margin-left: 20%;
}
.zoomtracker{
    top: 50%;
    height: 500px;
}
.product-breadcrumbs li a, .product-info .product-title,.product-info .product-info-actions .add-to-wishlist, .product-info .product-info-actions .price-notification{
           color: wheat;
}

.slick-prev {
    left: -35px!important;
}

.slick-next {
    right: -35px!important;
}
.slick-prev, .slick-next{
	background-color:#ff81528a;
}

.wrapper {
    padding: 0 0 20px 0;
}

.wishlists .product-list {
    display: flex;
    flex-wrap: wrap;
}
.account-payment #payment-accordion .panel{
	color:black;
}

.account-content .row.section {
    background-color: #333333;
}
}
