css:
@-moz-document domain("lichess.org") {
::-webkit-scrollbar-thumb,
body::-webkit-scrollbar-thumb,
ol::-webkit-scrollbar-thumb,
ul::-webkit-scrollbar-thumb,
#transcript-scrollbox::-webkit-scrollbar-thumb {
    /* Color of handle */
    background: #580303!important;
    background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
    box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
    border-radius: 22px!important;
    border: 1px solid rgba(0, 0, 0, .25)!important;
}
 
::-webkit-scrollbar-thumb:hover,
body::-webkit-scrollbar-thumb:hover,
ol::-webkit-scrollbar-thumb:hover,
ul::-webkit-scrollbar-thumb:hover,
#transcript-scrollbox::-webkit-scrollbar-thumb:hover {
    /* Color of handle */
    background: #580303!important;
    background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
    border-radius: 22px!important;
    box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}
 
::-webkit-scrollbar-track,
#messages div::-webkit-scrollbar-track {
    /* Color of slider */
    background: #170000!important;
    box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
    border: 1px solid rgba(0, 0, 0, .25)!important;
}
 
::-webkit-scrollbar,
#messages div::-webkit-scrollbar {
    /* Width */
    width: .8em!important;
}
 
::-webkit-scrollbar-button,
#messages div::-webkit-scrollbar-button {
    /* Color of handles at top and bottom */
    background: #4c0707!important;
    box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
}
 
::-webkit-scrollbar-button:hover,
#messages div::-webkit-scrollbar-button:hover {
    border-radius: 22px!important;
    box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}
}

@-moz-document domain("lichess.org") {
/*__________________________________________________________________________________*/

* {
	text-decoration: none!important;
}

#topnav section>a,
.button,
.ceval .engine .cloud,
.fbt,
.learn__table .result button,
.tournament-leaderboards h2,
.tournament-shields h2
{
	text-transform: capitalize!important;
}

#topnav section:active>a,
#topnav.hover section:hover>a,
.game-setup .optional_config,
.game-setup .ratings,
.learn__side-home,
.learn__side-map .categ.active,
.learn__table .goal,
.learn__table .progress,
.learn__table .wrap,
.number-menu--tabs .nm-item.active,
.tabs-horiz,
.tournament-leaderboards,
.tournament-shields,
.tview2-column comment.mistake,
:focus {
	border: none!important;
	outline: none!important;
	box-shadow: none!important;
}

.lobby__forum li {
	line-height: 21px;
	white-space: pre-line!important;
}

body {
	overflow-x: visible;
}

/*___ Fonts ________________________________________________________________________*/

.btn-rack form i,
.btn-rack__btn i,
.button,
.hooks__list th,
.learn-stages .categ>h2,
.learn__side-map .stage,
.lobby__forum .extract,
.lobby__streams .stream,
.lpools,
.tabs-horiz span,
.timeline .entry,
body,
h1,
h2,
h3,
h4 {
	font-family: cursive, Roboto, Arial, sans-serif!important;
}

.button {
	font-size: 21px!important;
}

.gamebook .feedback.play,
.mchat__messages,
comment,
td,
th
{
	font-size: 18px!important;
}

html {
    font-size: 15px;
}

.learn__table .title h2,
.learn__table .title h3 {
	font-size: 1.3em!important;
}

.tview2-column move {
	font-size: 1.392em;
}

.ceval .engine,
.ceval help {
	flex: 1 1 auto;
	font-size: 12px;
	line-height: 21px;
	margin-top: 3px;
	color: #787878;
}

.mchat__messages .color,
.mchat__messages .user-link,
td,
th
{
	letter-spacing: 2px;
}

table {
	border-collapse: separate;
	border-spacing: 10px;
}

.is-after::after,
.is::before,
.rp::before,
.user-link .line,
[data-icon]::before
{
	font-size: 1.2em;
}

/*___ Remove Items _________________________________________________________________*/

selector {
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/*___ Left/Right/Top/Bottom ________________________________________________________*/

.lobby__blog .post .text {
	margin-left: 0!important;
}

.categ:nth-child(3) .text {
	margin-left: 1rem!important;
}

cg-board,
.main-board .cg-wrap {
	left: 19px;
	top: 13px;
}

.main-board {
	left: 2rem!important;
}

.mini-board {
	left: -1.25rem!important;
}

.main-board .cg-wrap {
	left: -18px;
	top: -15px;
}

.now-playing cg-board {
	left: 19px;
	top: 13px;
}

.manipulable cg-board {
	left: -26px;
	top: 20px;
}

.learn__table {
	margin-left: 2rem;
}

.practice__side__title i {
	flex: 0 0 4.5em;
	margin-right: 1.6rem;
}

coords.files {
	/* Letters on the board */
	bottom: -21px;
	left: -24px;
}

coords.ranks {
	/* Numbers on the board */
	top: 22px;
	right: 29px;
}

.practice-box {
	flex: 0 0 152px;
}

.analyse__tools .ceval {
	flex: 0 0 53px;
}

.analyse__clock.bottom {
	bottom: -33px;
}

.is.text::before,
.text[data-icon]::before {
	margin-right: 0.3em;
}

.analyse .analyse__tools {
    margin-top: 3rem!important;
}
/*___ Margins ______________________________________________________________________*/

.analyse__acpl,.analyse__board,.analyse__side,.analyse__tools,
.community__leaders .leaderboards .user-top,
.coord-trainer__table,
.learn-stages,.learn__main,.learn__side,
.lobby__app,.lobby__puzzle,.lobby__side,.lobby__tv,.lobby__table,.lobby__about,.lobby__support,.lobby__forum,.lobby__blog,
.now-playing a,
.page-menu__content,.page-menu__menu,
.practice-side,.practice__side,
.puzzle__side,.puzzle__tools,
.round__app,.round__app__board,.round__side,
.tournament-leaderboards__item,.tournament-shields__item
{
	margin: 12px!important;
}

.lobby__blog .post,
.tour-spotlight {
	margin: 1rem 0!important;
}

.button,
group.radio label {
	margin: 0 1rem!important;
}

.gamebook .feedback.end a::before {
	margin: 0 0;
}

/*___ Paddings _____________________________________________________________________*/

#modal-wrap,
.analyse .mchat,.analyse__acpl,.analyse__side,.analyse__tools,.analyse__underboard,
.chat__members,
.community__leaders,.community__online,
.coord-trainer__table,
.learn-stages,.learn__side,.learn__table,
.lobby__app,.lobby__puzzle,.lobby__side,.lobby__tv,.lobby__table,.lobby__about,.lobby__support,.lobby__forum,.lobby__blog,
.now-playing a,
.page-menu__content,.page-menu__menu,
.puzzle__side,.puzzle__tools,
.round__side,
.tournament-leaderboards__item,.tournament-shields__item
{
	padding: 12px!important;
}

.round__app {
	padding: 32px 0!important;
}

.ruser a {
	padding-left: 4rem!important;
}

.ceval .engine .cloud {
	padding: 4px 6px;
}

.community .user-top li {
	padding: 6px 12px;
}

.lobby__box__top .title {
	padding: 0 2.3em;
}

.ruser {
	padding: 2.5em 0.5em 0.5em 1.3em!important;
}

.vstext {
	padding: 3.3em 0.6em;
}

.community .user-top li a:nth-child(2) span {
	padding-left: 1em;
}

/*___ Heights ______________________________________________________________________*/

.lobby__tournaments,
.pocket-c1 {
	max-height: none!important;
}

selector {
	height: auto!important;
}

.training-box .no-square {
	height: 89px;
}

.material {
	height: 9rem;
}

.pocket-c2 {
    height: 6rem;
}

.gamebook .feedback.play .no-square {
	height: 84px;
	margin-right: 0px;
}

.gamebook .feedback {
	height: 3rem;
}

#powerTip,
#miniGame {
	height: auto;
}

.upt__info {
	height: 12rem;
}

.btn-rack {
	height: 10rem;
}

piece {
    height: 10.5%;
}

.upt__info__ratings>span {
    height: 37px!important;
}

/*___ Widths _______________________________________________________________________*/

#main-wrap,
#top,
.upt__info__ratings>span {
	max-width: none!important;
}

#main-wrap {
    --main-max-width: none;
}

.gamebook {
	width: 40rem;
}

.page-menu {
	width: 95%;
}

piece {
    width: 8.5%;
}


#main-wrap {
    display: block!important;
}


















}

@-moz-document domain("lichess.org") {
/*__________________________________________________________________________________*/
a[href],
td span {
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited {
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

.fbt:hover:not(.disabled):not([disabled]),
.forum .pagination .current,
.mchat__tab-active,
.rmoves m2.active,
.study__chapters>div.active>span, .study__chapters>div:active>span,.study__chapters>div:hover span,
.tview2-column move:hover,
a[href]:hover span,.tview2 move index,
a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label {
	color: #ff0!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

#analyse-cm a,
.advice-summary td,
.ceval .engine,.ceval help,
.fbt,
.game-setup .slider,
.lobby__box td,
.mchat__tab,
.rmoves index,
.ruser rating,
.studies .study .top span,
.tview2 eval,.tview2 line move,
.user-link .line,.user-link,
.vstext,
body,
li,
optgroup,
option,
p
{
	color: #fff!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Khaki ________________________________________________________________________*/

.lpools>div,
.site-buttons .link,
.tabs-horiz span,
.tview2-column>index,
.user-link,
h1,
h2,
option:disabled,
th
{
	color: #F0E890!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Burlywood ____________________________________________________________________*/

strong,
h3,
h4 {
	color: #e0b880!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Sandy Brown __________________________________________________________________*/


h5,
h6,
label {
	color: #F0A060!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Blue _________________________________________________________________________*/

.tournament-leaderboards li a:nth-child(2),
.lobby__tournaments td:nth-child(3),
.tournament-shields li,

.analyse__clock.active,
.lpools .clock,
.rclock .time,
.vstext__clock,
time
{
	color: #07beff!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Green ________________________________________________________________________*/


selector {
	color: #07ff24!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Red __________________________________________________________________________*/

.tview2-column comment.mistake,
#friend_box .nobody {
	color: #ff0707!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

.mselect__label,
group.radio label,
input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea {
	background: #1d0909!important;
	background-color: #1d0909!important;
	border-radius: 22px!important;
	color: #fff!important;
}

/*__________________________________________________________________________________*/

::selection {
	background: #ff9!important;
	color: #000!important;
}

pre ::selection {
	background: #ff9!important;
	color: #fff!important;
}

::placeholder { /* Chrome, Firefox, Opera, Safari 10.1+ */
  color: #f48f8f;
  opacity: 1; /* Firefox */
}
























}

@-moz-document domain("lichess.org") {
/*__________________________________________________________________________________*/

body,
html {
	background: url(https://image.ibb.co/hVmiOe/wallpaper_with_black_background_45_free_wallpaper.jpg)!important;
	background-attachment: fixed!important;
}

#modal-overlay,
.learn__screen-overlay {
	background: rgba(0, 0, 0, 0.89)!important;
}

#friend_box .friend_box_title,
.btn-rack form,
.btn-rack__btn,
.button.button-empty.button-green:not(.disabled):hover,
.button.button-empty.button-red:not(.disabled):hover,
.button.button-empty:not(.disabled):hover,
.button.button-metal {
	background: linear-gradient(to bottom, #8d1515 0%, #120000 100%)!important;
}

.ui-widget-content {
	background: #172a00;
}

.learn__main square.has-item.move-dest {
	background: radial-gradient(#dc1c1cba 0%, transparent 80%, rgba(85, 0, 0, 0.99) 80%);
}

square.last-move {
	background-color: rgba(0, 0, 0, 0.6);
}

.crosstable fill,
.crosstable povs {
	background: #020202;
}

#powerTip .forecast-info .title,
.cmn-toggle+label::after,
.crosstable__score,
.crosstable__users,
.rcontrols .rematch.fbt {
	background: linear-gradient(to bottom, #000000 0%, #141414 100%);
}

square.selected {
    background-color: rgba(85, 20, 20, 0.5);
}

square.move-dest {
    background: radial-gradient(rgba(85, 20, 20, 0.5) 19%, rgba(0,0,0,0) 20%);
}

/*___ Background Gray ______________________________________________________________*/

.slist thead, .cmn-toggle+label::after,
#top {
	background: #060505!important;
}

/*___ Background 0000 ______________________________________________________________*/

.pocket,
.tournament-leaderboards li:nth-child(even), .tournament-shields li:nth-child(even),
.tournament-leaderboards h2, .tournament-shields h2,
.tournament-leaderboards__item, .tournament-shields__item,
.community__leaders .leaderboards li:nth-child(even),
.community__leaders .leaderboards h2,
#analyse-cm .title,#analyse-cm a:hover,
#hook .opponent,
#topnav section:active>a,#topnav.hover section:hover>a,
.activity section h2 time,
.analyse__clock.active,.analyse__tools .sub-box .title,
.ceval,
.cmn-toggle+label::after,
.coach-list__main .coach-widget:hover,.coach-list__main .coach-widget:nth-child(even),
.crosstable__score,.crosstable__users,.crosstable__users, .crosstable__score, .cmn-toggle+label::after, .slist thead, #analyse-cm .title, .study__player, .study__metadata h2,
.fbt.active,.fbt:hover:not(.disabled):not([disabled]),
.feedback,
.forum .pagination .current,.forum .pagination a:hover,
.game__meta,
.hooks__list td,
.learn__screen .buttons .back,.learn__side-home,.learn__side-map .categ.active>h2,.learn__table .goal,.learn__table .title,
.lobby__app__content,.lobby__blog .post:hover,.lobby__box tr:nth-child(even),.lobby__box__content,.lobby__box__top,
.mchat__content,.mchat__tab:hover,
.number-menu,
.opening_box,
.practice-box .comment,.practice__side .finally,.practice__side__title,
.ps__chapter.active .status,
.puzzle__feedback,.puzzle__history,.puzzle__side__metas,.puzzle__side__user,
.rclock .time,
.rmoves .buttons,.rmoves index,.rmoves,
.round__app__table,
.slist tbody tr:nth-child(even),
.study__chapters,.study__members,.study__player,.study__chapters>div.active>span, .study__chapters>div:active>span,.study__metadata,
.tview2-column>index,.tview2-column>interrupt,
.user-show__header,.user-show__social,
.vstext
{
	background: rgba(0, 0, 0, 0)!important;
}

/*__ Blue __________________________________________________________________________*/

.button,
.gamebook .feedback.end a,
.learn__screen .buttons .next,
.lobby__support a,
.tour-spotlight {
	background-image: linear-gradient(to bottom right, #043dda, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

.button:hover,
.gamebook .feedback.end a:hover,
.learn__screen .buttons .next:hover,
.lobby__support a:hover,
.tour-spotlight:hover {
	background-image: linear-gradient(to bottom right, #154eeb, #000)!important;
	color: #ff0!important;
}

.button:active,
.gamebook .feedback.end a:active,
.learn__screen .buttons .next:active,
.lobby__support a:active,
.tour-spotlight:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*__ Orange ________________________________________________________________________*/

.lpools>div {
	background-image: linear-gradient(to bottom right, #da3a04, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

.lpools>divr:hover {
	background-image: linear-gradient(to bottom right, #eb4b15, #000)!important;
	color: #ff0!important;
}

.lpools>div:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*___ Plain Black __________________________________________________________________*/

.page-menu__content.box,
.community__online,
.community__leaders,
.analyse .mchat,.analyse__side,.analyse__tools,
.analyse__acpl,
.analyse__underboard,
.chat__members,
.coord-trainer__table,
.learn-stages,.learn__main,.learn__side,
.lobby__about,.lobby__app,.lobby__blog,.lobby__forum,.lobby__leaderboard,.lobby__puzzle,.lobby__side,.lobby__simuls,.lobby__support,.lobby__table,.lobby__tournaments,.lobby__tv,.lobby__winners,
.page-menu__content,.page-menu__menu,
.practice-side,.practice__side,
.puzzle__side,.puzzle__tools,
.round__side
{
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

.page-menu__content.box:hover,
.community__online:hover,
.community__leaders:hover,
.analyse .mchat:hover,.analyse__side:hover,.analyse__tools:hover,
.analyse__acpl.h,
.analyse__underboard:hover,
.chat__members:hover,
.coord-trainer__table:hover,
.learn-stages:hover,.learn__main:hover,.learn__side:hover,
.lobby__about:hover,.lobby__app:hover,.lobby__blog:hover,.lobby__forum:hover,.lobby__leaderboard:hover,.lobby__puzzle:hover,.lobby__side:hover,.lobby__simuls:hover,.lobby__support:hover,.lobby__table:hover,.lobby__tournaments:hover,.lobby__tv:hover,.lobby__winners:hover,
.page-menu__content:hover,.page-menu__menu:hover,
.practice-side:hover,.practice__side:hover,
.puzzle__side:hover,.puzzle__tools:hover,
.round__side:hover
{
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

/*___ Plain ________________________________________________________________________*/

.mselect__list,
.tournament-leaderboards__item, .tournament-shields__item,
#powerTip,
#miniGame,
#analyse-cm,
#friend_box,
#modal-wrap,
#topnav div,
.box,
.learn__screen,
.learn__table,
.now-playing a,
.practice-app .study.ongoing,
.round__app,
optgroup,
option {
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

.mselect__list:hover,
.tournament-leaderboards__item:hover,
.tournament-shields__item:hover,
.community__leaders .leaderboards .user-top:hover,
#powerTip:hover,
#miniGame:hover,
#analyse-cm:hover,
#friend_box:hover,
#modal-wrap:hover,
#topnav div:hover,
.box:hover,
.learn__screen:hover,
.learn__table:hover,
.now-playing a:hover,
.practice-app .study.ongoing:hover,
.round__app:hover,
optgroup:hover,
option:hover {
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

.community__leaders .leaderboards .user-top {
	background-color: #1111116e!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

/*___ Red Gradient _________________________________________________________________*/

.mselect__list>*:hover,
.mselect__list .current,
.community .user-top li:hover,
.community__leaders .leaderboards li:hover,
#modal-wrap .close,
#topnav div a:hover,
.contact .branch .links a:hover,
.eval-gauge .black,
.hook:hover,
.learn__side-map .stage.active,
.learn__side-map .stage.future:hover,
.learn__table .result button,
.learn__table .result.failed,
.lobby__box__content tr:hover,
.mchat__messages li:hover,
.mchat__tab-active,
.ps__chapter.active,
.ps__chapter:hover,
.rmoves m2.active,
.rmoves m2:hover,
.slist tbody tr:hover,
.study__chapters>div.active,
.study__chapters>div:hover,
.study__members>div.active,
.study__members>div:hover,
.sub-ratings a.game:hover
{
