css:
@-moz-document domain("github.com") {
/* Padding Fixes */
aside[aria-label="Explore"] { padding-left: 10px; }
div.dashboard#dashboard { padding-left: 10px;  padding-right: 10px; }

/* Small Shadow */
.btn, .avatar[width="40"],
.social-count, .form-control,
.subnav-links {
    box-shadow: 0 0 2px rgba(0, 0, 0, 0.175);
}

/* Normal Shadow */
.Box, .avatar[width="32"],
.commit-tease, .file-wrap,
.overall-summary, .repository-lang-stats-graph {
    box-shadow: 0 0 5px rgba(0, 0, 0, 0.25);
}

/* Navigation Stuff */
header.Header { box-shadow: 0 1.5px 7px rgba(0, 0, 0, 0.25); }
.reponav-item.selected { box-shadow: 0 -1px 2px rgba(0, 0, 0, 0.15); }

/* Repo Viewing */
.commit-tease, .files, .file-wrap, .overall-summary, .repository-lang-stats-graph { border-radius: 5px 5px 5px 5px !important; }
.overall-summary { margin-bottom: 10px; }
.commit-tease { margin-top: 10px; }
.file-wrap { margin-top: 20px; margin-bottom: 20px; }
}
