css:
@-moz-document domain("www.blognone.com")
{

#header {
    background: #00449c;
}

#navi-menu {
    background: #449b29;
    font-family:Tahoma;
}

#wrapper, body {
    background: #E7EAEF;
}

#footer {
    background: #0f3b6d;
}

.node {
    border: 1px solid #E7EAEF;
    -moz-border-radius: 3px;
    -webkit-border-radius: 3px;
    border-radius: 3px;
}

.content-title-box {
    position: relative;
    padding: 5px 15px;
    font-weight: bold;
    background: #2c6dc3;
    margin: 0px;
    margin-bottom: 5px;
    -moz-border-radius-topleft: 3px;
    -moz-border-radius-topright: 3px;
    -webkit-border-top-left-radius: 3px;
    -webkit-border-top-right-radius: 3px;
    border-top-left-radius: 3px;
    border-top-right-radius: 3px;
}

.news-info {
    border-top: 1px solid #E7EAEF;
    padding: 2px 15px;
    text-align: justify;
}

.block .content {
    padding: 0px;
}

#rightbottom_sidebar {
    border: #E7EAEF solid 1px;
    -moz-border-radius: 3px;
    -webkit-border-radius: 3px;
    border-radius: 3px;
}

#block-views-jobs-blognone-com-block {
    display:none;
}

#rightbottom_sidebar .block h2 {
    background: #449b29;
    -moz-border-radius-bottomright: 2px;
    -moz-border-radius-topright: 2px;
    -webkit-border-bottom-right-radius: 2px;
    -webkit-border-top-right-radius: 2px;
    border-bottom-right-radius: 2px;
    border-top-right-radius: 2px;
}

#block-user-login h2, .user-header h2, #block-main-login h2 {
    -moz-border-radius-bottomright: 3px;
    -moz-border-radius-topright: 3px;
    -webkit-border-bottom-right-radius: 3px;
    -webkit-border-top-right-radius: 3px;
    border-bottom-right-radius: 3px;
    border-top-right-radius: 3px;
}

#block-user-login, #block-block-35, .user-block, #block-main-login {
    -moz-border-radius: 3px;
    -webkit-border-radius: 3px;
    border-radius: 3px;
}

#block-user-login #edit-name, #block-user-login #edit-pass {
    border: 0px;
    padding: 4px 5px;
}

#block-user-login label {
    color: #f5f9ff;
}

#block-user-login .item-list ul a {
    color: #f5f9ff;
}

#block-user-login #edit-submit {
    -moz-border-radius: 8px;
    -webkit-border-radius: 8px;
    border-radius: 8px;
    padding: 5px 5px;
}

#page-sharing {
    display: none;
}

#above_comments {
    display: none;
}

#comments {
    margin-top: 0;
}

#comment-area {
    border: 1px solid #e7eaef;
    border-bottom-left-radius: 3px;
    border-bottom-right-radius: 3px;
}

#comments .title {
    display: none;
}

.comment-info {
    background: #083877;
    background: #1e446f;
    color: #dedede;
}

.comment-even-depth ~ .indented {
    background: #e7eaef;
}

#comment-area a.comment-target {
    color: #ccc;
}

#main-content img {
    border-radius: 8px;
}

.submitted {
    background-color: #e7eaef;
    margin: 0 5px 0 0;
    padding: 4px;
    border: 1px solid #dcdfe4;
    -moz-border-radius-bottomleft: 4px;
    -moz-border-radius-bottomright: 4px;
    -webkit-border-bottom-left-radius: 4px;
    -webkit-border-bottom-right-radius: 4px;
    border-bottom-left-radius: 4px;
    border-bottom-right-radius: 4px;
    -moz-border-radius-topleft: 4px;
    -moz-border-radius-topright: 4px;
    -webkit-border-top-left-radius: 4px;
    -webkit-border-top-right-radius: 4px;
    border-top-left-radius: 4px;
    border-top-right-radius: 4px;
}

}
