css:
@-moz-document domain("amazon.com") {

    /* top image */
    #nav-upnav,
    
    #nav-swmslot,
    
    /* Amazon Prime ad */
    #non-prime,
    
    /* "Try Audible" */
    #AudibleTrialUpsell,
    
    /* home page carousel */
    #gw-desktop-herotator,
    
    /* "Get yourself a little something" */
    #desktop-1,
    
    /* "Now 100+ Amazon Dash Buttons" */
    #desktop-5,
    #desktop-6,
    
    /* "Movies included with Prime membership at no additional cost" */
    #desktop-7,
    
    /* sponsored search results */
    .s-hidden-sponsored-item,
    
    /* sponsored related products */
    #sponsored-products-dp_feature_div,
    
    /* Amazon Giveaway */
    #dpx-giveaway_feature_div,
    
    /* promotions */
    #promotions_feature_div,
    #quickPromoBucketContent,
    #heroQuickPromo_feature_div,
    .billboardRowWrapper,
    
    /* sponsored links */
    #dp-ads-middle_feature_div,
    #dp-ads-middle-3psl,
    
    /* hero quick promo */
    #hqpWrapper,
    
    /* share buttons */
    #tellAFriendBox_feature_div,
    
    /* right sidebar */
    #sponsored-products-rhs-widget,
    #sidebar,
    
    /* bottom junk */
    #centerBelowExtra,

    /* top banner on product page */ 
    #detail-ilm_div,

    /* "Today's Deals" bottom banner */
    .bxc-grid__container,

    #navFooter { display: none !important; }
    
    /* right sidebar */
    .showRightCol { padding-right: 0; }
    
    /* "Sponsored" header
    can't remove the content (no CSS parent selector) so make them obvious instead */
    
    .s-sponsored-list-header {
    	color: #111 !important;
    	background: #febd69;
    	padding: .3em;
    	border-radius: .3em;
    	margin-bottom: .5em !important;
    }

}
