css:
@-moz-document domain("hostwinds.com") {
.hw-main-container,
#main-body.container,
.dataTables_wrapper .listtable,
.client-status, 
table.table-list thead, 
table.table-striped thead {
    background: #000
}
.ticket-reply.staff .user,
.ticket-reply .user,
.panel-footer,
.dropdown-menu,
.navbar-nav > li > .dropdown-menu > ul > li,
#admin-area-links > .navbar-nav > .dropdown > .dropdown-menu > ul > li {
    background: #111
}
.panel-body,
.table-list > tbody > tr > td,
#order-standard_cart .row .products .product {
    background: #111;
    color: #fff
}
.table-list > tbody > tr:nth-child(2n) > td,
.dataTables_wrapper .dataTables_filter label .form-control,
.select2-container--default .select2-selection--single {
    background: #222
}
.md-editor,
.md-editor > .btn-toolbar,
.md-editor > textarea.markdown-editor, 
.md-editor > .md-preview {
    background: #222 !important;
    color: #fff !important;
    border-color: #444 !important
}
.navbar-nav > li > .dropdown-menu > ul > li:hover,
.navbar-nav > li > .dropdown-menu > ul > li:focus, 
#admin-area-links > .navbar-nav > .dropdown > .dropdown-menu > ul > li:hover, 
#admin-area-links > .navbar-nav > .dropdown > .dropdown-menu > ul > li:focus {
    background: #222;
    color: #3492e1
}
a.list-group-item,
button.list-group-item,
.ticket-reply,
.form-control[disabled], 
.form-control[readonly], 
fieldset[disabled] .form-control,
.form-control,
.btn.btn-default,
.pagination > li > a,
.pagination > li > span,
.addon-products-category .panel-addon .addon-footer {
    background: #222;
    color: #fff
}
.md-editor .md-footer, 
.md-editor > .md-header {
    background: #333
}
a.list-group-item:focus, 
a.list-group-item:hover, 
button.list-group-item:focus, 
button.list-group-item:hover,
.btn.btn-default:focus, 
.btn.btn-default:hover,
a.list-group-item.active, 
a.list-group-item.active:focus, 
a.list-group-item.active:hover, 
.panel-sidebar a.list-group-item.active, 
.panel-sidebar a.list-group-item.active:focus, 
.panel-sidebar a.list-group-item.active:hover,
.pagination > li > a:focus,
.pagination > li > a:hover,
.pagination > li > span:focus,
.pagination > li > span:hover,
.pagination > .active > a, 
.pagination > .active > a:focus, 
.pagination > .active > a:hover, 
.pagination > .active > span, 
.pagination > .active > span:focus, 
.pagination > .active > span:hover,
.input-group-addon {
    background: #333;
    color: #fff
}
.pagination > .disabled > a, 
.pagination > .disabled > a:focus, 
.pagination > .disabled > a:hover, 
.pagination > .disabled > span, 
.pagination > .disabled > span:focus, 
.pagination > .disabled > span:hover {
    background: #444;
    color: #fff
}
.dataTables_wrapper .listtable .topbar, 
.dataTables_wrapper .row:first-of-type,
.panel-default > .panel-heading, .panel-sidebar > .panel-heading {
    background: #00263f
}
table.table-list thead th, table.table-striped thead th,
.dataTables_wrapper table.table-list thead th.sorting_asc, 
.dataTables_wrapper table.table-list thead th.sorting_desc {
    background: #006db2;
    color: #fff
}
.navbar-nav > li > .dropdown-menu a,
span.select2-container--default .select2-selection--single .select2-selection__rendered,
.addon-desc,
.addon-desc-expand {
    color: #fff
}
.panel-default,
.panel-sidebar,
.ticket-reply, 
.ticket-reply.staff,
.panel-footer,
.panel-default,
a.list-group-item,
button.list-group-item,
.form-control, 
select, 
span.select2-container--default .select2-selection--single,
.md-editor,
.btn.btn-default,
.dataTables_wrapper .listtable,
.table-list > tbody > tr > td,
.table-list > tbody > tr:nth-child(2n) > td {
    border-color: #444
}
.dataTables_wrapper .dataTables_filter label .form-control {
    padding: 7px !important
}
.input-group-addon {
    border: none
}
}
