css:
@-moz-document url-prefix("http://www.meirtv.co.il/") {
body {
    margin: 0px;
    background-color: #383838;
}
.logo,
.under-video,
.BannerDiv.container,
iframe {
    display: none;
}



#loch-sub-pas ,
.frame-div-title,
.white16b, a.white16b,
.white19b, a.white19b,
.white14b, a.white14b,
.div-in-frame-PreText,
.articleHead,
.bgColorArchive,
.title_page_sets,
.title_page,
#div_side_archive_left ,
#div_side_article_left,
#div_side_live_right,
#div_side_right,
#div_side,
#div_main_allpage,
#div_main ,
#div_article_main,
#div_main_content,
#sub_menu,
#menu,
a.navline1link,
a.navline3link,
#meir,
#serach,
#up_nav,
a.searchbyrabbi,
#new_nav {
    background-color: #383838;
}
body * {
    background-image: none !important;
}
/* Ver 1 */
body #pageframe * {
    background-image: none !important;
    background-color: #383837 !important;
    color: #a8a8a8 !important;
    border: 0 none !important;
}
/* Ver 2 */
body #pageframe * {
    background-image: none !important;
    background-color: #797265 !important;
    color: #a19785 !important;
    border: 0 none !important;
    font-family: serif;
    font-size: 1.03em;
    box-shadow: 0px 0px 6px -5px #000 inset;
    padding: 0.02em;
    border-radius: 13px;
}

body #pageframe *:hover {
    color: #fff !important;
}


#form_search input.textsearch {
    top: -64px;
    width: 100vw;
    text-align: center;
    background-color: #78e2d263 !important;
    padding: 7px;
    left: -50%;
    position: absolute;
    font-size: xx-large;
    color: #000 !important;
}


}
