css:
@-moz-document domain("baidu.com") {
/*这些是每个搜索结果的样式，可以尝试自己修改到喜欢的样子。*/
.c-container {
	padding: 15px;
	margin-top: 2em;
	border-radius: 8px;
	box-shadow: 0px 0px 5px 1px rgba(0, 0, 0, .15);
	background-color: #9c9c9c12;
	transition: 0.3s;
	transition-timing-function: cubic-bezier(0.15, 0.87, 0.36, 0.93);
}
.c-container:hover {
	background-color: #9c9c9c4f;
	box-shadow: 0px 0px 0px 2px rgba(120, 143, 194, .65);
}
.c-border {
	background: #fffffff0;
}
A {
	text-decoration: none;
}
/*知道搜索界面*/
.dl {
	background-color: #9c9c9c12;
	margin: 10px;
	padding: 10px;
	border-radius: 5px;
	box-shadow: 0px 0px 5px 1px rgba(0, 0, 0, .15);
	transition: 0.3s;
	transition-timing-function: cubic-bezier(0.15, 0.87, 0.36, 0.93);
}
.dl:hover {
	background-color: #9c9c9c4f;
	box-shadow: 0px 0px 0px 2px rgba(120, 143, 194, .65);
}
.list-wraper .dt {
	font-size: 13px;
}

/*隐藏掉右边的广告 我发现右边有的时候有一些有用的搜索就不取消了。*/
.cr-content {
	padding: 15px;
	margin-top: 2em;
	border-radius: 8px;
	color: #000000fa;
	/* 	background-color: #2cece412; */
	transition: 0.3s;
	transition-timing-function: cubic-bezier(0.15, 0.87, 0.36, 0.93);
	box-shadow: inset 2px 3px 7px 5px #11111112;

	/* 	visibility: hidden; */
}
.cr-content a {
	color: #000;
}

#content_right {
	border-left: 1px solid #fff0;
}

A {
	color: black;
	text-shadow: 0 0 10px rgba(0, 0, 0, 0.15);
}

a em {
	text-decoration: none;
	background-color: #00ffee33;
	border-color: #04040424 !important;
	border-radius: 6px;
	background-size: cover;
	color: #046ca9;
}
}
