css:
@-moz-document domain("coop-land.ru") {
/*Ваш фон*/
  body
  {
    background: url(https://steamuserimages-a.akamaihd.net/ugc/910171378794663095/C35BF82688A3743D2479A62D58F06001A718B759/) center fixed;
  }
/*Ваш фон End*/

#easyTooltip{
	border: 0px;;
	border-radius: 3px;
}
.popupTableForum td{
	border-right: 0.5px dashed #555;
}
.profile .user-information .icon-button:hover{
	filter: contrast(2)
}
.popupTableForum th{
	border-right: 0px;
}

/*Только для Хрома*/
/*Ширина скролла */
  ::-webkit-scrollbar
  {
    width: 7px;
  }
/*Оформление*/
  ::-webkit-scrollbar-thumb
  {
    border-width: 1px 1px 1px 2px;
    background-color: #4e9644;
  }
/*При наведении курсора*/
  ::-webkit-scrollbar-thumb:hover
  {
    border-width: 1px 1px 1px 2px;
    background-color: #4e9644;
  }

  ::-webkit-scrollbar-track
  {
    border-width: 0;
  }

/*Только для Хрома End*/


  .lc_progress_bar
  {
    height: 2px;
  }

  .lc_buttons .lc_add
  {
    padding: 8px 16px;
    color: #fff;
    margin-top: -6px;
    margin-right: -8px;
}
  .sidebar-link
  {
    clear: both;
    display: block;
    text-align: center;
  }


  .header
  {
    min-height: 113px;
    width: 100%;
    border-bottom: 3px solid #4e9644;
  }

  .errors
  {
    border: 1px solid #4e9644;
  }

  .pagination span:not(.nav_ext)
  {
    border-bottom: 2px solid #4e9644;
  }

  .likeAllContent .popup_profile img,
.forum .forum-messages .author .avatar img
  {
    border: 0px solid #fff;
  }


  .forum .forum-messages .author .avatar span.online:after,
.forum .forum-messages .author .avatar span.offline:after
  {
    border: 0px solid #fff;
  }



  #dle-comments-list .comment .user .user-avatar:after
  {
    border: 0px solid;
  }


  .dark-theme ~ .wrapper,
.dark-theme ~ #dle-content,
.dark-theme ~ .wrapper .flex-stretch .content
  {
    color: #bdbdbd;
    border-color: gray;
  }

  .links a.active,
.links a:hover
  {
    background: no-repeat;
  }

  .user-popup .user-avatar span
  {
    border: 2.5px solid darkgrey;
    width: 38.3%;
    height: 100px;
  }

  .user-popup .user-avatar span:before
  {
    border: 0px;
  }

  .user-popup .user-avatar
  {
    padding-top: 4px;
	padding-bottom: 5px;
  }

  .profile .friends li img
  {
    padding: 2px;
  }
  
  .profile .profile-sub-title
  {
    border-left: 3px solid #4e9644;
  }

  .provider-activated
  {
    border: 2px solid #4e9644;
    padding: 2px;
  }

  .page-note,
.edit-profile p
  {
    border-left: 3px solid #4e9644;
  }

  .content
  {
    box-sizing: border-box;
    float: left;
  }
.dark-theme ~ .wrapper .news .article.fixed:hover{
    background-color: rgba(78,150,68,.15);
}

  .button-up.show
  {
    right: calc(35% - 480px);
  }
  .user-popup .user-avatar img
  {
    border: 0px;
  }

  .site-team .person img
  {
    margin-left: 5px;
    width: 35%;
    height: 100%;
  }

  .site-team .person
  {
    border-radius: 4px;
  }
  .user-menu .user-avatar img
  {
    margin-top: 1.2px;
  }


  .top-menu>ul>li:hover
  {
    background: #4e9644;
  }

  .top-menu>ul li:hover>ul
  {
    border-bottom: 5px solid #4e9644;
  }

  .top-menu>ul>li>ul li:hover.mark
  {
    background-position: right 7px center;
  }
  .icon-button
  {
    width: 48px;
    height: 47px;
  }

  .btn, .btn.btn-default
  {
    border-color: transparent;
  }

  .btn:hover, .btn.btn-default:hover
  {
    border-color: transparent;
  }
  .profile .all-information .information-block:hover>a
  {
    border: 0px;
    border-radius: 5px;
  }

  .profile .all-information .information-block
  {
    border: 0px;
  }

  .likeAllContent
  {
    color: #fff;
    border-radius: 5px;
  }

  .full-story .full-story-top-panel .panel-separator
  {
    background-color: #0d796f00;
  }

  .user-popup .activity .activity-block .activity-count
  {
    text-align: center;
  }
  .full-story .full-story-top-panel .panel-separator
  {
    border-right: 0px;
  }

  .forum .navigation li:not(:last-of-type) a:hover
  {
    border-radius: 3px;
  }

  .forum .navigation input
  {
    border: 0px;
  }

.lc_bb_panel li:hover:before{
	color: #4e9644;
}

.profile .profile-avatar img{ 
padding: 2px;
}

.forum .forum-messages .moderation .complaint a:hover::before{
	color: rgb(210, 11, 33);
}
.friend .friend-name .actions .fa{
	color: #4b8b43;
}

.lc_doregerr>div{
	background: rgba(54, 57, 63, 0.07);
}
.dark-theme~.wrapper .full-story .tags a:hover{
	background: #4b8b43;
}
.friend .left-inform img{
	border-radius: 50%;
}
.opponent-module .complaint_proposal:hover:before{
	color: rgb(210, 11, 33);
}
.informer-sidebar-block .informer-article-block img{
	border-radius: 3px;
}
.user-popup .user-actions .complaint a{
	padding: 10px 20px;
    color: #cecece;
}
.user-popup .user-avatar span{
	border: 2px solid darkgrey;
}
.user-popup .user-avatar span.online:before{
	background-color: #4e9644;
}
.user-popup .user-avatar span:before{
	left: 4px;
	width: 18px;
    height: 18px;
}

  .btn, .btn.btn-default
  {
    border-color: transparent;
	background-color: #d8d8d8;
  }
  .site-team .person img
  {
    margin-left: 5px;
    width: 35%;
    height: 85%;
  }
  .site-team .person
  {
    border-radius: 4px;
	height: 60px;
  }
.dotted-links a, .links-wrapper a{
	border-bottom: 0.5px dashed darkgrey;
    padding-bottom: 1px;
}
.ui-dialog-buttonset button:first-child:hover{
	background-color: #4e9644;
}
.forum .BoxButtom button, .forum .BoxButtom .b01, .forum .actions .b01, .forum .actions .editor-button{
	border-radius: 2px;
}
.fa{
	border-radius: 50%
}

.ed_progress_1 span, .ed_progress_2 span, .ed_progress_3 span, .ed_progress_4 span, .ed_progress_5 span, .ed_progress_6 span, .ed_progress_7 span, .ed_progress_8 span, .ed_progress_9 span, .ed_progress_10 span{
	border: 1px solid #4e9644;
	background: #4e9644;
}
.ed_progress span{
		-webkit-box-shadow: inset 0 0px 0 rgba(255,255,255,.5);
    box-shadow: inset 0 0px 0 rgba(255,255,255,.5);
}
.btn.btn-success, .btn.btn-success:hover{
	border-radius: 3px;
	border: 0px;
}
.btn.btn-success:hover{
	background-color: #4e9644!important;
}
.btn.btn-count:hover, .btn.btn-count{
	border: 0px;
}
.rounded-text{
	border-radius: 5px;
}

.forum .forum-messages div[class^=likeBox] div{
	border: 0px;
}
.forum .forum-messages, .forum .forum-messages div[class^=likeBox] div{
	margin-top: 20px;
}
.forum .forum-messages div[class^=likeBox] div{
	border-radius: 0px;
	border-left: 2px solid #4e9644;
}
.pollanswer:hover:not(.checkboxchecked) {
    background-color: rgba(0, 0, 0, 0.2);
    border-radius: 2px;
}

.dark-theme~.wrapper .partners-block-button{background: linear-gradient(transparent,#151515 37%);}
.profile .btn-group .btn{color:#333;}
.profile .btn-group .btn:hover{color:#151515;}
}
