css:
@-moz-document url-prefix("https://www.bitchute.com") {
/* hotfixes */
#carousel {border: 0}
.unauth-link a {color: #ffffff}
.notification-unread, .notify-count, #nav-top-menu .user-link .notify-count {background: #5f86df; background-color: #5f86df}
.spa, a:link, a:visited {color: #5f86df}
div.channel-card:hover .channel-card-title {color: #ffffff !important}
.notification-actions span, .scripted-link {color: #5f86df}
card:hover .video-card-title a, .channel-card:hover .channel-card-title, .playlist-card:hover .title, .notify-button:hover, .notify-button:focus, .notify-button:active, .channel-videos-title a:hover, .channel-videos-title a:focus, .video-trending-title a:hover, .video-trending-title a:focus, .video-trending-channel a:hover, .video-trending-channel a:focus, .playlist-video .title a:hover, .playlist-video .title a:focus, .playlist-video .channel a:hover, .playlist-video .channel a:focus, .rcad-item:hover .rcad-item-headline, .video-actions span:hover, .video-actions span:hover a {color: #B1DAEC}
.tags ul li a:hover {background: #5f86df}

/* global */
/*[[mob_or_desk]]*/
body, #main-content {background: #121619}
#page-detail, #carousel {background: #121619}
#nav-top-menu .dropdown .notification-dropdown {background: #17212b}
#nav-top-menu .dropdown ul li a:hover, #nav-top-menu .notification-dropdown .dropdown-item:hover {background: #3d556e; color: #ffffff}
p, span, div {color:#CDE7F2}
#page-footer a {color: #5f86df}
#page-footer {color: #485c70}

/* video player */
.plyr--full-ui input[type=range] {color: #5f86df}

/* notifications list */
.notification-list {background: #17212b}
.notification-list .notification-item:hover {background: #3d556e; color: #ffffff}

/* home page */
#carousel .carousel-indicators li {color: #5f86df; fill: #5f86df; background: #5f86df}
.channels {color: #5f86df}
.help-us-grow span {color: #5f86df}
#carousel .carousel-indicators li.active {color: #5f86df; fill: #5f86df; background: #CDE7F2; border-color: #5f86df}

/* fonts */
p, span, div, h1, h2, h3, body, html, pre, strong, b, i, em {font-family: Lato, Arial, sans-serif}

/* navigation */
#nav-top-menu .logo img  {content:url('https://offer.carvana.com/rs/976-AQX-796/images/bit-chute-iced.png')}
#nav-top-menu {background-image: none !important; border: none;}
#nav-top-menu {background-color: #17212b}
#nav-top-menu svg path {fill:#ffffff !important}
#nav-top-menu .user-menu .dropdown-toggle img:hover { border-color: #5fdf6a}
#nav-top-menu .dropdown .user-menu-dropdown {background-color: #2b3947}
#nav-top-menu .dropdown .user-menu-dropdown li a {color: #CDE7F2}
#nav-top-menu .dropdown .user-menu-dropdown li a:hover {background-color: #3d556e; color: #ffffff}

/* hamburger menu */
#nav-side-menu {background-color:#17212b}
#nav-side-menu .social-list {background-color:#2d3b4a}
#nav-side-menu .side-toggle {color: #5fdf6a; fill: #5fdf6a}
#nav-side-menu .side-toggle:hover {color: #CDE7F2; fill: #CDE7F2}
#nav-side-menu div.social-list path {color: #ffffff; fill: #ffffff}
#nav-side-menu .side-content .side-list li a {color: #CDE7F2}
#nav-side-menu .side-content a:hover {background: #5f86df}

/*  search box */
#nav-top-menu .search-box .form-control {color:#CDE7F2}
#nav-top-menu .search-box {color:#CDE7F2; border: none; border-bottom: thin solid #CDE7F2; background: #181F26; border-radius: 0px;}

/* channel cards */
div.channel-card {background-color: transparent; background: transparent; width: 200px; border-radius: 5%}

/* video cards */
div.video-card {background-color: transparent; background: transparent;padding: 5px;}
div.video-card img:nth-child(1) {border-radius: 15px 15px 0 0; border-bottom: 1px solid #5f86df;}
.spa {text-overflow: ellipsis; overflow: hidden; white-space: nowrap;}
.video-card .video-card-text a {color: #5f86df}
.video-card:hover a:nth-child(1) {color: #fff}
.video-card-published {color:#59748b}
.video-card .video-card-text .video-card-channel a {color:#59748b}
.video-card .video-card-channel {margin: 3px 0}
.play-overlay {content:url('https://images.vexels.com/media/users/3/135176/isolated/preview/a6508e565d25ab01f79a35c4319e0083-play-button-flat-icon-by-vexels.png'); transition: none;}

/* page-detail nav tabs */
.nav-tabs>li.active {border-bottom: thin solid #5f86df}
.nav.nav-tabs a {color: #5f86df}

/* video playing page */
h2.sidebar-heading, .sidebar-heading {background: transparent; border-bottom: 2px solid #5f86df}
#page-bar {background: transparent; margin-top: 35px; border-color: #5f86df}
#page-bar h1 {color: #ffffff;}

/* video descriptions */
#video-description a, .video-detail-list a {color: #5f86df}
#video-description span.more, #video-description span.less {color: #5fdf6a; fill: #5fdf6a; position: relative; top: auto; bottom: auto; margin-top:15px; display: block; width: auto; background: transparent; box-shadow: 0px 5px 5px -3px rgba(95,223,106,0.2)}
#video-description span.less {box-shadow: none}
#video-like-on path {fill: #67b5e4}
#video-like-off path {fill: #67b5e4}
#video-like-count {color: #67b5e4}
#video-dislike path, .video-statistics svg:nth-child(0) > path {fill: #aaa}
#video-dislike-count, .video-statistics {color: #aaaaaa}
.video-information .video-statistics .video-like.active {border: none; border-bottom: 2px solid #67b5e4; border-radius: 0px; transition: none}
.video-information .video-statistics .video-dislike.active {border: none; border-bottom: 2px solid #aaaaaa; border-radius: 0px; transition: none}
.video-information .video-statistics .video-like {transition: none}
.video-information .video-statistics .video-dislike {transition: none}
.video-detail-text p {color: #CDE7F2}

/* hide bitchute fund us block it's ugly */
div.video-container div.text-center {display: none}

/* video banners */
.channel-banner .backdrop, div.subscriber, .channel-banner .actions > div {background: #17212b}
.video-information, .video-information .video-actions .action-list {background: #17212b}
.video-actions svg path {fill: #ffffff}
.channel-banner a {color: #5f86df}
.channel-banner a:hover {color: #ffffff}

/* subscription button */
.subscriber button.btn {background: #67b5e4; color: #ffffff; border: none; border-radius: 0}
.subscriber button.btn-default {background: #5fdf6a; color: #ffffff; border: none; border-radius: 0}

/* video playing side bar */
.switch input:checked+.slider {background-color: #5f86df}
.switch .slider {background-color: #aaaaaa}
.switch {border: none}

/* membership banner */
.get-membership-banner {display: none}

/* channel page */
.channel-videos-text a {color: #5f86df}
.channel-videos-title a {color: #5f86df}
.channel-videos-title a:hover {color: #ffffff}
.channel-videos-image-container, .channel-videos-image-container img {border-radius: 20% 0 3% 0}
#channel-description {color: #CDE7F2}
#channel-description a, .channel-about-list a {color: #5f86df}

/* your subscriptions */
.page-bar-container, .subscription-description {background: #17212b; border-radius: 2%}
.subscription-container .subscription-banner {background: #17212b; border-radius: 2%}
.subscription-description {white-space: normal;}
.subscription-container .subscription-image, .subscription-image-container {border-radius: 100%; background: transparent}
.subscription-container .subscription-name {color: #5f86df}
.subscription-container .subscription-last-video {color: #59748b}
.subscription-container .subscription-button {background: #5fdf6a; color: #ffffff; border: none; border-radius: 0; padding: 5px 10px; margin-top: 10px; margin-right: 10px}
.subscription-container path, .subscription-container svg {color: #ffffff; margin-top: 16px; margin-right: 5px}
}
