css:
@-moz-document domain("7cups.com") {
body {
    background: #17191a !important;
}
.navbar {
    background-image: none !important;
}
.bg-primary {
    background-color: #3A3A3A !important;
}
#navbarCollapse > ul > li:nth-child(5) > a > span.hidden-sm-down {
    background-color: #3A3A3A !important;
}
#navbar-main > nav > div > div:nth-child(1) > a {
    background-color: #3A3A3A !important;
}
#main-page-content, .hidden-sm-down {
    background-color: #17191a !important;
}
#actionButtonsChat > div.action.hidden-sm-down {
    background-color: #2B2F30 !important;
}
#bottomStatus.heavyLoad {
    color: #fff !important;
    background-color: #2B2F30 !important;
    background: #089FD1 !important;
}
.convHolder, #actionButtons, #actionButtons img {
    background-color: #FFF !important;
}
#bottomStatus .btn-primary {
    border: none !important;
}
#bottomStatus .hidden-sm-down {
    background-color: #fff !important;
}
#actionButtonsChat .action .pointer {
    background-color: #2B2F30;
}
.action .hidden-sm-down {
    background-color: #2B2F30;
}
.btn-default, .btn-primary {
    color: #fff !important;
    background-color: #17191A !important;
}
.card .nav-link, .active {
    color: #fff !important;
}
.card {
    background-color: #2B2F30 !important;
}
.card .nav-link, .active {
}
.convHolder, #actionButtons, #actionButtons img {
    background-color: #2B2F30 !important;
}
.convHolder .note {
    color: #FFFFFF !important;
}
.tab-content .activeConv {
    background-color: #17191A !important;
}
.card-title {
    color: #ffffff !important;
}
textarea {
    background-color: #2B2F30 !important;
    color: #fff !important;
}
body > div.bootbox.modal.fade.modal-rating {
    color: #000 !important;
}
.meWrap {
    display: inline-block;
    color: black;
    background-color: #1e90fe;
    border-top-left-radius: .5em;
}
.youWrap {
    display: inline-block;
    font-size: 1.1em;
    color: #0d1010;
    background-color: #e0f3ff;
    border-bottom-right-radius: .5em;
}
.text-center, .details {
    color: #fff !important;
}
#MessageScrollDivResize, #MessageScrollDiv {
    border: none !important;
}
.modal-content {
    background-color: #2B2F30 !important;
}
.modal-content a {
    color: #fff !important;
}
.listenerData {
    color: #fff !important;
}
#therapy-referral {
 display: none;   
}
#main-page-content > div > div.row.chatRow > div.hidden-md-down.col-lg-3 > a {
    display: none;
}
table .listenerData .data {
    color: #fff !important;
}
.userScreenName {
    color: #f0f8ff
}
/* by isa */
 .compassionHeart {
    color: #42C0FB;
}
.blueStars span {
    display: inline-block;
    width: 100%;
    height: 100%;
    background-image: url(http://s32.postimg.org/rwi1skddx/rating_stars_empty.png;);
    background-position: 0 50%;
}
.img-circle {
    border-radius: 50% !important;
}
.img-fluid {
    border-radius: 50% !important;
}
.youWrap:hover {
    box-shadow: 0 0 0 .2625em blue;
    color: darkblue;
}
a, h4 {
    color: lightblue;
}
#personalReqModal, #multiModal, #generalReqModal {
    color: white;
}
#therapy-referral {
    display: none !important;
}
.chatBox {
    color: white;
}
a.btn.btn-white {
    background-color: #8a90a3 !important;
    color: #fff !important;
}
.activeConv {
    background-color: #1e90fe !important;
}
.col-md-6.col-lg-7 {
    color: #fff;
}
div.container.text-center {
    background: #000 !important;
}
#layout-wrapper > div:nth-child(10) > div:nth-child(4) > div > div {
    color: white;
}
.card table {
    color: #fff !important;
    ;
    background: #373a3c !important;
    border: none !important;
}
.table-upgrade {
    background-color: #8a90a3;
    border-radius: .8em;
}
.table-bordered, .table-bordered td, .table-bordered th {
    border: none;
}
.postMessageCopy {
    color: #fff;
}
.forumPostHead {
    color: #999;
}
#form1 > label {
    color: #fff !important;
}
}
