css:
@-moz-document domain("iiwiki.us") {
/*Wikipedia logo preference*/
a.mw-wiki-logo{background-image:url(https://en.wikipedia.org/static/images/project-logos/enwiki.png);background-size:135px auto}
/*Sticky header*/
#mw-header-hack{box-shadow: 0 0px 4px 2px rgba(0,0,0,0.1)}
.color-left,.color-middle,.color-right{height:0 !important}
/*Hide username*/
#mw-header h2 span{display:none !important}
/*Left-align the TOC*/
.toc{float:left;margin:1rem 1rem 0 0 !important;max-width:15rem}
/*Hierarchy in TOC*/
#toc ul, .toc ul{margin:0 1rem 0}
/*Full-width content*/
#mw-content{margin-right:0}
/*Move right-sidebar to the lef*/
#mw-related-navigation{clear:left;float:left;padding-left:0;width:13em}
/*Headers have grey underline*/
#mw-content h1.firstHeading,#mw-content h2,#mw-site-navigation .sidebar-chunk h3{border-bottom:1px solid #ccc}
/*Sidebar background*/
.sidebar-chunk{background:#fff}
}
