css:
@-moz-document domain("projecteuler.net") {
:root {
    --body-bg-color:         #212121;
    --main-bg-color:         #4F4F4F;
    --main-text-color:       #90BDE3;
    --link-text-color:       #ACE8B8;
    --link-text-color_hov:     unset;
    --header2-text-color:    #C26B38;
    --header-text-color:     #9FB6C8;
    --main-shadow-color:     #353535;
    --table-bg-color:        #636363;
    --table-bg-color_even:   #3B3B3B;
    --navbar-bg-color:       #3D3D3D;
    --navbar-text-color:     #FFF;
    --navbar-bg-color_act:   #CD761A;
    --navbar-text-color_act: #FFF;
}

}

@-moz-document domain("projecteuler.net") {
*::-webkit-scrollbar {
    width: 0.5em;
}
*::-webkit-scrollbar-track {
    background-color: #2D2D2D;
}
*::-webkit-scrollbar-thumb {
  background-color: #4E4E4E;
  border-radius: 10px
}

*::selection {
    background-color: #91B68E;
}

body {
    color: var(--main-text-color);
    background-color: var(--body-bg-color);
}

a {
    color: var(--link-text-color);
}

#header > img {
    filter: brightness(1.5) contrast(2);
}

/* username and date */
#info_panel {
    color: #AECBE3;
}

#info_panel a[href="search"] img {
    content: url(https://image.flaticon.com/icons/svg/639/639375.svg);
    width: 20px;
}

#info_panel a[href="rss2_euler.xml"] img {
    content: url(https://image.flaticon.com/icons/svg/179/179336.svg);
    width: 18px;
    margin-left: 5px;
}

/* not using
#hamburger {
    background-color: var(--main-bg-color);
}

#hamburger .bar {
    background-color: #DD7200;
}
*/

h2 {
    color: var(--header2-text-color);
}

h1, h3, h4 {
    color: var(--header-text-color);
}

.problem_content, .form_box {
    color: var(--main-text-color);
    background-color: var(--main-bg-color);
    border: none;
    border-radius: 4px;
        box-shadow: 5px 5px 5px var(--main-shadow-color);
}

input, input[type="text"], select, textarea {
    color: #EDEDED;
    background-color: #404040;
    border: 1.2px solid #2F2F2F;
    border-radius: 4px;
}

input[type="text"]:focus, select:focus, textarea:focus {
    outline: none;
    border-color: #30A530;
}

#captcha_image {
    margin-top: 20px;
    margin-bottom: 5px;
    filter: invert(0.87) hue-rotate(180deg);
    border: 1.1px solid #8D8D8D !important;
    border-radius: 10px;
}

.pagination a, .pagination a:link, .pagination a:visited, .pagination a:active, input[type="submit"], input[type="button"] {
    color: #EDEDED;
    background-color: #404040;
    box-shadow: 2px 2px 3px 0px var(--main-shadow-color);
    border: none;
    border-radius: 4px;
}

.pagination a:focus, .pagination a:hover, .pagination a:active,
input[type="submit"]:hover, input[type="button"]:hover,
input[type="submit"]:active, input[type="button"]:active,
input[type="submit"]:focus, input[type="button"]:focus {
    outline: none;
    background-color: #A47340;
}

.pagination a.current {
    background-color: #4A8B4A;
    box-shadow: 2px 2px 3px 0px #376A37;
}

.grid td, .grid th, .grid tr {
    color: var(--main-text-color);
    background-color: var(--main-bg-color);
    /*border-color: #606060;*/
    border: none;
}

#problems_table_page #problems_table th.final_column {
    background: var(--main-bg-color);
    border: none;
    /*border: 1px solid #606060;*/
}

#problems_table_page .sortby {
    color: var(--main-text-color);
}

#problems_table_page .age_hours, #problems_table_page #problems_table a {
    color: #EE914A;
}

.poster_info_2 div {
    color: #D9D9D9 !important;
}

#problems_table tr:nth-child(even) td, .grid tr:nth-child(even)  {
    background-color: var(--table-bg-color_even);
}

#problems_table tr:hover td {
    background-color: var(--table-bg-color);
}

#message {
    border: none;
}

.dark_img {
    filter: invert(0.69);
}

img[src="images/icon_pdf.png"] {
    content:url(https://image.flaticon.com/icons/svg/1126/1126862.svg);
    width: 20px;
}

img[src="images/icon_pdf_gs.png"] {
    content:url(https://image.flaticon.com/icons/svg/1126/1126862.svg);
    width: 20px;
    filter: grayscale(1);
}

td.difficulty_column div {
    border-radius: 4px;
    border-width: 1.2px !important;
}

td.difficulty_column div div {
    height: 8.1px !important;
    background: #F93;
    border-radius: 2px 0 0 2px;
    position: relative;
    top: -0.1px;
    left: -0.1px;
}

td.difficulty_column div div[style*="width:100%"] {
    border-radius: 2px;
}





}

@-moz-document domain("projecteuler.net") {
/* navigation menu */
.nav {
    display: block;
    background-color: transparent;
    position: fixed;
    left: 0;
    top: 50px;
    width: 140px;
    border: none;
}

.nav #highlight {
    color: var(--navbar-text-color);
}

.nav a {
    display: table/*inline-block;*/;
    float: none;
    color: var(--navbar-text-color);
    background-color: var(--navbar-bg-color);
    text-align: left;
    transition: 0.5s;
    width: auto;
    height: 20px;
    position: relative;
    left: -140px;
}

.nav a:hover {
    background-color: var(--navbar-bg-color);
    left: 40px;
    color: var(--navbar-text-color);
    border-radius: 0 7px 7px 0;
}

.nav a:first-child::before {
    border-radius: 0 10px 0 0;
}

.nav a:nth-last-child(2)::before {
    border-radius: 0 0 10px 0;
}

.nav a:hover::before {
    left: -40px;
    border-radius: 0;
}

.nav a::before {
    transition: 0.5s;
    background-color: var(--navbar-bg-color);
    width: 20px;
    height: 20px;
    display: block;
    padding: 10px;
    position: absolute;
    top: 0;
    left: 140px;
}

.nav #current, .nav #current::before {
    color: var(--navbar-text-color_act);
    background-color: var(--navbar-bg-color_act);
}

.nav a[href="about"]::before {
    content: url(https://image.flaticon.com/icons/svg/830/830651.svg);
}

.nav a[href="archives"]::before {
    content: url(https://image.flaticon.com/icons/svg/265/265697.svg);
}

.nav a[href="recent"]::before {
    content: url(https://image.flaticon.com/icons/svg/785/785116.svg);
}

.nav a[href="recent"]#current::before {
    content: url(https://image.flaticon.com/icons/svg/785/785218.svg);
}

.nav a[href="progress"]::before {
    content: "";
    background-image: url(https://image.flaticon.com/icons/svg/1535/1535019.svg);
    background-position: 50%;
    background-size: 26px;
    background-repeat: no-repeat;
}

.nav a[href="account"]::before {
    content: url(https://image.flaticon.com/icons/svg/149/149071.svg);
}

.nav a[href="news"]::before {
    content: url(https://image.flaticon.com/icons/svg/148/148985.svg);
}

.nav a[href="friends"]::before {
    display: inline-block;
    content: url(https://image.flaticon.com/icons/svg/149/149066.svg);
}

.nav a[href="statistics"]::before {
    content: url(https://image.flaticon.com/icons/svg/138/138349.svg);
}

.nav a[href^="sign_out"]::before {
    content: url(https://image.flaticon.com/icons/svg/891/891391.svg);
}

.nav a[href="register"]::before {
    content: url(https://image.flaticon.com/icons/svg/138/138659.svg);
}

.nav a[href="sign_in"]::before {
    content: url(https://image.flaticon.com/icons/svg/891/891389.svg);
}
}

@-moz-document domain("projecteuler.net") {
#forum_page .post_box {
    border: none;
    border-radius: 7px;
}

#forum_page .post_box .post_header {
    border-radius: 7px 7px 0 0;
    background-color: #577185;
    color: white;
    border: none;
}

#forum_page .post_box .post_message {
    color: var(--main-text-color);
    background-color: var(--main-bg-color);
}

.collapse_box, .collapse_box .collapse_click {
    background-color: #2C2C2C;
    color: #D6D6D6;
    border-color: #060606;
}

.collapse_code, .collapse_code code {
    color: #C8C8C8;
    background-color: #3E3E3E;
}

/* numbers */
.hljs-sub .hljs-identifier, .hljs-pi, .hljs-tag, .hljs-tag .hljs-keyword, .hljs-decorator, .ini .hljs-title, .hljs-shebang, .hljs-prompt, .hljs-hexcolor, .hljs-rules .hljs-value, .css .hljs-value .hljs-number, .hljs-symbol, .hljs-symbol .hljs-string, .hljs-number, .css .hljs-function, .clojure .hljs-title, .clojure .hljs-built_in, .hljs-function .hljs-title, .coffeescript .hljs-attribute {
    color: #5FC75F;
}

/* types and functions */
.hljs-class .hljs-title, .haskell .hljs-type, .smalltalk .hljs-class, .hljs-javadoctag, .hljs-yardoctag, .hljs-phpdoc, .hljs-typename, .hljs-tag .hljs-attribute, .hljs-doctype, .hljs-class .hljs-id, .hljs-built_in, .setting, .hljs-params, .clojure .hljs-attribute {
    color: #DCC34D;
}

/* else if */
.hljs-keyword, .hljs-literal, .nginx .hljs-title {
    color: #458EEC;
}

/* strings */
.hljs-string {
    color: #DF893D;
}

/* comments */
.hljs-comment, .hljs-template_comment, .hljs-javadoc, .hljs-comment * {
    color: #4F996E;
}
}

@-moz-document domain("projecteuler.net") {
#about_page #disclaimer {
    background-color: var(--main-bg-color);
    color: #DEDEDE;
    border-color: #414141;
}

.content_tab {
    background-color: var(--main-bg-color);
    border-color: #6A6A6A;
}

.content_tab a#current {
    background-color: #CD761A;
}

.content_tab a {
    color: white;
}

.content_tab a:hover {
    color: var(--main-bg-color);
    background-color: #9D9D9D;
}

#progress_page .problem_unsolved {
    background-color: var(--main-bg-color);
}

#progress_page #problems_solved_section .info {
    color: #A2BBD1;
}

#progress_page .problem_solved {
    background-color: #BA7221 !important;
}

#progress_page .problem_solved .info {
    color: var(--main-bg-color) !important;
}

.info span {
    color: #FFF;
    background-color: #656565;
    border: none;
    box-shadow: 2px 2px 3px 1px var(--main-shadow-color);
}

.info span div:last-child {
    color: #E3E38B !important;
}

.info span div.heading {
    color: #B8F3FF !important;
}

#progress_page .level_box, #progress_page .award_box, #progress_page .post_made_box {
    background-color: var(--main-bg-color);
    margin: 2.5px;
    border-width: 2px;
    border-color: #7A7A7A;
}

#progress_page .level_box a, #progress_page .award_box a, #progress_page .post_made_box a, 
#progress_page .level_box a:hover, #progress_page .award_box a:hover, #progress_page .post_made_box a:hover {
    color: var(--main-text-color);
}

#difficulty_progress_table tr:nth-child(even) td, .problem_history tr:nth-child(even) td {
    background-color: var(--table-bg-color_even);
}

.small_notice {
    color: #85AFD2;
}

#news_page .news, .data_entry {
    background-color: var(--main-bg-color);
    border: none;
    border-radius: 10px;
}

#friends_table>tbody>tr:first-child, #friends_table>tbody>tr:first-child th {
    color: white;
    background-color: #6C8492;
}

#friends_table>tbody>tr:first-child th:nth-child(even) {
    background-color: #5C707C;
}

#friends_table td:nth-child(even) {
    background-color: var(--table-bg-color_even);
}

#friends_page #friends_table .rank_column {
    color: var(--main-text-color);
}

#friends_table .username_column .info, #friends_table .username_column td, #friends_table .username_column tr {
    background-color: transparent;
    border: none;
}

#account_page #profile_image_box, #friends_page #friends_image {
    display: none;
}

.data_entry {
    box-shadow: none;
}

#next_problem img, #previous_problem img {
    content: url(https://image.flaticon.com/icons/svg/283/283707.svg);
    width: 20px;
    filter: hue-rotate(-90deg);
}

#previous_problem img {
    transform: scaleX(-1);
}
}
