css:
@-moz-document domain("lazyfoo.net") {
body {
    font-family: sans-serif;
    font-size: 1rem;
    line-height: 1.5;
    color: #fff;
}
body,
html {
    background: #333;
}

h1 {
    width: 100%;
    color: #fff;
    text-align: center;
}

h2 {
    width: 100%;
    color: #F4BF75;
    text-align: center;
}

h3 {
    width: 100%;
    color: #272822;
    text-align: center;
}

h6 {
    width: 100%;
    font-size: 1.2rem;
    max-width: 500px;
    margin: 45px auto;
    color: #FFF173;
    background: rgba(0, 0, 0, .4);
    padding: 10px 25px;
    text-align: center;
}

b {
    color: #F92672;
}

a.nav {
    padding: 10px;
    color: #F4BF75;
}

a.leftNav {
    float: left;
    color: #A6E22E;
}

a.tutLink {
    color: #3EE9B5;
    margin: 0 10px;
}

div.nav {
    display: flex;
    flex-flow: row wrap;
    max-width: 900px;
    margin: 0 auto;
    align-items: center;
}

div.tutPreface,
.tutFooter {
    width: 100%;
    max-width: 900px;
    background-color: rgba(35, 39, 45, .9) !important;
    color: #fff;
    border-radius: 5px;
    box-shadow: 2px 5px 40px rgba(0, 0, 0, .1), 2px 5px 10px rgba(0, 0, 0, .2);
    text-align: center !important;
    padding: 25px !important;
}

div.tutCodeHeader {
    width: 90%;
    margin: 0px auto 0px auto;
    background-color: #F9F8F5;
    border: medium solid #F92672;
    color: #272822;
    text-align: left;
}

div.tutCode {
    width: 90%;
    margin: 25px auto;
    max-width: 900px;
    font-family: dejavu sans mono;
    background-color: #272822;
    border: medium solid #F88369;
    box-shadow: 2px 5px 40px rgba(0, 0, 0, .1), 2px 5px 10px rgba(0, 0, 0, .2);
    border-radius: 5px;
    color: #fafafa;
    white-space: pre;
}

div.tutText {
    width: 100%;
    max-width: 900px;
    background-color: rgba(35, 39, 45, .9);
    color: #fff;
    border-radius: 10px;
    margin: 0 auto;
    box-shadow: 2px 5px 40px rgba(0, 0, 0, .1), 2px 5px 10px rgba(0, 0, 0, .2);
    padding: 25px;
    font-size: 1rem;
    line-height: 1.5;
    text-align: left;
}

div.tutImg {
    margin: 0 auto;
    text-align: center;
    width: auto;
    display: flex;
    object-fit: contain;
}
.tutImg img {
    width: 100%;
    object-fit: contain;
    display: inline-flex;
    box-shadow: none;
    border: none;
    margin: 10px;
}

td.ToCTitle {
    background-color: #272822;
    border: thin solid #66D9EF;
    color: #f9f8f5;
    height: 30px;
    text-align: center;
}

td.tutLink {
    background-color: #f9f8f5;
    border: thin solid #FFF;
    color: #272822;
    text-align: center;
}
.content {
    display: flex;
    flex-flow: column;
    margin: 0 auto !important;
    max-width: 1200px !important;
    width: 90% !important;
}
}
