css:
@-moz-document url-prefix("https://hannover.freifunk.net/karte/") {
html body{background:#111;color:#fff}html header{background:rgba(254,254,254,0.02);border-bottom-color:#2b2b2b}html .leaflet-container{background:#0d151c}html .leaflet-label.leaflet-label-right{background-color:#111}html .leaflet-control-container .leaflet-control-layers-toggle{background:#2b2b2b;color:#fefefe}html .leaflet-control-zoom a{background:#2b2b2b;color:#fefefe}html .leaflet-control-zoom a:hover{background:#111}html .leaflet-control-layers.leaflet-control{opacity:.9}html .language-switch{color:#fefefe}html .language-switch option{background:#111}html .filter-node input{color:#fefefe}html .sidebar .infobox,html .sidebar .container{background:rgba(17,17,17,0.97);border-right:1px solid #000}html .sidebar img{-webkit-filter:invert(100%);filter:invert(100%)}@media screen and (max-width: 1199px){html .sidebar{background:#111}}html .tabs{background:rgba(254,254,254,0.02);border-bottom-color:#2b2b2b}html .tabs li{color:rgba(254,254,254,0.5)}html .tabs li:hover{color:#fefefe}html .bar{background:#306415}html .bar.warning{background:#832c20}html .bar label{color:#111}html button{background:#2b2b2b;color:#fefefe}html button:hover{background:#111}html button.close{background:transparent;color:rgba(254,254,254,0.5)}
}
