@-moz-document url("https://ani.gamer.com.tw/") {
/*依更新日 依人氣*/
#BH_background > div.container > section > div > div > a.new, #BH_background > div.container > section > div > div > a.popular{
	color: #FFFFFF !important;
	background: #333333 !important;
	border-radius: 3px !important;
	padding-right: 4px !important;
  	padding-left: 4px !important;
	padding-top: 1px !important;
  	padding-bottom: 2px !important;
}

#BH_background > div.container > section > div > div > a.new:hover, #BH_background > div.container > section > div > div > a.popular:hover, #BH_background > div.container > section > div > div > a.now{
	color: #FFFFFF !important;
	background: #469BBC !important;
	border-radius: 3px !important;
	padding-right: 4px !important;
  	padding-left: 4px !important;
	padding-top: 1px !important;
  	padding-bottom: 2px !important;
}

/*看更多*/
#BH_background > div.container > section > div > div > a:not(.now):not(.new):not(.popular){
	color: #FFFFFF !important;
	background: #333333 !important;
	border-radius: 3px !important;
	padding-right: 6px !important;
  	padding-left: 2px !important;
	padding-top: 4px !important;
  	padding-bottom: 10px !important;
}

#BH_background > div.container > section > div > div:not(.title):hover > a:not(.now):not(.new):not(.popular){
	color: #FFFFFF !important;
	background: #469BBC !important;
	border-radius: 3px !important;
	padding-right: 6px !important;
  	padding-left: 2px !important;
	padding-top: 4px !important;
  	padding-bottom: 10px !important;
}
}

@-moz-document url-prefix("https://ani.gamer.com.tw/animeList.php"), url-prefix("https://ani.gamer.com.tw/mygather.php") {
