@-moz-document url-prefix("chrome-extension://clngdbkpkpeebahjckkjfobafhncgmne") {
/* ==UserStyle== 
@name         1973 prism - the Dark Side of Stylus 
@description  This Stylus theme is dark with colourful accents for both the Stylus edit style and styles management pages. The theme displays a clear visual identifier on top of a code block to indicate that code has been modified. It also introduces a new theme for the code editor (CodeMirror) that is based on the okaidia theme for PrismJS by @ocodia, which in turn was loosely based on the Monokai textmate theme by @monokai. To activate this 1973 prism theme for CodeMirror, simply select the Default theme in the Stylus settings. Press F5 to refresh once or twice if the editor still shows black on white after activating the default theme.
@namespace    ocor61.blogspot
@version      0.1.2
@author       OmeCor <ocor61@gmail.com> (ocor61.blogspot.com)
@homepageURL  https://ocor61.blogspot.com/user-style-1973-prism-the-dark-side-of-stylus.html
@license      unlicense
@preprocessor stylus
==/UserStyle== */

/* - - - - - - - - - - - - - 
 * Section Stylus interface: 
 * - - - - - - - - - - - - - */
#stylus-edit,
#stylus-manage,
#stylus-install-usercss {
  background: #1F201C!important;
  color: lightgrey;
  font-family: Roboto, Ubuntu, Arial, sans-serif;
}
#stylus-manage .newUI .entry .style-name:hover::before,
#stylus-manage .entry.enabled.even h2.style-name:hover::before {
  background: rgba(0, 0, 0, 0.6)!important;
  color: #27FFFB!important;
}
#stylus-manage .entry.enabled.odd h2.style-name:hover::before {
  background: rgba(255, 255, 255, 0.2)!important;
  color: #27FFFB!important;
}
a.style-name-link,
a.style-name-link:hover {
  color: #27FFFB!important;
  font-weight: normal;
}
#stylus-install-usercss #header-content-wrapper a {
  color: #27FFFB;
}
#stylus-edit button,
#style-actions button,
#options-buttons button,
#backup-buttons button,
#message-box-buttons button,
#popup-options button {
  background-image: none;
}
#message-box-close-icon {
  top: 13px;
  right: 13px;
}
.dropbtn,
#backup-buttons .dropdown-content {
  background-color: #525252!important;
  border-color: #525252;
  border-radius: 2px;
  color: #ccc;
  font-weight: normal!important;
}
#backup-buttons .dropdown-content a {
  color: #ccc;
}
/* #backup-buttons .dropdown-content: */
.dropbtn:hover,
#file-all-styles:hover,
#sync-dropbox-export:hover,
#unfile-all-styles:hover,
#sync-dropbox-import:hover {
  background-color: #343434!important;
  border-color: #555!important;
  color: #ccc!important;
}
#message-box #message-box-title {
  color: #12938E;
}
#message-box #message-box-contents {
  background-color: #525252;
  color: #ccc;
}
#message-box #message-box-buttons {
  background-color: #6e6e6e;
}
#header {
  border-right: 2px solid #1F201C;
  -webkit-box-shadow: 3px 0 5rem -0.2rem rgba(0, 0, 0, .6);
  box-shadow: 3px 0 5rem -0.2rem rgba(0, 0, 0, .6);
}
.style-name-link {
  color: lightgrey;
}
body.find-open .search-target-editor {
  outline: 1px!important;
  outline-color: #000000!important;
}
body.find-open .cm-searching.search-target-match {
  background: darkorange!important;
  color: black;
}
input[type="checkbox"]:not(.slider),
select,
button,
#message-box-buttons button {
  background-color: #525252;
  border-color: #525252;
  border-radius: 2px;
  color: #ccc;
  font-weight: normal!important;
}
input[type="checkbox"]:not(.slider):hover,
select:hover,
button:hover,
#message-box-buttons button:hover {
  background-color: #343434!important;
  border-color: #555!important;
  color: #ccc!important;
}
input.style-contributor,
input[id="editor.tabSize"] {
  background-color: #d5d5d5;
  border-color: #6e6e6e;
  border-radius: 3px;
  border-style: solid;
  border-width: 1px;
  color: #424242;
}
input.style-contributor:hover,
input[id="editor.tabSize"]:hover,
input.style-contributor:active,
input[id="editor.tabSize"]:active,
input.style-contributor:focus,
input[id="editor.tabSize"]:focus {
  border-color: #12938E;
}
.svg-icon,
.svg-icon.select-arrow {
  filter: brightness(175%)!important;
  fill: #cecece!important;
}
.svg-icon.settings,
.svg-icon.info,
.svg-icon.edit {
  filter: brightness(100%)!important;
  fill: cornflowerblue!important;
}
.svg-icon.remove,
.svg-icon.add {
  filter: brightness(100%)!important;
  fill: #f92672!important;
}
#to-mozilla-help,
#keyMap-help,
#search-help,
#sorter-help,
#update-history,
#usercss-wiki {
  filter: brightness(110%)!important;
  fill: cornflowerblue!important
}
#filters .select-resizer {
  left: 25px;
}
.svg-icon.settings:hover,
.svg-icon.info:hover,
.svg-icon.edit:hover,
.remove-applies-to:hover .svg-icon,
.add-applies-to:hover .svg-icon {
  filter: brightness(145%)!important;
  fill: #27FFFB!important;
}
.svg-icon.checked {
  fill: #f92672!important;
}
.svg-icon.checked:hover {
  fill: #27FFFB!important;
}
#stylus-popup #installed .style-name .svg-icon {
  filter: none!important
}
#stylus-popup #installed .style-name .checker, #stylus-popup #installed .style-name .svg-icon.checked {
  background: #525252!important;
  border-color: grey!important;
  filter: none!important;
}
#stylus-popup #installed .style-name .checker:hover, #stylus-popup .main-controls .style-name .svg-icon:hover {
  background: #343434!important;
  border-color: white!important;
}
#manage-text a {
  color: lightgrey;
}
#help-popup {
  background-color: #5e5e5e;
}
#help-popup .title {
  background-color: #424242!important;
  border-color: #555!important;
  color: #ccc!important;
}
#help-popup input {
  border: 1px solid #555;
  border-radius: 3px;
  padding: 3px 5px;
}
#help-popup input:hover,
#help-popup input:active,
#help-popup input:focus {
  border-color: #12938E;
}
#help-popup .CodeMirror {
  background: #1F201C;
  color: #eee;
}
#help-popup .CodeMirror-activeline {
  color: #285959!important;
}
#help-popup .CodeMirror-activeline-background {
  background: rgba(255, 255, 255, .6);
}
#help-popup .CodeMirror-gutter-background.CodeMirror-activeline-gutter {
  background: #285959;
}
#help-popup .CodeMirror-activeline .CodeMirror-linenumber.CodeMirror-gutter-elt {
  color: #ccc;
}
#help-popup .CodeMirror-linenumber.CodeMirror-gutter-elt,
#stylus-install-usercss .CodeMirror-gutters,
#stylus-install-usercss .CodeMirror-linenumber.CodeMirror-gutter-elt,
#stylus-install-usercss .CodeMirror-gutter.CodeMirror-foldgutter {
  background: #424242;
  color: #ccc;
}
#help-popup .contents a {
  color: #27FFFB;
}

/* Import & Export: */
[style="display: block; pointer-events: auto;"] .CodeMirror-wrap {
  background: rgb(232, 187, 208, .3)!important;
}
#help-popup .CodeMirror {
  background-color: #285959!important;
  color: black;
}

#stylus-popup, #find-styles-link, .write-style-link {
  background: #272822!important;
  color: lightgrey;
}
#disableAll-label {
  color: orangered!important;
}
.search-result-description,
.search-result-meta {
  color: #272822!important;
}
#stylus-popup #installed {
  background-color: rgba(255, 255, 255, .25)
}

/* - - - - - - - - - - - - - - - - - - - - - -
 * Section for CodeMirror (the Stylus editor)
 * - - - - - - - - - - - - - - - - - - - - - - */
label.code-label {
  display: block!important;
  margin-bottom: 10px!important;
  padding: 4px 8px 0 15px;
  cursor: default;
}
#sections > .section > label::after {
  counter-increment: codebox;
  content: counter(codebox);
  margin-left: 0.25rem;
}
#stylus-edit.dirty div.section label.code-label::after {
  background: #285959!important;
  border: 1px solid #27FFFB;
  border-radius: 2px; 
  color: #27FFFB;
  content: 'modified | contains un{S}aved changes';
  cursor: progress;
  font-family: Roboto, Ubuntu, Consolas, 'Roboto Mono', 'Ubuntu Mono', 'Source Code Pro', 'Courier New';
  font-size: 14px;
  font-weight: 400;
  letter-spacing: .05em;
  line-height: 14px;
  margin-left: 12px;
  padding: 4px 6px 2px 6px;
  visibility: visible;
}

#search-replace-dialog textarea {
  color: rgba(111, 111, 111, 4);
  background-color: rgba(219, 219, 219, 1);
}

/* - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -
 * This section based on okaidia prismjs theme by @ocodia (https://github.com/ocodia/)
 * Loosely based on Monokai textmate theme by http://www.monokai.nl/
 * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -
 * Theoretically, this can be installed as a separate color theme for the Stylus code editor:
 * 1. Change all instances of '.cm-s-default' below into '.cm-s-1973-prism',
 * 2. Copy all the lines below the line '----- COPY ALL BELOW LINES INTO A SEPARATE FILE -----' into a separate file,
 * 3. Save the file as 1973-prism.css,
 * 4. Copy the file to the CodeMirror theme folder, which can be found at:
 *    C:\Users\[YOUR-USERNAME]\AppData\Local\[YOUR-BROWSERNAME]\User Data\Default\Extensions\[YOUR-UUID]\[VERSION-NUMBER]\vendor\codemirror\theme
 * 
 * If all went well, you SHOULD now be able to pick the 1973 prism theme in the Stylus settings.
 * I did write 'theoretically' and 'SHOULD', because I have not yet succeeded in making this work :/
 * - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - */

/* ----- COPY ALL BELOW LINES INTO A SEPARATE FILE ----- */
/*

    Name:       1973 prism
    Author:     Ome Cor (http://ocor61.blogspot.com)

    Based on okaidia prismjs theme by @ocodia (https://github.com/ocodia/)
    CodeMirror template by Jan T. Sott (https://github.com/idleberg/base16-codemirror)

*/
.cm-s-default.CodeMirror,
#stylus-install-usercss .CodeMirror {
  background: #272822;
  border-radius: 3px;
  color: #d6d5d4;
  font-family: Consolas, 'Roboto Mono', 'Ubuntu Mono', monospace;
  font-size: 14px;
}
.cm-s-default.CodeMirror pre {
  background: transparent;
  -webkit-tap-highlight-color: black;
}
.cm-s-default div.CodeMirror-selected {
  background: #3a3432;
}
.cm-s-default .CodeMirror-line::selection,
.cm-s-default .CodeMirror-line > span::selection,
.cm-s-default .CodeMirror-line > span > span::selection {
  background: rgba(58, 52, 50, .99);
}
.cm-s-default .CodeMirror-gutters {
  background: #283939;
  border-right: 0px;
}
.cm-s-default .CodeMirror-guttermarker {
  color: #66d9ef;
}
.cm-s-default .CodeMirror-guttermarker-subtle {
  color: #5c5855;
}
.cm-s-default .CodeMirror-gutter-wrapper {
  filter: brightness(200%);
}
.cm-s-default .CodeMirror-gutter-background {
  background: #27BBFB!important
}
.cm-s-default span[role="presentation"] {
  color: white;
}

.cm-s-default .CodeMirror-cursor {
  border-left: 2px solid #807d7c;
}

.cm-s-default span.cm-builtin {
  color: #46e22e;
}
.cm-s-default span.cm-comment,
#stylus-install-usercss .cm-comment {
  color: slategray;
}
.cm-s-default span.cm-atom {
  color: white;
}
.cm-s-default span.cm-number {
  color: #ae81ff;
}
.cm-s-default span.colorview-swatch {
  color: cornflowerblue;
}

.cm-s-default span.cm-qualifier {
  color: #a6e22e;
}
.cm-s-default span.cm-property,
.cm-s-default span.cm-attribute {
  color: #f92672;
}
.cm-s-default span.cm-keyword {
  color: #66d9ef;
}
.cm-s-default span.cm-string {
  color: #a6e22e;
}

.cm-s-default span.cm-variable {
  color: cornflowerblue;
}
.cm-s-default span.cm-variable-2 {
  color: #01C6E4;
}
.cm-s-default span.cm-variable-3 {
  color: lightblue;
}
.cm-s-default span.cm-def {
  color: #e8bbd0;
}
.cm-s-default span.cm-bracket {
  color: orangered;
}
.cm-s-default span.cm-tag {
  color: #66d9ef;
}
.cm-s-default span.cm-keyword {
  color: red;
}
.cm-s-default span.colorview {
  color: cornflowerblue;
}
.cm-s-default span.cm-link {
  color: #ae81ff;
}
.cm-s-default span.cm-error {
  background: #66d9ef;
  color: #807d7c;
}

.cm-s-default .cm-overlay {
  background-color: rgba(255, 255, 255, .1)!important;
}
.cm-s-default .cm-matchHighlight {
  background-color: rgba(0, 0, 0, .7)!important;
}
.cm-s-default .CodeMirror-activeline-background {
  background: rgba(0, 0, 0, .45);
}
.cm-s-default .CodeMirror-matchingbracket {
  background: lightyellow;
  color: #272822!important;
}

}
