css:
@-moz-document domain("nitrotype.com") {
body {
    background-color: #2d2e2e;
    overflow-y: scroll;
    background: fixed url(https://i.imgur.com/KQuBpa6.png) repeat-y center 0;
    overflow-x: hidden;
    position: relative;
}

html {
    background: url("https://www.nitrotype.com/dist/site/images/backgrounds/bg-noise.png") top left repeat, #2d2e2e;
    box-sizing: border-box;
    color: #2d2e2e;
    font-family: "Montserrat", sans-serif;
    font-size: 16px;
    font-weight: 300;
    line-height: 1.6;
}

.bg--gradient {
    background: url("https://www.nitrotype.com/dist/site/images/backgrounds/bg-noise.png") top left repeat, linear-gradient(180deg, #000000 0%, #202020 80%);
}

.bg--gradient-dkGold {
    background: url("https://i.imgur.com/XHwBaPG.png") top left repeat, linear-gradient(to bottom, #43380e 0%, #13141c 15%, #13141c 100%);
}

.footer {
    background: url("https://www.nitrotype.com/dist/site/images/backgrounds/bg-noise.png") top left repeat, #2d2e2e;
    border-radius: 20px 20px 0 0;
    position: relative;
    flex-shrink: 0;
    width: 100%;
    overflow-y: hidden;
}

.card--a {
    background-color: #6060;
    color: #d3d3d3
}

.card--b {
    background-color: #202020;
    color: #d3d3d3;
}

.card--c {
    background-color: #020202;
    color: #d3d3d3;
}

.card--d {
    background: url("https://www.nitrotype.com/dist/site/images/backgrounds/bg-noise.png") top left repeat, #2d2e2e;
    background-color: #000000;
    color: #d3d3d3;
}

.card--fade.card--d {
    background: url("https://www.nitrotype.com/dist/site/images/backgrounds/bg-noise.png") top left repeat, #2d2e2e;
}

.card--e {
    background: linear-gradient(145deg, #20222e 0%, #383b4b 30%, #2E3141 40%, #292c3b 41%, #292c3b 51%, #272a37 80%, #20222e 100%), #3b3d4c;
    border: 1px solid #3b3d4c;
    color: #d3d3d3;
}

.theme--pDefault .profile-bg {
    background: url("https://i.imgur.com/BQrVdbw.png") top left repeat, linear-gradient(0deg, #2d2e2e 60%, #ffffff 100%);
    color: #d3d3d3;
}

.theme--pDefault .profile-bgHead {
    background: url("https://i.imgur.com/BQrVdbw.png") no-repeat 75% -50%/45%, url("https://i.imgur.com/BQrVdbw.png") top left repeat, linear-gradient(0deg, #2d2e2e 10%, #ffffff 100%);
}

.card--shadow {
    box-shadow: none;
}

.card--shadow-l {
    box-shadow: none;
}

.theme--pDefault .profile-body {
    color: #d3d3d3;
}

.theme--pDefault .profile-head {
    color: #202020;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.5);
}

.theme--pDefault .profile-username {
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.5);
}
.theme--pDefault .profile-username .profile-usernameLink {
    font-weight: inherit;
    color: #202020;
}
.theme--pDefault .profile-username .profile-usernameLink:hover {
    color: rgba(255, 255, 255, 0.5);
}

.theme--pDefault .profile-userteam {
    color: #202020;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.5);
}
.theme--pDefault .profile-userteam:hover {
    color: rgba(255, 255, 255, 0.75);
}

.prog-points {
    color: #202020;
    font-size: 12px;
    font-weight: 600;
    left: 0;
    position: absolute;
    text-align: right;
    text-shadow: 0 1px 10px rgba(2, 2, 2, 0.14);
    bottom: 100%;
    white-space: nowrap;
    width: 100%;
}

.theme--pDefault .profile-levelNum {
    color: #F9D034;
    text-shadow: 0 2px 2px rgba(2, 2, 2, .8);
}

.theme--pDefault .profile-title {
    color: #F9D034;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.8);
}

.profile-car {
    filter: drop-shadow(10px 4px 3px #202020);
}

.profile.is-gold .mysteryBox-copy {
    background: none;
    background-size: cover;
    border-radius: 20px;
    border: 1px solid rgba(249, 208, 52, 0.5);
    padding: 40px 20px 20px;
}

.btn--c {
    background: linear-gradient(to right, #D62F3A, #ab262e);
    border-radius: 5px;
    border: 1px solid #ab262e;
    color: rgba(255, 255, 255, 0.9);
    font-weight: 600;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
}
.btn--c:focus {
    background: linear-gradient(to right, #da444e, #D62F3A);
    color: #fff;
}
.btn--c:hover {
    background: linear-gradient(to right, #da444e, #D62F3A);
    color: #fff;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.65);
}


.btn--ghost {
    background-color: transparent;
    border-radius: 5px;
    color: #D62F3A;
    font-weight: 600;
}
.btn--ghost:focus {
    background: linear-gradient(to right, #da444e, #D62F3A);
    color: #fff;
}
.btn--ghost:hover {
    background-color: rgba(41, 44, 59, 0.01);
    color: #ee333373;
}

.footer-copyright {
    background: url("https://www.nitrotype.com/dist/site/images/backgrounds/bg-noise.png") top left repeat, #2d2e2e;
    padding: 20px;
    position: relative;
    width: 100%;
    z-index: 1;
}

.card--e {
    background: linear-gradient(145deg, #080808 0%, #404040 30%, #505050 40%, #606060 41%, #505050 51%, #404040 80%, #080808 100%), #3b3d4c;
    border: 1px solid #000;
    color: #d3d3d3;
}

.card--d {
    background: url("https://www.nitrotype.com/dist/site/images/backgrounds/bg-noise.png") top left repeat, #00000091;
    color: #d3d3d3;
}

.tc-dodger {
    color: #D62F3A;
}

.tc-lemon {
    color: #D62F3A;
}

.tc-emerald {
    color: #D62F3A;
}


.prog--a {
    background: #020202;
    border: 1px solid #D62F3A;
}
.prog--a .prog-bar {
    background: linear-gradient(90deg, #7a0e0e 0%, #c31616 50%, #f41c1c 100%);
}
.prog--a .prog-points {
    color: rgba(255, 255, 255, 0.8);
    font-style: italic;
    font-weight: 500;
    letter-spacing: 1px;
}

.prog--l {
    height: 23px;
}
.prog--l .prog-bar {
    height: 23px;
}

.dropdown--nav {
    border-radius: 5px 5px 0 0;
    color: #fff;
    padding: 5px;
}
.dropdown--nav:hover,
.dropdown--nav.is-active {
    background: url("https://www.nitrotype.com/dist/site/images/backgrounds/bg-noise.png") top left repeat, #2d2e2e;
    color: #fff;
    box-shadow: 0 28px 28px 0 rgba(2, 2, 2, 0.05), 0 17px 17px 0 rgba(2, 2, 2, 0.2), 0 8px 8px 0 rgba(2, 2, 2, 0.15);
}
.dropdown--nav:hover .dropdown-trigger,
.dropdown--nav.is-active .dropdown-trigger {
    color: #fff;
}
.dropdown--nav .dropdown-items {
    background: url("https://www.nitrotype.com/dist/site/images/backgrounds/bg-noise.png") top left repeat, #2d2e2e;
    border-radius: 0 5px 5px 5px;
    box-shadow: 0 28px 28px 0 rgba(2, 2, 2, 0.05), 0 17px 17px 0 rgba(2, 2, 2, 0.2), 0 8px 8px 0 rgba(2, 2, 2, 0.15);
    width: 200px;
    left: 0;
}
.dropdown--nav .dropdown-items.list .list-item {
    margin-right: 0;
}
.dropdown--nav .dropdown-link {
    color: #fff;
    padding: 15px;
    font-size: 12px;
}
.dropdown--nav .dropdown-link:hover {
    color: #fff;
    background: url("https://www.nitrotype.com/dist/site/images/backgrounds/bg-noise.png") top left repeat, #f41c1c;
    border-left-color: #59FFA0;
}
.dropdown--nav .dropdown-item {
    border-bottom: 1px solid #2E3141;
}
.dropdown--nav .dropdown-item:last-of-type {
    border-bottom: 0px;
}

.dropdown--account:hover,
.dropdown--account.is-active {
    background: url("https://www.nitrotype.com/dist/site/images/backgrounds/bg-noise.png") top left repeat, #2d2e2e;
    color: #2E3141;
    box-shadow: 0 28px 28px 0 rgba(2, 2, 2, 0.05), 0 17px 17px 0 rgba(2, 2, 2, 0.2), 0 8px 8px 0 rgba(2, 2, 2, 0.15);
}
.dropdown--account .dropdown-items {
    background: url("https://www.nitrotype.com/dist/site/images/backgrounds/bg-noise.png") top left repeat, #2d2e2e;
    border-radius: 5px 0 5px 5px;
    box-shadow: 0 28px 28px 0 rgba(2, 2, 2, 0.05), 0 17px 17px 0 rgba(2, 2, 2, 0.2), 0 8px 8px 0 rgba(2, 2, 2, 0.15);
    width: 250px;
}
.dropdown--account .dropdown-link {
    color: #fff;
    padding: 15px;
    font-size: 12px;
}
.dropdown--account .dropdown-link:hover {
    color: #fff;
    background: url("https://www.nitrotype.com/dist/site/images/backgrounds/bg-noise.png") top left repeat, #f41c1c;
}
.dropdown--account .dropdown-trigger {
    color: #c31616;
}
.dropdown--account .dropdown-item:first-of-type {
    border-bottom: 1px solid #2E3141;
}
.dropdown--account .dropdown-item:first-of-type .dropdown-link {
    flex-direction: column;
    width: 100%;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
    align-items: flex-start;
}
.dropdown--account .dropdown-item:last-of-type .dropdown-link {
    color: rgba(255, 255, 255, 0.6);
    background: url("https://www.nitrotype.com/dist/site/images/backgrounds/bg-noise.png") top left repeat, #2d2e2e;
}
.dropdown--account .dropdown-item:last-of-type .dropdown-link:hover {
    color: #fff;
    background: #D62F3A;
}
.dropdown--account.dropdown--r .dropdown-items {
    border-top-right-radius: 0;
    border-top-left-radius: 5px;
}
.dropdown--account.is--online .dropdown-trigger {
    color: #D62F3A;
}

.prog {
    background: rgba(2, 2, 2, 0.3);
    border-top: 2px solid rgba(2, 2, 2, 0.8);
    display: block;
    position: relative;
    width: 100%;
    filter: drop-shadow(10px 1px 19px #20202040);
}

.tabs--a .tab {
    background: url("https://www.nitrotype.com/dist/site/images/backgrounds/bg-noise.png") top left repeat, #2d2e2e7a;
    color: rgba(255, 255, 255, 0.5);
    text-decoration: none;
}
.tabs--a .tab:hover {
    background: url("https://www.nitrotype.com/dist/site/images/backgrounds/bg-noise.png") top left repeat, #2d2e2ecc;
    color: rgba(255, 255, 255, 0.75);
}
.tabs--a .tab.is-active {
    background: url("https://www.nitrotype.com/dist/site/images/backgrounds/bg-noise.png") top left repeat, #2d2e2e;
    color: #fff;
}

.input-label.input-label--radio:hover {
    background: #D62F3A;
}

a {
    color: #D62F3A;
}

a:active,
a:hover,
a:focus {
    color: #d62f3a82;
}

.chart--a .chart-gradient .chart-gradient1 {
    stop-color: #c31616;
    stop-opacity: 0.5;
}

.chart--a .chart-gradient .chart-gradient2 {
    stop-color: #f41c1c;
    stop-opacity: 0.3;
}

.chart--a .chart-gradient .chart-gradient3 {
    stop-color: #f64949;
    stop-opacity: 0.6;
}

.chart--a .chart-line {
    stroke: #f64949 !important;
    stroke-width: 2;
}
.chart--a .chart-line circle {
    fill: #fcc6c6 !important;
    stroke-width: 2 !important;
    stroke: #c31616 !important;
}

.btn--page.is-current {
    background: linear-gradient(to bottom, #f41c1c 20%, #7a0e0e 100%);
    border-color: #c31616;
    box-shadow: 0 28px 28px 0 rgba(2, 2, 2, 0.05), 0 17px 17px 0 rgba(2, 2, 2, 0.2), 0 8px 8px 0 rgba(2, 2, 2, 0.15);
    color: #fff;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
}

.btn--page:hover:not(.is-unavailable):not(.is-current) {
    background: #3b2929;
    border-color: #433030;
    box-shadow: 0 28px 28px 0 rgba(2, 2, 2, 0.05), 0 17px 17px 0 rgba(2, 2, 2, 0.2), 0 8px 8px 0 rgba(2, 2, 2, 0.15);
    color: #fff;
    transform: scale(1.1) translateY(-2px);
}

.tooltip--a:after {
    background: url("https://www.nitrotype.com/dist/site/images/backgrounds/bg-noise.png") top left repeat, #f41c1c;
}

.tooltip--a.tooltip--c:before {
    border-color: #f41c1c transparent transparent transparent;
}

.link--h {
    color: #f41c1c;
}
.bg--dodger {
    background: #f41c1c;
}

.btn.is-active.btn--tab.btn--tabB {
    border-left-color: #f41c1c;
}

.btn.is-active.btn--tab {
    border-left: 5px;
    border-left-style: solid;
    background: url("https://www.nitrotype.com/dist/site/images/backgrounds/bg-noise.png") top left repeat, #404040;
}

.btn--f {
    background: url("https://www.nitrotype.com/dist/site/images/backgrounds/bg-noise.png") top left repeat, #2d2e2e;
    border-radius: 5px;
    color: rgba(255, 255, 255, 0.8);
}
.btn--f:hover,
.btn--f:focus {
    color: #fff;
    background-color: #5e4f4f;
}

.achievement.is-complete {
    background: url("https://www.nitrotype.com/dist/site/images/backgrounds/bg-noise.png") top left repeat, linear-gradient(to right, rgba(71, 204, 128, 0.2) 0%, rgba(71, 204, 128, 0.2) 68.5%, rgba(71, 204, 128, 0.5) 68.5%), #292c3b;
    border-color: rgba(89, 255, 160, 0.2);
}

.achievement {
    background: url("https://www.nitrotype.com/dist/site/images/backgrounds/bg-noise.png") top left repeat, linear-gradient(to right, #303343 0%, #303343 68.5%, #3b3d4c 68.5%);
}

.pane.__react_component_tooltip {
    background: url("https://www.nitrotype.com/dist/site/images/backgrounds/bg-noise.png") top left repeat, #2d2e2e;
    background-color: #13141c;
    box-shadow: 0 28px 28px 0 rgba(2, 2, 2, 0.05), 0 17px 17px 0 rgba(2, 2, 2, 0.2), 0 8px 8px 0 rgba(2, 2, 2, 0.15);
    color: #fff;
    opacity: 1;
}

.season--default {
    background: url("https://www.nitrotype.com/dist/site/images/backgrounds/bg-noise.png") top left repeat, url("https://www.nitrotype.com/dist/site/images/pages/leaderboards/season-default-bg.png") 85% 0% no-repeat, linear-gradient(to right, #292c3b, rgba(2, 2, 2, 0.8));
}
.season--default .season-title {
    color: #F9D034;
}
.season--default .season-date {
    border-left-color: #1C99F4;
}
.season--default .season-warn {
    background: rgba(2, 2, 2, 0.3);
}

.comment {
    background: url("https://www.nitrotype.com/dist/site/images/backgrounds/bg-noise.png") top left repeat, #3b2929;
}

.hp-heroBG {
    content: ' ';
    display: block;
    position: absolute;
    top: 0;
    left: -40px;
    background: linear-gradient(to bottom, #7a0e0e00, #c3161600);
    width: 140%;
    height: 125%;
    box-shadow: 0 1px 10px rgba(2, 2, 2, 0.14);
}

.raceChat-pickerToggle {
    color: #f41c1c
}

.raceChat-picker.is-toggled {
    transform: translateY(-7px);
}
.raceChat-picker.is-toggled .raceChat-pickerToggle {
    background: linear-gradient(to bottom, #c31616 30%, #f41c1c 100%);
    border-radius: 0px 0px 50% 50%;
    color: #fff;
    height: 50px;
}
.raceChat-picker.is-toggled .raceChat-pickerToggle:hover {
    background: linear-gradient(to bottom, #c31616 30%, #fa8e8e 100%);
    box-shadow: 0 1px 10px rgba(2, 2, 2, 0.14);
}
.raceChat-picker.is-toggled .raceChat-pickerOptions {
    display: block;
}
.raceChat-picker.is-toggled.raceChat-picker--emoji .raceChat-pickerOptions {
    display: flex;
    flex-wrap: wrap;
    width: 180px;
}
.raceChat-picker.is-toggled.raceChat-picker--emoji .raceChat-pickerOpt {
    flex: 0 0 50%;
    width: 50%;
}

.raceChat-pickerOptions {
    background: linear-gradient(to bottom, #c31616 30%, #f41c1c 100%);
    border-radius: 20px 0px 0px 20px;
    bottom: 50px;
    display: none;
    max-height: 240px;
    overflow-y: auto;
    padding: 15px;
    position: absolute;
    right: 0px;
    width: auto;
    z-index: 2;
}

.raceChat-bubble.raceChat-bubble--pos1 {
    bottom: 205px;
    color: #c31616;
}

.raceChat-bubble.raceChat-bubble--pos2 {
    bottom: 160px;
    color: #ab262e;
}

.raceChat-bubble.raceChat-bubble--pos3 {
    bottom: 115px;
    color: #bd2323;
}

.raceChat-bubble.raceChat-bubble--pos4 {
    bottom: 70px;
    color: #802d2d;
}

.raceChat-bubble.raceChat-bubble--pos5 {
    bottom: 25px;
    color: #412e2e;
}
}

