css:
@-moz-document url-prefix("https://idp.kuleuven.be/") {
header, .footer-wrapper, #small-logo, .alert.alert-info, .word {
    display: none !important;
}

#box-content::before {
    content: 'Login to toledo';
    color: #ffffff;
    margin-left: 15px;
    font-size: 25px;
    font-weight: bold;
}

/* show language select & settings icon */
.hidden-md-up {
    display: block !important;
}

body {
    background: none;
    background-image: url(data:image/png;base64,);
    
}
#box-content {
    background-color: inherit;
}

#box-content input[type="username"], #box-content input[type="password"] {
    background-color: transparent;
    color: #ffffff;
    border: 1px solid grey;
}
#box-content button[type="submit"] {
    background-color: #4f5a7f;
    border: none;
}
#box-content button[type="submit"]:hover {
    background-color: #6979ac;
}
.login-input .fa {
    color: #6979ac;
}
#btnnauthlogin img {
    display: none;
}

#btnnauthlogin {
    background-color: transparent !important;
    color: grey;
    border: none;
}
#box-content .line {
    background: grey;
}
.form-error {
    color: red;
}
}
