css:
@-moz-document domain("remanga.org") {
body {
    background: #151517!important;
    color: #eaeaea;
}
#reader {
    background: rgba(21, 21, 21, 0.5)!important;
    border-left: 1px #797979 solid;
    border-right: 1px #797979 solid;
}
.card {
    background-color: #232529;
    border: 1px solid rgb(121, 121, 121)!important;
}
.card-title {
    /*background-color: rgb(21, 21, 23)!important;*/
    background-color: rgb(35, 37, 41)!important;
    color: #ececec!important;
}
.hr{
    border-top: 1px solid rgb(236, 236, 236);
}
.table{
    color:inherit;
}
tr.not-viewed {
    background-color: #1b1e23!important;
}
a {
    color: #4CAF50;
}
a:hover {
    color: #FF5722;
}
.chapter-title > a {
    color: #c1c1c1;
}
.chapter-title > a:hover {
    color: #3ab74b;
}
.tr {
    color: #c1c1c1;
}
.tr:hover {
    background-color: #34449e!important;
    color:#fff
}
.navbar-nav >.nav-item:hover, .navbar-collapse > .nav-item:hover {
    background-color: #34449e!important;
}
.dropdown-item:focus {
    background:#c1c1c1; /*Цвет нажатой и "оттащиной" вкладки в осн. меню*/
    color:black!important;
}
.btn-dark:hover {
    background-color: #454b50;
}
div[list_type='0'] {
    background-color: rgba(64, 64, 64, 0.8)!important;
}
div[list_type='1'] {
    background-color: rgba(64, 64, 64, 0.8)!important;
}
#show-all-chapters {
    background-color: rgb(21, 21, 23);
}
.btn-outline-dark {
    color: #c1c1c1;
}
.team-chapters .tr {
    color:inherit!important
}
.popover-header {
    background-color: inherit;
}
.popover{
background-color: #32343a;
}
.notif.row.unread {
    background-color: #232529;
}
.modal-content {
    background-color: rgb(35, 37, 41);
}
.close {
    text-shadow: 0px 0px 2px #fff;
}

::-webkit-scrollbar-button { 
/*background-image:url(''); */
background-repeat:no-repeat; 
width:6px; 
height:0px 
} 

::-webkit-scrollbar-track { 
background-color:#32312e; 
box-shadow:0px 0px 3px #000 inset; 
} 

::-webkit-scrollbar-thumb { 
-webkit-border-radius: 5px; 
border-radius: 5px; 
background-color:#c1c1c1; 
box-shadow:0px 1px 1px #fff inset; 
background-image:url('https://c.radikal.ru/c24/1906/6d/1fd97e724f7a.png'); 
background-position:center; 
background-repeat:no-repeat; 
} 

::-webkit-resizer{ 
/*background-image:url('');*/ 
background-repeat:no-repeat; 
width:7px; 
height:0px 
} 

::-webkit-scrollbar{ 
width: 13px; 
}
/*24.07.2019*/
.nowrap {background: #232529;}
th {background: #232529;}
td.field-rus_name {background: #232529;}
td.field-en_name {background: #232529;}
td.field-another_name {background: #232529;}
td.field-uploaded {background: #232529;}
td.field-tome {background: #232529;}
td.field-chapter {background: #232529;}
td.field-name {background: #232529;}
td.field-text {background: #232529;}
td.action-checkbox {background: #232529;}
#changelist .actions {background: #232529;}
table thead th .text a {background: #232529;}
#changelist #toolbar {background: #232529;}
table thead th.sorted .sortoptions {background: #232529;}
thead th {background: #232529;}
.module {background: #232529;}
thead th.sorted .text {background: #232529;}
#changelist .actions select {background: #fff;}
#changelist .paginator {background:inherit}
.submit-row {background: #232529;}
.required label, label.required {color: #a2a2a2;}
label {color: #a2a2a2;}
}
