css:
@-moz-document domain("wanikani.com") {
/* Selection */
::-moz-selection { 
    background-color: #919191;
    color: #171717; }
  
::selection { 
    background-color: #919191;
    color: #171717; }

/* Background colour */
body {
    background-color: #191919;
    background-image: none;
    color: #A9A9A9
}

/* Top bar colour and border */
.global-header {
    background: #141414;
    border-bottom: 1px solid #61616161;;
}

/* Top bar hover - more visible */
.sitemap__section-header:hover, .sitemap__section-header[data-expanded="true"] {
    background-color: rgba(238, 238, 238, .1);
}

.sitemap__page--lessons a:hover, .sitemap__page--reviews a:hover {
    background-color: rgba(238, 238, 238, .1);
}

/* Lessons and Reviews */
.navigation-shortcut a {
    border: none;
    text-shadow: none;
    color: #A9A9A9;    
    height: 100%;
}

.navigation-shortcuts a:hover  {
    background-color: rgba(238, 238, 238, .1);  
}

/* Levels, Radicals, Kanji, Vocabulary, Account colour */
h1, html#public-profile div.wall-of-shame div.title, h2, h3 {
    text-shadow: none;
    color: #B5B5B5
}

/* Dashboard review section colours */
.dashboard section.review-status {
    text-shadow: none;
    color: #B5B5B5;
}

/* Dashboard lines */
.dashboard section.review-status ul li {
    border-right: none;
    border-left: none;
}

.dashboard section.review-status {
    border-bottom: none;
}

hr {
    visibility: hidden;
}

/* Progression Dashboard colour*/
.dashboard section.progression {
    background-color: #141414;
}

/* Progress Plugin colour */
.progress_percentages > div {
    background: #141414 !important;
}

.stage {
    color: #B5B5B5
}

/* Progression Dashboard text colours */
.additional-info h3, .small-caps {
    text-shadow: none;
}

.progression h3 {
    color: #B5B5B5;
}

.dashboard .lattice-single-character div span {
    color: #B5B5B5;
}

/* Progression Dashboard little line */
.dashboard .lattice-single-character {
    background-image: none;
}

/* Progression Dashboard progress bar colour */
.progress {
    background-color: #202020;
}

/* Progression Dashboard bottom shadow */
.dashboard section.progression, .dashboard section.newbie, .dashboard section.upgrade, .dashboard section.system-alert, .dashboard section.alert-where-user-scripts-cant-ignore {
	-webkit-box-shadow: none;
	-moz-box-shadow: none;
	box-shadow: none;
}

/* Search Form colour*/
#search form .search-query {
    background-color: #141414;
    border: 1px solid #616161;
}

/* Search Form icon centering */
#search #main-ico-search {
	position: absolute;
	top: .1em;
	left: .6em;
}

/* Heatmap Plugin colour */
section.heatmaps {
    background-color: #141414;
}

/* Heatmap Plugin text-shadow removal and colour fix*/
div.chart {
    text-shadow: none;
    color: #A9A9A9;
}

.heatmaps .graph-label {
    fill: #A9A9A9 !important;
}

/* New Unlocks, Critical Condition Items, Burned Items colours*/
.dashboard-sub-section h3 {
    background-color: #141414;
}

.additional-info h3, .small-caps {
    color: #A9A9A9;
}

.dashboard section.dashboard-sub-section div.see-more {
	background-color: #141414;;
	-webkit-box-shadow: none;
	-moz-box-shadow: none;
	box-shadow: none;
}

.dashboard section.dashboard-sub-section a.small-caps {
    color: #A9A9A9;
}

.kotoba-table-list table tr.none-available {
    background-color: #202020;
    text-shadow: none;
    color: #696969;
}

.kotoba-table-list table tr.none-available div {
	border: 5px solid #696969;
	-webkit-box-shadow: none;
	-moz-box-shadow: none;
	box-shadow: none;
}

/* New Unlocks and Critical Condition Items gradient removal */
ul.single-character-grid li[class|="vocabulary"], ul.single-character-grid tr[class|="vocabulary"], ul.multi-character-grid li[class|="vocabulary"], ul.multi-character-grid tr[class|="vocabulary"], .kotoba-table-list li[class|="vocabulary"], .kotoba-table-list tr[class|="vocabulary"] {
	background-image: none;

}

/* Burned Items gradient removal */
ul.single-character-grid li[class|="radical"], ul.single-character-grid tr[class|="radical"], ul.multi-character-grid li[class|="radical"], ul.multi-character-grid tr[class|="radical"], .kotoba-table-list li[class|="radical"], .kotoba-table-list tr[class|="radical"] {
	background-image: none;
}

/* Recent Community Topics colours */
.small-caps.invert {
	background-color: #141414;
    background-image: none;
}

.forum-topics-list table tbody tr {
    background-color: #202020;
}

/* Recent Community icon */

.dashboard h3.small-caps.invert div.heading-symbol i::before {
    color: #61616161;
    text-shadow: none;
}

.dashboard h3.small-caps.invert div.heading-symbol {
    border: 5px solid #616161;
}

.dashboard div.heading-symbol {
	-webkit-box-shadow: none;
	-moz-box-shadow: none;
	box-shadow: none;
} 

/* Recent Community Topics text shadow removal and colour fix */
.forum-topics-list table th, .forum-topics-list table td {
    text-shadow: none;
}

.forum-topics-list a.topic-title, .forum-topics-list [class*="icon-"]::before {
    color: #A9A9A9;
}

.forum-topics-list table tbody tr {
	background-color: #202020;
	-webkit-transition: background-color ease-in 0.3s;
	-moz-transition: background-color ease-in 0.3s;
	-o-transition: background-color ease-in 0.3s;
	transition: background-color ease-in-out 0.3s;
}

.forum-topics-list table tbody tr:hover {
	background-color: #181818;
}

.forum-topics-list a {
    color: #595959;
}

.forum-topics-list a:hover {
    color: #A9A9A9;
}

/* Removal of the line above Visit The Community Center... */
.forum-topics-list a.small-caps {
    background-image: none;
}

/* Removal of the box shadows in the Lesson Summary */
#lessons-summary #radicals, #lessons-summary #kanji, #lessons-summary #vocabulary {
	-webkit-box-shadow: none;
	-moz-box-shadow: none;
	box-shadow: none;
}

/* Lessons Summary Radicals invertion */
#lessons-summary #radicals h2 {
    background-color: #141414;
    color: #0af;
}

/* This fixes the radical when it's an image instead of a letter */
#question #character img {
	filter: invert(61%) sepia(100%) saturate(3841%) hue-rotate(171deg) brightness(100%) contrast(108%);
}

/* Lessons Summary Kanji invertion */
#lessons-summary #kanji h2 {
	background-color: #141414;
    color: #f0a;
}

/* Lessons Summary Vocabulary invertion */
#lessons-summary #vocabulary h2 {
	background-color: #141414;
    color: #a0f;
}

/* The Lesson Summary section that shows you the radicals/kanji/vocab in the session */
#lessons-summary #radicals, #lessons-summary #kanji, #lessons-summary #vocabulary {
    background-color: #171717;
}

/* Removal of the white box shadow in the thingy from right above */
#lessons-summary #radicals > div > ul > li, #lessons-summary #kanji > div > ul > li, #lessons-summary #vocabulary > div > ul > li {
	-webkit-box-shadow: none;
	-moz-box-shadow: none;
	box-shadow: none;
}

/* Same thing but for the Wanikani square on top right */
#lessons-summary header nav #back-dashboard {
	-webkit-box-shadow: none;
	-moz-box-shadow: none;
	box-shadow: none;
}

/* Removal of the box shadows in the Reviews Summary and also changing the colour*/
#reviews-summary #review-stats [id*="review-stats-"] {
    background-color: #171717;
	-webkit-box-shadow: none;
	-moz-box-shadow: none;
	box-shadow: none;
}

#reviews-summary #incorrect, #reviews-summary #correct {
	background-color: #171717;
	-webkit-box-shadow: none;
	-moz-box-shadow: none;
	box-shadow: none;
}

/* Reviews Summary percentage colour */
#reviews-summary #review-stats [class*="pure-u-"]:first-child [id*="review-stats-"] {
    background-color: #101010;
}

#reviews-summary #review-stats [class*="pure-u-"]:first-child [id*="review-stats-"]::after {
	border-color: transparent transparent transparent #101010;
}

/* Reviews Summary Inccorect invertion */
#reviews-summary #incorrect h2 {
	background-color: #141414;
    color: #f03;
}

/* Reviews Summary Correct invertion */
#reviews-summary #correct h2 {
    background-color: #141414;
    color: #88cc00;
}

/* Fixing the ugly background of the little headers above the incorrect or correct sections */
#reviews-summary #incorrect .apprentice h3 span, #reviews-summary #incorrect .guru h3 span, #reviews-summary #incorrect .master h3 span, #reviews-summary #incorrect .enlightened h3 span, #reviews-summary #incorrect .burned h3 span, #reviews-summary #correct .apprentice h3 span, #reviews-summary #correct .guru h3 span, #reviews-summary #correct .master h3 span, #reviews-summary #correct .enlightened h3 span, #reviews-summary #correct .burned h3 span {
	background-color: #171717;
}

/* The box behind numbers too */
#reviews-summary #incorrect .apprentice h3 strong, #reviews-summary #incorrect .guru h3 strong, #reviews-summary #incorrect .master h3 strong, #reviews-summary #incorrect .enlightened h3 strong, #reviews-summary #incorrect .burned h3 strong, #reviews-summary #correct .apprentice h3 strong, #reviews-summary #correct .guru h3 strong, #reviews-summary #correct .master h3 strong, #reviews-summary #correct .enlightened h3 strong, #reviews-summary #correct .burned h3 strong {
    background-color: #171717;
    color: #A9A9A9;
}

/* The lines in the boxes, too */
#reviews-summary #incorrect .apprentice h3, #reviews-summary #incorrect .guru h3, #reviews-summary #incorrect .master h3, #reviews-summary #incorrect .enlightened h3, #reviews-summary #incorrect .burned h3, #reviews-summary #correct .apprentice h3, #reviews-summary #correct .guru h3, #reviews-summary #correct .master h3, #reviews-summary #correct .enlightened h3, #reviews-summary #correct .burned h3 {
    border-bottom: none;
}

/* Again removing the white box shadow in the radical/kanji/vocab thingies in the reviews summary section */
#reviews-summary #incorrect .apprentice > ul > li, #reviews-summary #incorrect .guru > ul > li, #reviews-summary #incorrect .master > ul > li, #reviews-summary #incorrect .enlightened > ul > li, #reviews-summary #incorrect .burned > ul > li, #reviews-summary #correct .apprentice > ul > li, #reviews-summary #correct .guru > ul > li, #reviews-summary #correct .master > ul > li, #reviews-summary #correct .enlightened > ul > li, #reviews-summary #correct .burned > ul > li {
	-webkit-box-shadow: none;
	-moz-box-shadow: none;
	box-shadow: none;
}

/* Same thing but for the Wanikani square on top right */
#reviews-summary header nav #back-dashboard {
	-webkit-box-shadow: none;
	-moz-box-shadow: none;
	box-shadow: none;
}

/* Loading colour */
#loading {
    background-color: #191919;
}

#loading-screen {
    background-color: #191919;
}

/* Response Form colour and box-shadow */
#answer-form input[type="text"] {
    background-color: #121212;
	-moz-box-shadow: none;
	box-shadow: none;
}

/* The Answer button */
#answer-form button {
    background-color: #121212;
}

/* Meaning questions */
#question #question-type.meaning {
    border-top: 1px solid #101010;
    border-bottom: 1px solid #101010;
	color: #A9A9A9;
	background-color: #101010;
	background-image: none;
}

/* Reading questions */
#question #question-type.reading {
	color: #fff;
	text-shadow: -1px -1px 0 #282828;
	border-top: 1px solid #282828;
	border-bottom: 1px solid #282828;
	background-color: #282828;
	background-image: none;
}

/* Reverting the colours of the vocabulary in reviews/lessons - inspired by WaniKani Breeze Dark */
#question #character.vocabulary {
	background-color: #141414;
	background-image: none;
	text-shadow: 5px 5px 0 #080808;
	color: #a100f1 !important;
}

/* Reverting the colours of the kanji in reviews/lessons */
#question #character.kanji {
	background-color: #141414;
	background-image: none;
	text-shadow: 5px 5px 0 #080808;
    color: #f100a1;
}

/* Reverting the colours of the radicals in reviews/lessons */
#question #character.radical {
	background-color: #141414;
	background-image: none;
	text-shadow: 5px 5px 0 #080808;
    color: #00a1f1;
}

/* Previous vocabulary colour */
.wki_cinza {
    background-color: #141414;
}

.wki_cinza:hover {
    background-color: #101010;
}

/* The class is called information but really I only picked it after seeing the Kana Chart is white sooooo I don't know what else it changes */
#information {
	background-color: #141414;
	-webkit-box-shadow: none;
	-moz-box-shadow: none;
	box-shadow: none;
	z-index: 10;
}

/*Kana Chart colours and borders */
#kana-chart ol li.active {
    color: #bbbbbb;
    border-bottom: none;
}

#kana-chart ol {
	border-bottom: 3px solid #61616161;
}

#kana-chart ul li span {
    color: #bbbbbb;
}

#kana-chart ul li {
	border-right: 3px solid #61616161;
}

#additional-content ul li.active i {
    color: #A9A9A9A9;
}

/* Fixing the white info button in the "Previous <thingy> you answered" */
#wki_mimic_button {
    background-color: #141414 !important;
}

/* Inside the thing above */
.qtip-bootstrap {
    background-color: #191919;
    color: #A9A9A9;
}

.qtip-bootstrap .qtip-titlebar {
    background-color: #19191919;
    border-bottom: 1px solid #191919;
}

/*Fixing the ugly glow in that same thing */
[class*="-icon"] {
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    box-shadow: none;
}

/* And the text-shadow */
p, ol, figcaption, .body-copy, .body-copy li {
    text-shadow: none;
}

[class^="note-"] {
    text-shadow: none;
}

/* Vocabulary highlight */
.highlight-vocabulary {
    background-color: #a100f1;
    color: #EEE;
}

/* Kanji highlight */
.highlight-kanji {
    background-color: #f100a1;
    color: #EEE;
}

#item-info [class*="highlight-"] {
    text-shadow: none;
}

/* Radical highlight */
.highlight-radical {
    background-color: #0af;
    color: #EEE;
}

/* +Add Synonym */
.user-synonyms ul li.user-synonyms-add-btn::before {
    background-color: #303030;
    color: #A9A9A9;
}

/* Removal of box shadow in the Related Kanji thingy and fixing it's color */
#item-info #related-items ul span {
	-webkit-box-shadow: none;
	-moz-box-shadow: none;
	box-shadow: none;
}

#item-info #related-items a, #item-info #related-items a:active, #item-info #related-items a:hover, #item-info #related-items a:visited {
    color: #A9A9A9;
}

/* Show All Information colour */
#item-info #all-info {
    background-color: #141414;
}

#item-info #all-info:hover {
    color: #bbbbbb;
}

/* Item Info borders */
#item-info h2 {
    border-bottom: 1px solid #61616161;
}

/* Again, this is generally just fieldset, but I changed it for Meaning Note so I'm not sure if it changes anything else */
fieldset {
    background-color: #181818 !important;
}

/* Hint */
#item-info blockquote {
	margin: 1em 0 0;
	padding: 10px;
	background-color: #181818;
	color: #888888;
}

/* Lesson breakdown, meaning and reading colours */
#lesson #supplement-info {
	background-color: #141414;
	-webkit-box-shadow: none;
	-moz-box-shadow: none;
	box-shadow: none;
}

#lesson #supplement-nav ul li.active::before {
	border-color: transparent transparent #171717 transparent;
}

/* Lesson borders */
#lesson #supplement-info h2 {
    border-bottom: 1px solid #61616161;
}

/* Removing box shadow from the little squares... again */
#lesson #supplement-info .radical, #lesson #supplement-info .kanji, #lesson #supplement-info .vocabulary {
	-webkit-box-shadow: none;
	-moz-box-shadow: none;
	box-shadow: none;
}

/* And changing the colour */
#lesson #supplement-info #supplement-rad-related-kanji ul li a, #lesson #supplement-info #supplement-kan-breakdown ul li a, #lesson #supplement-info #supplement-kan-related-vocabulary ul li a, #lesson #supplement-info #supplement-voc-breakdown ul li a {
    color: #A9A9A9;
}

/* Squares at the bottom */
#lesson #batch-items ul li span {
	-webkit-box-shadow: none;
	-moz-box-shadow: none;
	box-shadow: none;
}

#lesson #batch-items ul li[data-index="quiz"] span {
    background-color: #303030;
}

/* Vocabulary Lesson colour invertion */
header #main-info.vocabulary {
	background-color: #141414;
	background-image: none;
    color: #a0f;
}

header #main-info.kanji {
    background-color: #141414;
	background-image: none;
    color: #f0a;
}

header #main-info.radical {
    background-color: #141414;
	background-image: none;
    color: #0af;
}

header #main-info #character img {
    filter: invert(61%) sepia(100%) saturate(3841%) hue-rotate(171deg) brightness(100%) contrast(108%);
}

header #main-info.vocabulary #character {
	text-shadow: 5px 5px 0 #080808;
}

header #main-info.vocabulary #meaning {
	text-shadow: 3px 3px 0 #080808;
}

header #main-info.kanji #character {
	text-shadow: 5px 5px 0 #080808;
}

header #main-info.kanji #meaning {
	text-shadow: 3px 3px 0 #080808;
}

header #main-info.radical #character {
	text-shadow: 5px 5px 0 #080808;
}

header #main-info.radical #meaning {
	text-shadow: 3px 3px 0 #080808;
}

/* Start Lesson header in Lesson Summary */
    /* Border colour */
#lessons-summary header nav {
    border-bottom: 1px solid #616161;
}

/* Lessons navigation */
#lesson #supplement-nav {
    background-color: #101010;
}

/* Lesson Hint */
#lesson #supplement-info blockquote {
    background-color: #181818;
}

    /* Start Session colour */
#lessons-summary header nav #start-session a {
    background-color: #000;
    color: #0af;
}

#reviews-summary header nav #start-session a.disabled {
    background-color: #141414;
}

    /* And on hover */
#lessons-summary header nav #start-session a:hover {
    background-color: #141414;
}

/* Start Lesson header in Reviews Summary */
    /* Border colour */
#reviews-summary header nav {
    border-bottom: 1px solid #616161;
}

    /* Start Session colour */
#reviews-summary header nav #start-session a {
    background-color: #000;
    color: #0af;
}

    /* And on hover */
#reviews-summary header nav #start-session a:hover {
    background-color: #141414;
}

/* Lesson Reviews */
    /* Reading question */
#quiz #question-type.reading {
	color: #fff;
	text-shadow: -1px -1px 0 #282828;
	border-top: 1px solid #282828;
	border-bottom: 1px solid #282828;
	background-color: #282828;
	background-image: none;
}

    /* Name (meaning) question */
#quiz #question-type.meaning {
	color: #A9A9A9;
	border-top: 1px solid #101010;
	border-bottom: 1px solid #101010;
	background-color: #101010;
	background-image: none;
}

    /* Text shadow fix */
header.quiz #main-info.vocabulary #character {
    text-shadow: 5px 5px 0 #080808;
}

/* Settings */
    /* Background colour */
.settings-section {
    background-color: #181818;
    box-shadow: none;
}
    /*Navigation colour */
.page-list {
    background-color: #141414;
}

.page-list ul > li > a, .page-list ul > li > span {
    background-color: #141414;
    border: 1px solid #141414;
    text-shadow: none;
    color: #A9A9A9;
}

.page-list ul > li.active a {
    background-color: #101010;
}

.page-list ul > li > a:hover {
    background-color: #080808;
}

    /* Forms (like new username) */
textarea, input[type="text"], input[type="password"], input[type="datetime"], input[type="datetime-local"], input[type="date"], input[type="month"], input[type="time"], input[type="week"], input[type="number"], input[type="email"], input[type="url"], input[type="search"], input[type="tel"], input[type="color"], .uneditable-input {
	background-color: #141414;
	border: 1px solid #616161;
    color: #999;
    filter: none;
}

    /* Buttons (like update username) */
.btn {
	color: #999;
	text-shadow: none;
	background-color: #101010;
	background-image: none;
	border-color: #616161 #616161 #616161;
	filter: none;
	border: 1px solid #616161;
	border-bottom-color: #616161;
	box-shadow: none;
}

.btn:hover {
    background-color: #171717;
    color: #919191;
}

    /* Select gorms (like the "Receive WaniKani related emails") */
select {
    background-color: #141414;
}

    /* Token code (and any other code, if there's any on the site?) */
code {
    background-color: #141414;
    border: 1px solid #616161;
}

    /* Tools - Form TFG42 */
.table-striped tbody > tr:nth-child(2n+1) > td, .table-striped tbody > tr:nth-child(2n+1) > th {
	background-color: #171717;
}

.table-bordered th, .table-bordered td {
	border-left: 1px solid #616161;
}

.table th, .table td {
    border-top: 1px solid #616161;
}

.table-bordered {
    border-right: 1px solid #616161;
    border-top: 1px solid #616161;
    border-bottom: 1px solid #616161;
}

/* Profile */
    /* Wall of Shame background*/
html#public-profile div.wall-of-shame {
    background-color: #191919;
}

    /* "Wall of Shame" label */
html#public-profile div.wall-of-shame h3 span {
	background-color: #141414;
	color: #eee;
	text-shadow: none;
	box-shadow: none;
}

    /* Wall of shame text-shadow removal */
html#public-profile div.wall-of-shame ul li > span:first-child {
    text-shadow: none;
}

    /* Header with the "about" info and "crabinator apprentice" info */
html#public-profile .public-profile-header div.user-info {
	background-color: #141414;
	background-image: none;
	box-shadow: none;
    border-bottom: 1px solid #313131;
}

    /* Progress bars (affects other things) */
.progress .bar {
	background-color: #101010;
	background-image: none;
}

.progress {
    background-color: #202020;
}    

        /* Wall of Shame progress background bar */
html#public-profile div.wall-of-shame .progress {
	background-color: #151515;
	background-image: none;
}

/* Subscription page */
    /* Select a plan background */
