css:
@-moz-document domain(sugarondemand.com)
{
.record-panel-header, .panel-active, .a11y-wrapper {
    background-color: #ddddff !important   
}
.record-panel-content {
    background-color: #fff
}
.sorting, th.sorting_asc, th.sorting_desc, [data-fieldname="description"]  {
    background-color: #ddd !important
}
.record-cell .record-label {
    color: black;
    background-color: #dfdfdf;
}
.reportDataChildtablelistView td {
        width: auto;
}
#reportDetailsTable {
    display: none;
}
.dashlet-content td {
    width: auto
}
.dashlet-content > div {
    max-height: initial !important;
}
.dashlet .webpage {
    display: initial;
}
.moduleTitle {
	display: none;
}

[action="index.php?action=ReportCriteriaResults&module=Reports&page=report&id=10f5649c-a8d8-11e9-b65c-02dfd714a754"] [title="Select"] {
    display: none;
}
[action="index.php?action=ReportCriteriaResults&module=Reports&page=report&id=10f5649c-a8d8-11e9-b65c-02dfd714a754"] #filters_tab > div {
    display: none;
}
[action="index.php?action=ReportCriteriaResults&module=Reports&page=report&id=10f5649c-a8d8-11e9-b65c-02dfd714a754"] #filters_tab {
	    display: flex;
}
[action="index.php?action=ReportCriteriaResults&module=Reports&page=report&id=10f5649c-a8d8-11e9-b65c-02dfd714a754"] #filters_tab > table:last-child tr:first-child {
	display: none;
}
[action="index.php?action=ReportCriteriaResults&module=Reports&page=report&id=10f5649c-a8d8-11e9-b65c-02dfd714a754"] > table:nth-of-type(2), #showHideReportDetails {
			display: none;
}
#filters_tab {
	margin-bottom: 0;
}
#report_results > table:nth-last-of-type(2) {
	display: none !important;
}
.megamenu .active > .btn-group {
	background-color: #d5c5e5 !important
}
}
