css:
@-moz-document domain(alchemygame.ru)
{
div #click {
    background: #1d1d24;
}
#new_init {
    background-color: #2f2f2f;
    width: 100px;
}
#new_static {
    background-color: #2f2f2f;
    width: 100px;
}
.container {
    background: #2f2f2f;
}
a {
    color: #9ced9c;
}
.news_read {
    color: white;
}
body {
    color: #b0b0b0;
}
.green_button {
    background: #484848;
}
#controls {
    background: #141419;
}
#footer {
    background: #141419;
}
#tools {
    background: #141419;
}
.reactor_hints {
    color: #dfdfdf;
}
#reagents_under {
    background: #383838;
}
#results_under {
    background: #383838;
}
body {
    background: #1d1d24;
}
#prop_msg {
    width: 100%;
    height: 100px;
    color: #fdfdfd;
}
#reactions_list {
    background: #383838;
}
.reaction-string {
    background-color: #383838 !important;
    border-bottom: 2px solid #1d1d24;
    padding: 2px;
}
a {
    color: #fdfdfd;
}
#controls {
    background: #141419;
}
#reactionsFilterContainer {
    background: #1d1d24 !important;
    border-bottom: 2px solid #51a051 !important;
}
#inits {
    border: 2px solid #51a051;
}
#statics {
    border: 2px solid #51a051;
}
#welcome {
    background: #1d1d24;
}
.link {
    color: #9ced9c !important;
}
.ui-dialog-titlebar {
    background: #141419 !important;
}
#uploaded_pictures {
    background: #1d1d24;
}
#settings {
    background: #1d1d24;
}
#save_dialog {
    background: #1d1d24;
}
#in_development_desc {
    color: #c4c4c4;
}
#description_desc {
    color: #c4c4c4;
}
#welcome_dialog {
    background: #1d1d24;
    color: white;
}
#recipe_list {
    background: #1d1d24;
}
#help {
    background: #1d1d24;
}
#element_hint {
    background: #1d1d24;
}
#mod_action {
    background-color: #1d1d24;
    color: #fdfdfd;
    border: 0;
    width: 140px;
}
input {
    -webkit-appearance: textfield;
    background-color: #1d1d24;
    color: #fdfdfd;
    -webkit-rtl-ordering: logical;
    cursor: text;
    padding: 1px;
    border-width: 2px;
    border-style: inset;
    border-color: initial;
    border-image: initial;
}
#id_title {
    height: 30px;
    font-size: 25px;
    width: 340;
    color: #fdfdfd
}
#id_description {
    height: 100px;
    width: 340px;
    color: #fdfdfd
}
#id_json_data {
    width: 600px;
    height: 400px;
    color: #fdfdfd
}
#statics {
    border: 2px solid #1d1d24;
}
#inits {
    border: 2px solid #1d1d24;
}
#reactionsFilterContainer {
    background: #1d1d24 !important;
    border-bottom: 2px solid #1d1d24 !important;
}
textarea {
    -webkit-appearance: textarea;
    background-color: #1d1d24;
    -webkit-rtl-ordering: logical;
    flex-direction: column;
    cursor: text;
    white-space: pre-wrap;
    overflow-wrap: break-word;
    border-width: 1px;
    border-style: solid;
    border-color: initial;
    border-image: initial;
    padding: 2px;
}
.ui-dialog .ui-dialog-content {
    border: 0;
    padding: .5em 1em;
    overflow: auto;
    zoom: 1;
    text-align: justify;
    background-color: #1d1d24;
}
.file_upload {
    position: relative;
    overflow: hidden;
    direction: ltr;
    cursor: pointer;
    text-align: center;
    color: #333;
    font-weight: 700;
    -moz-border-radius: 10px;
    -webkit-border-radius: 10px;
    border-radius: 10px;
    width: 100%;
    height: 30px;
    line-height: 30px;
    background: #eee;
    border: 1px solid #1d1d24;
}
#order {
    padding: 0;
    border: 1px solid #141419;
    background-color: #141419;
    color: #fdfdfd
}
.questbox {
    color: #fdfdfd;
}
div .container td {
    background: #1d1d24;
}
.addthis_toolbox {
    background: #383838;
}
}
