css:
@-moz-document domain("aerogligli.fr") {
/* <global> */
 body {
    background: #222;
    color: #3498db
}
a {
    cursor: pointer
}
#page {
    background: #333;
    border: 1px solid #444
}
/*
</global>
<footer>
*/
 footer {
    background: #444
}
footer a {
    color: #3498db
}
footer a:hover {
    text-decoration: underline;
}
/*
</footer>
<menu>
*/
 #main-menu ul li ._s_menu ul {
    background: #444
}
#main-menu ul li ._s_menu ul > li > a:hover {
    background: #333
}
#user-menu a {
    border: 1px solid #f39c12;
    background: #333;
    color: #f39c12;
    text-shadow: none;
    transition: all .2s
}
#user-menu a:hover {
    background: #f39c12;
    color: white
}
#right-menu a.link4 {
    border: 1px solid #ec8d38;
    background: #333;
    color: #ec8d38;
    text-shadow: none;
    transition: all .2s
}
#right-menu a.link4:hover {
    background: #ec8d38;
    color: white
}
#right-menu a.link3 {
    border: 1px solid #3498db;
    background: #333;
    color: #3498db;
    text-shadow: none;
    transition: all .2s
}
#right-menu a.link3:hover {
    background: #3498db;
    color: white
}
#right-menu a.link2 {
    border: 1px solid #1abc9c;
    background: #333;
    color: #1abc9c;
    text-shadow: none;
    transition: all .2s
}
#right-menu a.link2:hover {
    background: #1abc9c;
    color: white
}
#right-menu a.link1 {
    border: 1px solid #27ae60;
    background: #333;
    color: #27ae60;
    text-shadow: none;
    transition: all .2s
}
#right-menu a.link1:hover {
    background: #27ae60;
    color: white
}
/*
</menu>
<home page elements>
*/
 #home-public>div>div>a {
    background: #333;
    transition: all .2s
}
#home-public>div>div>a:hover {
    background: #3a81bf;
    color: #f2f2f2
}
.flexslider {
    border: 1px solid #444
}
._ulm_promo, #home-events .event {
    background: #444;
    border: 1px solid #444;
    color: #f2f2f2
}
._important {
    color: #eb2f06 !important
}
#home-events h2 {
    color: #e0e0e0
}
#home-events .event-content .content {
    color: #f2f2f2
}
#home-events .event-view-all:hover {
    text-decoration: underline
}
/*
</home page elements>
<login page elements>
*/
 .preview {
    background: #444;
    border: none;
    color: #f2f2f2
}
label {
    color: #f2f2f2
}
input, textarea {
    background: #444;
    border: 1px solid #444;
    padding: 10px 15px!important;
    border-radius: 4px;
    color: white
}
input[type="submit"], .submit {
    background: #444;
    color: #f2f2f2;
    padding: 10px 25px!important
}
/*
</login page elements>
<profile page elements>
*/
 .user-abo-home {
    background: #444;
    color: #f2f2f2
}
.user-abo-home td, .user-abo-home th {
    border: 1px solid #555
}
.jq-tooltip, .jq-tooltip-arrow:after {
    background: #444;
    color: #f2f2f2
}
.user-progression img {
    filter: invert(.8)
}
/*
</profile page elements>
<courses pages elements>
*/
 table.footable td, table.footable th {
    border: 1px solid #444!important;
    color: #f2f2f2
}
table.footable img {
    filter: invert(.8)
}
.block-group.ues article {
    background: #444;
    color: #f2f2f2
}
.block-group.ues .block {
    background: #444!important
}
.fil-arianne {
    color: #f2f2f2
}
.flexslider {
    background: #444;
    box-shadow: none
}
.flexslider .icon-resize-full-alt {
    color: #999!important
}
.qcms-next-ue {
    color: #f2f2f2
}
.qcm {
    border: 1px solid #444
}
.qcm .clearfix {
    background: #444!important;
    color: #f2f2f2;
    border-bottom: none!important
}
.qcm .explications, .qcm .options {
    background: #333;
    color: #f2f2f2;
    border: 1px solid #444
}
.qcm .options .clearfix {
    background: #333!important
}
.qcm .commentaires {
    background: #333;
    border: 1px solid #444
}
.qcms-options .clearfix {
    background: #333!important
}
.qcms-resultats, .qcms-options {
    border: none;
    background: #444;
    color: #f2f2f2
}
.qcms-resultats .score {
    background: #444
}
.qcms-options img {
    filter: invert(.7)
}
.block-group.afdp67 .block {
    border: 1px solid #444
}
.block-group.afdp67 img {
    filter: invert(.8)
}
/*
</courses pages elements>
*/
}
