css:
@-moz-document domain("vocaroo.com") {
/* @import url('https://fonts.googleapis.com/css?family=Roboto+Mono'); */
body, html {
    background: #202020 !important;
    font-family: 'Roboto Mono', 'Lucida Console', monospace !important;
    padding-top:0.5%;
    padding-bottom:2%;
    color: #cacaca !important;
    font-size: 9pt;
    text-align: center;
}
a {
    color:#dbc4de !important;
}
#idTitleImage {
    visibility: hidden !important;
}
#idMascotBox {
    content: url("https://puu.sh/DF6r5/9f822707a4.png");
    max-width: 200px;
    margin-left:auto;
    /* margin-right:40%; */
}
.uploadWaiterImg, #idUploadWaiter, #idProcessingWaiter {
    content:url(http://puu.sh/DFcLo/06e7112e87.gif) !important; 
    margin-left: auto;
    margin-right:auto;
    /* maybe ill fix this dw */
}
#idErrorBox {
    text-align:center;
    padding-top:15px; 
}
.addthis_button_facebook, .addthis_button_preferred_1 at300b, .addthis_button_twitter, .addthis_button_preferred_2 at300b, .addthis_button_google_plusone_share, .addthis_button_preferred_3, .addthis_button_gmail, .addthis_button_preferred_4 at300b, .addthis_button_mailto, .addthis_button_preferred_5 at300b, .addthis_button_compact at300m {
    margin:0 5px 0 0 !important;
}
#at15s, .atm-i, #at_hover.atm-s a, .atm-s a, .atm-f {
    background:#212121 !important;
    border:none;
    font-family: 'Roboto Mono', 'Lucida Console', monospace !important;
    color:#acacac !important;
    padding-top:5px;
    padding-left:6px;
}
#at-expanded-menu-host .at-expanded-menu-mask {
    background-color:#151515de !important;
}
#at-expanded-menu-title, .at-expanded-menu-page-title {
    text-transform:lowercase;
    font-family: 'Roboto Mono', 'Lucida Console', monospace !important;
    color:#acacac !important; 
}
#at-expanded-menu-host .at-expanded-menu-page-url {
    font-family: 'Roboto Mono', 'Lucida Console', monospace !important;
    color: #dbc4de !important;
}
.at-branding-logo {
    display:none;
    padding:0;
}
.at-expanded-menu-search-input[type="text"], at-expanded-menu-search-label-content {
    background-color:#0000 !important;
    font-family: 'Roboto Mono', 'Lucida Console', monospace !important;
    color:#acacac
}
#at-expanded-menu-host #at-expanded-menu-title, #at-expanded-menu-host .at-branding-logo, #at-expanded-menu-host .at-copy-link-result-message span, #at-expanded-menu-host .at-copy-link-share-page-url, #at-expanded-menu-host .at-expanded-menu, #at-expanded-menu-host .at-expanded-menu-button-label, #at-expanded-menu-host .at-expanded-menu-email-disclaimer, #at-expanded-menu-host .at-expanded-menu-load-btn, #at-expanded-menu-host .at-expanded-menu-page-title, #at-expanded-menu-host .at-expanded-menu-page-url, #at-expanded-menu-host .at-expanded-menu-privacy-link, #at-expanded-menu-host .at-expanded-menu-search-label-content, #at-expanded-menu-host .at-expanded-menu-top-services-header {
    font-family: 'Roboto Mono', 'Lucida Console', monospace !important;
    color:#acacac;
}
#at-expanded-menu-host .at-expanded-menu-close, #at-expanded-menu-load-btn, .at-expanded-menu-load {
    border-radius:0px !important;
    background-color:#212121 !important;
    color:#acacac !important;
    transition:none !important;
}
.at-expanded-menu-close:hover, #at-expanded-menu-load-btn:hover, .at-expanded-menu-load:hover {
    background-color:#262626 !important;
    color:#acacac !important;
}
#at-expanded-menu-host .at-expanded-menu-service-list li {
    width: 70px;
    min-width: 70px;
}
.at-expanded-menu-fade {
    display:none !important;
}
#idDeleteConfirmBox {
    background-color:#191919 !important;
    opacity:1 !important;
    text-align:center;
    padding-top:15px !important;
    padding-bottom:10px !important;
}
#idDeleteSuccessBox {
    width:450px !important;
}
#idErrorBoxText {
    padding-bottom:12px !important;
}
.standardBox, .playerBox {
    /* text-align: center; */
    background-color: #191919;
    margin-top: 1px;
    margin-left: auto;
    margin-right: auto;
    width: 500px;
    padding-left: 10px;
    padding-right: 10px;
    padding-top: 5px;
    padding-bottom: 5px;
    border-radius: 0px;
    box-shadow: none;
}
h1, h2 {
    color:#dbc4de;
}
textarea {
    background-color: #212121;
    font-family: 'Roboto Mono', 'Lucida Console', monospace !important;
}
.playerBoxHTML5, audio {
    width: 500px !important;
    padding: 0 !important;
    margin: 20px auto 20px auto !important;
    
}
.shareVocarooLinkBox, input[type=text] {
    padding-left: 8px;
    padding-right: 8px;
    border-radius: 0px;
    width: 300px;
    margin-left: 0px;
    margin-right: 7px;
    font-size: 10pt;
    border: none;
    color: #acacac;
    background-color: #262626 !important;
    font-family: 'Roboto Mono', 'Lucida Console', monospace !important;
}
.shareHorizontalSeparator {
    width: 0%;
    margin-top: 10px;
    margin-bottom: 10px;
    background-color: #212121;
}
.shareSmallButton {
    width: 70px;
    font-family: 'Roboto Mono', monospace;
    background-color: #313131;
    color:#acacac;
    border-radius:0;
    box-shadow:none;
    padding-top:6px;
    padding-bottom:7px;
    margin: 0 4px 0 4px;
}
.shareSmallButton:hover {
    background-color:#313131cc;
}
form[method=post] {
    border-top:2px solid #191919;
    padding-top:10px;
}
input[type=submit] {
    width: 100px;
    font-family: 'Roboto Mono', 'Lucida Console', monospace !important;
    background: #313131 !important;
    color:#acacac;
    border-radius:0;
    box-shadow:none;
    padding-top:6px;
    padding-bottom:6px;
}
.uploadButton {
    background:#262626;
    color:#acacac;
    border-radius:0;
    box-shadow:none;
    padding: 10px 20px 10px 20px;
    cursor:pointer !important;
}
/* disqus */
.commentsBox {
    background: #191919 !important;
    width:500px;
    margin-top: 15px;
    border-radius: 0px;
    box-shadow: none;
    font-family: 'Roboto Mono', 'Lucida Console', monospace; /* ok */
}
/* hide ads */
#google_ads_frame1, #google_ads_frame2, #google_ads_frame3, #aswift_2_expand {
    display:none !important;
    max-width: 0px !important;
    max-height: 0px !important;
}
}
