css:
@-moz-document regexp(".*aliexpress\\.com\\/.*&SearchText=.*") {
body {
    padding-top: 7.2em;
}

#narrow-form {
    position: fixed;
    top: 0;
    left: 0;
    padding: 0 1em;
    z-index: 999;
    width: 100vw;
    height: 3.5em;
    background: #e2e2e2;
}

#view-filter {
    position: fixed;
    top: 3.5em;
    left: 0;
    padding: .6em 1em;
    z-index: 998;
    width: 100vw;
    height: 3.6em;
    background: #f2f2f2;
    box-shadow: 0 0 .3em #222;
}

#pagination-bottom {
    position: fixed;
    top: 0;
    right: 1em;
    padding: .6em 0;
    z-index: 999;
    width: 46em;
    margin: 0;
    background: none;
}
#pagination-bottom .ui-pagination-navi {
    margin: 0;
    float: right;
}
#pagination-bottom .ui-pagination-navi a {
    border-radius: 3px;
    border-color: #ccc;
}
#pagination-bottom .ui-pagination-goto {
    padding-left: inherit;
    padding-right: 3em;
}

#bp-banner-new,
#p4p-old-style,
.product-fix-sns {
    display: none !important;
}
}

@-moz-document regexp(".*aliexpress\\.com\\/item\\/.*") {
.store-header,
.product-fix-sns div > div:first-child,
.ui-fixed-panel-collapse,
.ui-fixed-panel-expand,
.ui-mask,
.ui-newuser-layer-dialog {
    display: none !important;
}
}
