css:
@-moz-document url-prefix("https://mail.azet.sk/") {
#pata {
    background-color:#1B1B1B;
}
h1, .ax, .h1, #podklad #obsah h1.h1 {
    color:#ffffff;
    background-color:#1B1B1B;
}
.ax.aktualna, .settings-text, .nastavenia form div input, .nastavenia form .textarea textarea, #menu .box .priecinky .ponuka li a span.priecinok:hover, #obsah .vyhladavanie input {
    color:#000000;
}
#obsah .panelNastrojov .pocetSprav a:hover {
    color:#000000;
}
.js_Menu_contacts_list {
    background-color:#1B1B1B;
}
.pocetSprav, tr.empty, .citaj.j_filterFolder.inbox__ {
    color:#ffffff;
}
#mailbox th {
    background-color:#000000;
}
#mailbox td a, #mailbox td.pr b {
    color:#ffffff;
}
#sprava, #obsah div.formular.nastavenia, .zalozkovaPonuka {
    background-color:#1B1B1B;
    color:#ffffff;
}
#hlavickaSpravy, #sprava .hlavickaSpravyVTele {
    background-color: #000000;
    color:#999;
}
#hlavickaSpravy .clovek .ponuka, #sprava .hlavickaSpravyVTele .clovek .ponuka {
    background-color: #1B1B1B;
    color:#999;
}
#menu .box .hlavna, ul.hlavna, ul, .volby, #kapacita, #menu #prepinacVerzie, #menu .skiny {
    background-color:#1B1B1B;
    color:#ffffff;
}
#podklad #hornaLista #panelNastaveni {
    background-color: #1B1B1B;
    padding:0px;
}
#adresy, .blacklist, #menu .box .ponuka.hlavna .pocet {
    background-color:#1B1B1B;
}
#mailbox tr.highlight td, #mailbox tr.highlight:hover td {
    background:#000000;
}
#mailbox tr:hover td, #obsah #vObsahu, #obsah .nastavenia #infoOPolozke form div.pravidlo .box {
    background:none;
}
#obsah .aktivaciaSMSNotifikacii, #obsah .coToJe {
    color:#ffffff;
}
#hlavickaSpravy dd.c_plaintext_html span, #hlavickaSpravy dd.c_plaintext_html a {
    border:none;
    background:none;
}
#hlavickaSpravy dt {
    color: #ffffff;
}
#prilohySpravy {
    background:#000000;
    color: #ffffff;
}
#rychlaOdpoved {
    background:#000000;
}
#rychlaOdpoved .textarea .rychlaOdpovedText {
    color: #000000;
}
pre {
    color: #ffffff
}
#cboxWrapper {
    background:#252525;
}
#cboxMiddleLeft, #cboxMiddleRight, #cboxContent {
    background:#757575;
}
#hlavickaSpravy.odosliNovy .input {
    color:#ffffff;
    background:#1B1B1B;
}
#hlavickaSpravy.odosliNovy dt {
    color:#ffffff;
}
#ab_dropdown ul li a {
    color:#ffffff !important;
}
#ab_dropdown ul li a:hover {
    color:#333 !important;
}
}

@-moz-document url-prefix("https://mail.azet.sk/inbox/msg/a/compose"), url-prefix("https://mail.azet.sk/inbox/rozpisane/msg") {
.cke_inner {
    background-color:#1B1B1B;
}
.cke_editable.cke_editable_themed.cke_contents_ltr, #panelNastrojovSpravy, #obsahCistyText textarea {
    color:#ffffff;
    background:#000000;
}
.c_jsAutosaveInfo {
    color:#ffffff;
}
}

@-moz-document url-prefix("https://prihlasenie.azet.sk/") {
.landing-page {
    background:#404040;
}
.landing-page__step.landing-page__step--promo {
    background:#888888;
}
.landing-page h1.black, .landing-page__input .label{
    color:#ffffff;
}

header{
    box-shadow:none;
    background:none;
}
}
