css:
@-moz-document url-prefix("https://www.aktuality.sk"), url-prefix("https://spravy.aktuality.sk/"), url-prefix("https://domace.aktuality.sk/"), url-prefix("https://tema.aktuality.sk"), url-prefix("https://pocasie.aktuality.sk/"), url-prefix("https://sport.aktuality.sk/") {
body, .page-container, .list, .nameday-date {
    color : #ffffff !important;
    background-color: #303030 !important;
}
.main-logo {
    background: #ffffff;
    border-radius:5px;
    padding:5px 0 0 0;
}
.logo-wrap {
    background: #ffffff;
    border-radius:2px;
    padding:2px 2px 2px 10px;
    margin-left:-10px;
}

.paging a{
	background:#202020;
}

.footer .footer-first-row, .plus-options-container, .article-payment-blocker.not-paid-thanks-blocker, .article.info-text.span, .article-payment-blocker.not-paid-first-blocker {
    background-color: #202020;
    color: #ffffff;
}
h1, h2, h3, h4, a, .article-special, article, .article-box a:visited, .article-box, .article-object-link, .linked-article, .text, .menu-container .date, .temperature, .day-name, .forecast, .footer-item {
    color: #ffffff !important;
}
.article-box.article-box-timeline .article-item .article-title, .article-box .article-perex, .section-title.section-logo-aktualne-temy, .menu-container .temperature, .plus-introduction-container .introduction, .plus-benefits-container .benefits-list .benefit-item .first-line, .article-title, .article-item-plus .text-wrapper .article-title, .article.article-content.fulltexta.blocker-login-button {
    color: #ffffff !important;
}
.payment-description, .content-wrapper, .station-title, .article-time, .section-title.black-color {
    color : #ffffff !important;
    background-color: #303030 !important;
}
.payment-container .payment-shadow {
    box-shadow:0 -50px 125px 60px #303030;
}
.timeline-gradient-overlay-wrapper .timeline-gradient-overlay {
    background:linear-gradient(to top, rgb(35, 35, 45) 0%, rgba(0, 0, 0, 0) 100%);
}
.timeline-gradient-overlay-wrapper .timeline-gradient-btn-wrapper {
    background:rgb(35, 35, 45);
}
.article-box .article-head-title.hp-comments a, .article-box .article-head-title a.hp-comments a, .article-box a:visited {
    color : #ffffff !important;
    background-color: #303030 !important;
}
#compact-menu, .swipable-area {
    color : #ffffff !important;
    background-color: #2D2D2D !important;
}
.popup-base-wrapper, .credentials-wrapper, .btn-a.scroll-up{
    color: #000000 !important;
    background-color: none;
}
.payment-container-b .payment-box .picture-wrapper .popup-picture {
    height:309px;
}
.panel, .show, .styled-box-1 {
    background-color: #23232D !important;
    color: #ffffff !important;
}
.font-pt-serif-bold, .text, .linked-article, .payment-info-text, .article-object.article-object-link {
    color: #ffffff !important;
}
.article-payment-thanks-blocker .content-wrapper {
    position:absolute;
    right: 5px;
}
.payment-container .payment-box .content-wrapper .text-wrapper .icon-quote,.article-payment-blocker.not-paid-thanks-blocker {
    display:none;
}
.payment-container .payment-box .content-wrapper {
    padding:30px 30px 10px 30px;
}
.article-payment-blocker.not-paid-first-blocker {
    -webkit-box-shadow: none;
    box-shadow: none;
}
a.blocker-login-button {
    color:#ffffff !important;
}
#payment-blocker, #report-bug-btn {
    color: black !important;
}
#ab_dropdown ul li a {
    color:#ffffff !important;
}
.box-menu-item {
    color:#cf2f32;
}
a:hover, a:visited, .section-title {
    color:#ffffff;
}

.plus-options-container .plus-options-wrapper .option-container .option-wrapper .info {
    opacity:1;
}
}

@-moz-document url-prefix("https://sport.aktuality.sk/") {
/*sport*/
 .panel, .show, .styled-box-1, .default-title, .most-read-title, .item, .box-title, .homepage-result-box .table-header, .homepage-result-box:hover, .result-row-hp {
    background-color: #303030;
    color: #ffffff;
}
.timeline-gradient-overlay-wrapper .timeline-gradient-overlay {
    background:linear-gradient(180deg, hsla(0, 0%, 100%, 0) 0, #303030)
}
.homepage-result-box .table-content li .league-wrapper .league-name:after, .homepage-result-box .table-content li .team .team-name:after {
    background: linear-gradient(90deg, hsla(0, 0%, 100%, 0) 0, #303030);
}
.main-logo, .logo-wrapper {
    background: #ffffff;
    border-radius:5px;
    padding-bottom:5px;
}
.big-title, .most-read-container .most-read-title, .most-read-container .most-read-list .most-read-item .text-wrapper .item-title, .header-container .bottom-header-container .menu-container .menu-search-container .search-button-wrapper .search-title, .video-box-widget-container .box-header .box-title, .article-list-title, .layout-perex-item.top-item .text-wrapper .default-title, .category-info, .pagination-wrapper .pagination li .link-page {
    color: #ffffff;
    text-decoration:none;
}
.timeline-gradient-overlay-wrapper .timeline-gradient-btn-wrapper {
    background:#303030
}
.homepage-result-box .table-content li:hover {
    background:none;
}
.shifted-slide-menu .shifted-body .menu-wrapper .second-level-list {
    background:#404040
}
.shifted-slide-menu .shifted-body .menu-wrapper .first-level-list .main-category .item-wrapper:hover, .shifted-slide-menu .shifted-body .menu-wrapper .first-level-list {
    background:#454545
}
.shifted-slide-menu .shifted-body .menu-wrapper .first-level-list .main-category .item-wrapper {
    background:#303030
}
}
