css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("hattrick.org") {

/* Fix the header on screen */
#aspnetForm:not([action*="useFlashVersion=True"]) #header {
position: fixed !important;
top: 0px; 
width: 1200px;
z-index: 9999;
}

/* compensate for fixed header, move menu from the edge */
#aspnetForm:not([action*="useFlashVersion=True"]) #menu {
width: 1230px !important;
margin-left:0px !important;
padding:0px 0px 0px 4px !important;
-moz-border-radius-topright: 10px;
-moz-border-radius-bottomright: 10px;
}

/* Logout botton +radius */
#aspnetForm:not([action*="useFlashVersion=True"]) #menu .logout {
margin-right:11px !important; 
}

/* add whitespace below header */
#aspnetForm:not([action*="useFlashVersion=True"]) #menu {
margin-bottom: 0px !important;
}

/* compensate for fixed header */
#aspnetForm:not([action*="useFlashVersion=True"]) #content {
padding-top: 100px !important;
}


/* fix for forumbookmark z-index 99999 */
.cfWrapper .bookmarkMessage {
z-index: 0 !important;
} 



/* STADIONIMAGEFIX */

/* moves Arena to the bottom */
#aspnetForm:not([action*="isYouth=True"])  #ctl00_CPMain_pnlPreMatch object#ARENA {
float:right !important;
margin-top:615px !important;
}

/* moves Arena info to the bottom */
#aspnetForm:not([action*="isYouth=True"])  #ctl00_CPMain_pnlPreMatch div.arenaInfo {
margin-top:600px !important;
}

/* moves rest to the top */
#aspnetForm:not([action*="isYouth=True"])  #ctl00_CPMain_pnlPreMatch div#ctl00_CPMain_pnlTeamInfo {
margin-top:-870px !important;
float:left !important;
}

/* fix left submenu */
#aspnetForm:not([action*="useFlashVersion=True"]) #content .subMenu {
position: fixed !important;
}

/* compensate for fixed left submenu */
#aspnetForm:not([action*="useFlashVersion=True"]) #content #mainWrapper {
margin-left: 170px ;
min-height: 620px !important; /* temporary height hack before sticky footer */
}

/* compensate for fixed left submenu Forum */
#aspnetForm:not([action*="useFlashVersion=True"]) #content #mainWrapper {

margin-left: 170px;
width:655px !important;
}

#mainWrapper>div[class="main mainConf"]
{
margin-left: -170px;
}

/* part 3 */
/* Kitdesignerfix */
#aspnetForm[action*="?TeamID="] div#mainWrapper>div[class="main mainFull"] {
margin-left:-275px !important;
width: 950px !important;
}

}
