css:
@-moz-document domain("webcourses.ucf.edu") {
/* UCF Webcourses Dark and Compact Theme
 * Created by: Keenan Turley
 * Discord: Para#2248
 * Email: keenan.turley@gmail.com
 *
 * I'm a CSS noob so please don't bully me :)
 */

/* Body */
 body {
    /* Compact line spacing */
    line-height: 1.25;
    /* Dark text color */
    color: hsl(0, 0%, 60%);
    /* Dark background*/
    background-color: hsl(0, 0%, 10%);
}
/* Main background */
 .ic-Layout-columns {
    background-color: hsl(0, 0%, 10%);
}
/* Remove webcourses@UCF logo */
 .ic-app-nav-toggle-and-crumbs::after {
    display: none;
}
/* ---- Left Side ---- */

/* Background */
 .with-left-side #left-side {
    /* webcourses uses !important, so I guess I have to use it too... */
    background-color: hsl(0, 0%, 5%) !important;
}
/* Navigation */
 .ic-app-header__main-navigation {
    background-color: hsl(0, 0%, 0%);
    border-right: 1px solid hsl(47, 100%, 51%);
    padding-right: 5px;
}
/* Logo container */
 .ic-app-header__logomark-container {
    background-color: hsl(0, 0%, 0%);
}
/* Logo */
 .ic-app-header__logomark {
    /* Hopefully they don't delete this file */
    background-image: url(https://www.ucf.edu/brand/files/2016/07/UCF-tab-NoBleed_vert-KG-7406.png);
}
/* Avatar */
 .ic-avatar {
    border: 2px solid hsl(47, 100%, 51%);
}
/* Option Icons */
 .ic-app-header__menu-list-link .ic-icon-svg {
    fill: hsl(47, 100%, 51%);
}
/* Currently Selected Option*/
 .ic-app-header__menu-list-item.ic-app-header__menu-list-item--active .ic-app-header__menu-list-link {
    background-color: hsl(0, 0%, 15%);
}
/* -- Course Menu -- */
 .ic-app-course-menu {
    padding: 5px 0px 0px 5px;
}
/* Options */
 .ic-app-course-menu #section-tabs-header-subtitle {
    padding: 0;
}
/* Sections */
 .list-view > ul > li > a, .list-view > nav > ul > li > a {
    border-radius: 0;
    border-bottom: 1px solid hsl(0, 0%, 25%);
    border-left: 1px solid hsl(0, 0%, 25%);
    border-right: 1px solid hsl(0, 0%, 25%);
    padding: 5px 5px;
    color: hsl(0, 0%, 70%);
}
/* Top border for first section */
 .list-view > nav > ul > li:first-of-type > a {
    border-top: 1px solid hsl(0, 0%, 25%);
}
/* Bottom border for last section */
 .list-view > nav > ul > li:last-of-type > a {
    border-bottom: 1px solid hsl(0, 0%, 25%);
}
/* Active section */
 .list-view a.active {
    background-color: hsl(0, 0%, 10%);
}
/* Hovered section */
 .list-view > nav > ul > li > a:hover {
    background: hsl(0, 0%, 15%);
}
/* -- Modules Header -- */
 .header-bar {
    background-color: hsl(0, 0%, 5%);
    border: none;
    min-height: 0px;
    padding-bottom: 0px;
}
/* Shrink header thickness */
 .ic-app-nav-toggle-and-crumbs {
    min-height: 3rem;
    padding: 5px 8px;
}
/* -- Module group containers -- */
 .item-group-container .item-group-condensed {
    border-bottom: 1px solid hsl(0, 0%, 20%);
    padding: 5px;
}
.ic-Layout-contentMain {
    padding: 12px;
}
.item-group-container {
    background-color: hsl(0, 0%, 10%);
    border: none;
    border-radius: 5px;
}
/* -- Modules Footer -- */
 .module-sequence-footer .module-sequence-footer-content {
    background-color: hsl(0, 0%, 5%);
    border-top: 1px solid hsl(0, 0%, 15%);
}
/* -- Modules -- */
 .ig-list .ig-row {
    background: hsl(0, 0%, 7%);
    border-color: hsl(0, 0%, 20%);
    border-radius: 0;
    padding: 2px 10px 2px 10px;
    color: hsl(0, 0%, 70%);
}
/* Module group header */
 .context_module .header {
    padding: 0px;
}
.ig-header-title .name {
    color: hsl(0, 0%, 60%);
    text-shadow: none;
}
/* Top row top border */
 .item-group-condensed .ig-list li:first-child .ig-row, .item-group-condensed .ig-list li:first-of-type .ig-row, .item-group-condensed .first_visible .ig-row {
    border-top: 1px solid hsl(0, 0%, 20%);
}
/* Hover color */
 .ig-list .ig-row:hover {
    background: hsl(0, 0%, 15%);
}
/* Fix first and last row having white pixels on edges */
 .context_module_item.context_module_item_hover {
    background-color: hsl(0, 0%, 10%);
}
/* Title color */
 .ig-list .ig-row a.ig-title {
    color: hsl(0, 0%, 70%);
}
/* Details / Due date color */
 .ig-details {
    color: hsl(0, 0%, 50%);
}
/* -- Breadcrumbs -- */
 .ic-app-nav-toggle-and-crumbs {
    background-color: hsl(0, 0%, 15%);
    border-width: 0;
}
#breadcrumbs {
    background-color: hsl(0, 0%, 15%);
}
#breadcrumbs > ul > li > span {
    color: hsl(0, 0%, 60%)
}
#breadcrumbs > ul > li + li:last-of-type a {
    color: #008EE2;
}
/* -- Buttons -- */
 .btn, .Button, .ui-button {
    background-color: hsl(0, 0%, 8%);
    color: hsl(0, 0%, 60%);
    border: 1px solid hsl(0, 0%, 20%);
}
/* Buttons hover */
 .btn:hover, .Button:hover, .ui-button:hover, .ui-button.ui-state-hover {
    background-color: hsl(0, 0%, 15%);
    color: inherit;
}
/* ---- Right Side ---- */

/* Buttons */
 #right-side .button-sidebar-wide {
    text-align: center;
}
.ic-app-main-content__secondary {
    padding: 8px;
}
body.course-menu-expanded .ic-app-main-content__secondary {
    padding-left: 8px;
}
/* -- Todo List -- */

/* Header */
 #right-side .todo-list-header {
    color: hsl(0, 0%, 60%);
    border-bottom: 1px solid hsl(0, 0%, 25%);
    padding-bottom: 0;
}
/* Item */
 .ToDoSidebarItem {
    color: hsl(0, 0%, 60%);
}
.ToDoSidebarItem__Info > ul > li {
    font-size: 0.75rem;
}
/* -- Recent Feedback -- */

/* Change divider color */
 #right-side .h2 {
    border-bottom: 1px solid hsl(0, 0%, 25%);
}
/* Remove Recent Feedback white gradient */
 #right-side .events_list .event-details::after {
    background: none;
}
/* Assignment Feedback */
 #right-side .right-side-list li a {
    padding: 5px 0px;
}
/* ---- Dashboard ---- */

/* -- Header -- */

/* Container */
 .ic-Dashboard-header__layout {
    background-color: hsl(0, 0%, 10%);
    border-bottom: 1px solid hsl(0, 0%, 20%);
}
/* Planner Header */
 .PlannerHeader {
    background-color: hsl(0, 0%, 10%);
}
/* Buttons */
 .PlannerHeader > button > span, .PlannerHeader > span > span > button > span, #DashboardOptionsMenu_Container > span > button > span > span {
    color: hsl(0, 0%, 60%);
}
#planner-today-btn {
    background-color: hsl(0, 0%, 5%);
    border: 1px solid hsl(0, 0%, 20%);
}
/* -- Planner Day -- */
 .planner-day {
    background-color: hsl(0, 0%, 8%);
    border: 1px solid hsl(0, 0%, 20%);
    margin-top: 1rem;
    border-radius: 5px;
    padding: 5px 0px;
}
/* Day Text */
 .planner-day > h2 {
    color: hsl(0, 0%, 60%);
    padding-left: 2.5rem;
    border-bottom: none;
}
/* Class Grouping */
 .planner-grouping {
    margin-top: .5rem;
}
/* Class name in group */
 .planner-grouping > a > span {
    background-color: hsl(0, 0%, 10%);
}
/* Class name background fade removal */
 .planner-grouping > a > span::after {
    display: none;
}
/* Item in group */
 .planner-item, .planner-completed-items {
    border-bottom: 1px solid hsl(0, 0%, 20%);
    background-color: hsl(0, 0%, 6%);
    padding: 3px 5px;
}
/* Item class text (uses a wildcard selector, these are new to me) */
 .planner-item > div > div > div > div[class ^="type"] > span {
    color: hsl(0, 0%, 50%);
}
/* Item name text */
 .planner-item > div > div > div > div[class ^="title"] > a > span > span, .planner-item > div > div > div > div[class ^="title"] > button > span > span {
    color: inherit;
}
/* Item Checkbox / Completion */
 .planner-item > div > div > label > span > span {
    background-color: hsl(0, 0%, 3%);
}
/* Graded "pill" */
 li[class ^="item"] > span[data-cid="Pill"] {
    background-color: hsl(0, 0%, 5%);
    border-color: hsl(0, 0%, 25%);
}
/* remove "Nothing Plannet Yet" text from empty days */
 .planner-day > div > span {
    display: none;
}
/* Empty Days */
 .planner-empty-days {
    background-color: hsl(0, 0%, 8%);
    border: 1px solid hsl(0, 0%, 20%);
    border-radius: 5px;
    padding: 5px;
    color: hsl(0, 0%, 60%);
}
/* Empty day dates */
 .planner-empty-days > h2 {
    padding-left: 2.5rem;
    border-bottom: none;
}
/* ---- Courses Pull-out ---- */

/* Left side container */
 #nav-tray-portal > span > span {
    background-color: hsl(0, 0%, 0%);
    width: 25em;
}
/* Container */
 .tray-with-space-for-global-nav > div {
    /* Inline style must be overwritten using !important */
    padding: 5px !important;
}
/* Remove white dividers */
 .tray-with-space-for-global-nav > div > hr, .tray-with-space-for-global-nav > div > ul > li > hr {
    display: none;
}
/* Course List */
 .tray-with-space-for-global-nav > div > ul {
    background-color: hsl(0, 0%, 5%);
    border: 1px solid hsl(0, 0%, 25%);
    border-radius: 5px;
    /* !important due to inline style */
    padding: 5px !important;
}
/* Course list item */
 .tray-with-space-for-global-nav > div > ul > li {
    /* Text color for semester */
    color: hsl(0, 0%, 60%)
}
/* ---- Inbox ---- */

/* -- Header -- */
 .conversations .panel {
    background: hsl(0, 0%, 10%);
    border-bottom: 1px solid hsl(0, 0%, 25%);
}
/* Message details */
 .message-detail {
    background-color: hsl(0, 0%, 8%);
}
/* Message */
 .message-list .messages > li {
    background-color: hsl(0, 0%, 8%);
    border-left: 1px solid hsl(0, 0%, 25%);
    border-bottom: 1px solid hsl(0, 0%, 25%);
    padding: 10px 5px;
}
/* Active message */
 .message-list .messages > li.active {
    background-color: hsl(0, 0%, 15%);
}
/* Author */
 .message-middle-column .author {
    color: hsl(0, 0%, 40%);
}
/* Subject */
 .message-middle-column .subject {
    color: hsl(0, 0%, 50%);
}
/* Summary */
 .message-middle-column .summary {
    color: hsl(0, 0%, 60%);
}
/* ---- Rubric ---- */

/* General */
 .rubric {
    background-color: hsl(0, 0%, 8%);
}
/* Title */
 .rubric_container .rubric_title {
    background-color: hsl(0, 0%, 5%);
    border-color: hsl(0, 0%, 25%);
}
/* Table head */
 .rubric_container thead th {
    background-color: hsl(0, 0%, 5%);
    border-color: hsl(0, 0%, 25%);
}
/* Table body */
 .rubric_container td, .rubric_container th, .rubric_container table.ratings td {
    border-color: hsl(0, 0%, 25%);
}
/* ---- Announcements ---- */

/* Container header and footer */
 .toolbarView .headerBar {
    border: 1px solid hsl(0, 0%, 25%);
    background-color: hsl(0, 0%, 8%);
}
/* Message body */
 .topic .entry-content {
    border-left: 1px solid hsl(0, 0%, 25%);
    border-right: 1px solid hsl(0, 0%, 25%);
}
}
