css:
@-moz-document regexp(".*(\\/questions\\/)[\\d].*") {
.top-bar {
    height: 30px;
    z-index: 0;
}
.s-input__search {
    font-size: 10px;
    padding: 2px;
    max-width: 400px;
}

.help-button-item, .site-switcher-item {
	display: None !important;
}

body {
    background-image: none;
}
div[id^=answer-] {
    padding: 10px;
}

pre, code {
    font-family: SF Mono, monospace;
    font-size: 14px !important;
}
.bottom-notice, .bounty-notification, .comment-flagging, .everyonelovesstackoverflow, .hero-container, .js-favorite-count, .js-favorite-btn, .js-vote-down-btn, .new-post-login, .post-text~div, #dfp-mlb, #left-sidebar, #left-sidebar, #post-form, #sidebar, div[id^=comments-link], footer, #question > div:nth-child(1) > div:nth-child(3), .site-header, .comments:first-of-type {
    display: None;
}
p, .comments {
    max-width: 800px;
}
.answer, .answers-header, .mainbar, #answers, #mainbar, #questions {
    width: 100%;
    margin-right: 2vw
}
}
