css:
@-moz-document domain("nebula.moe"), domain("acg.mn"), domain("pawoo.net"), domain("wxw.moe"), domain("mastodon.social"), domain("mstdn.jp") {
body, .ui, .getting-started__footer a, .drawer__inner.darker, .detailed-status__display-name strong, .notification__message, .column-link, .status, .reply-indicator__content, .status__content, .empty-column-indicator, .error-column, .setting-text, .text-btn, .detailed-status__display-name, .drawer__inner, .search__input, .navigation-bar, .navigation-bar strong {
    background: #fff8dd;
    color: #000;
}

.loading-bar {
    background-color: #591804;
}

.tabs-bar {
    background: #fff8dd;
    color: #000;
    border: 1px solid #591804;
}

.tabs-bar__link {
    color: #591804;
}

.tabs-bar__link.active {
    border-bottom: 2px solid #591804;
    color: #591804;
}

.tabs-bar__link:active, .tabs-bar__link:focus, .tabs-bar__link:hover, .tabs-bar__link.active:active, .tabs-bar__link.active:focus, .tabs-bar__link.active:hover {
    background: #591804;
    color: #fff;
    border: 0;
}

.tabs-bar__link {
    border-bottom: 2px solid #fff1cb;
}

.floating-action-button {
    background: #591804;
}

.floating-action-button:hover {
    background: #fff8dd;
    color: #591804;
}

.getting-started .column-link, .scrollable .column-link {
    background: #fff8dd;
    color: #591804;
    border-bottom: 1px solid #591804;
}

.getting-started .column-link:active, .getting-started .column-link:focus, .getting-started .column-link:hover, .scrollable .column-link:active, .scrollable .column-link:focus, .scrollable .column-link:hover {
    background: #fff1cb;
}

.getting-started .navigation-bar {
    border-top: 1px solid #591804;
    border-bottom: 1px solid #591804;
}

/* Pawoo Start */

/*.status__content.status__content--with-action {
    max-height: 300px;
    overflow-y: hidden;
}

.media-gallery, .media-gallery__item, .media-spoiler {
    max-height: calc((((100vw - 300px) / 2) - 100px)/ 16 * 9);
}*/

.search-results, .trend-tags, .compose-form .autosuggest-textarea__textarea, .compose-form .hastag-editor__input, .compose-form .spoiler-input__input {
    background: #fff8dd;
}

.trends__header, .search-results__header, .search-results__section h5, .pawoo-subcolumn__header, .pawoo-navigation-column .pawoo-navigation-column__subcolumns .pawoo-navigation-column__subcolumns__button>* {
    background: #fff1cb;
    color: #591804;
}

.pawoo-navigation-column .pawoo-navigation-column__subcolumns .pawoo-navigation-column__subcolumns__button>*:hover {
    background: #591804;
    color: #fff1cb;
}

.status__content a.hashtag, .status__content a span.ellipsis, .suggestion-tags__name * {
    color: #591804;
}

.suggestion-tags__name *:hover {
    color: #A5705E;
}

.suggestion-tags__description {
    color: #A5705E;
}

.suggestion-tags__description.suggestion:before {
    background-color: #A5705E;
}

.suggestion-tags__body li {
    border-bottom: 1px solid #A5705E;
}

.suggestion-tags__button {
    background: #fff1cb;
    color: #591804;
}

.suggestion-tags__button:hover {
    background: #591804;
    color: #fff1cb;
}

.privacy-dropdown__option.active, .privacy-dropdown__option:hover, .time-limit-dropdown__option.active, .time-limit-dropdown__option:hover {
    background: #591804;
}

.getting-started p {
    color: #606984;
}

/* Pawoo End */

.drawer__header {
    background: #fff8dd;
    border: 1px solid #A5705E;
}

.drawer__tab {
    color: #591804;
}

.drawer__header a:hover {
    background: #591804;
    color: #fff;
}

.drawer__inner__mastodon {
    background-color: #fff8dd;
}

.column-subheading, .column-link:hover {
    background: #fff1cb;
    color: #000;
}

.account .account__display-name {
    color: #606984;
}

.account__display-name strong, .status__display-name strong {
    color: #000;
}

.column-settings__section, .setting-toggle__label {
    color: #282c37;
}

.dropdown-menu, .dropdown-menu__item a {
    background: #fff;
}

.dropdown-menu__item a:active, .dropdown-menu__item a:focus, .dropdown-menu__item a:hover {
    background-color: #591804;
}

.notification__filter-bar {
    border: 1px solid #A5705E;
    border-top: 0;
}

.account__section-headline a, .account__section-headline button, .notification__filter-bar a, .notification__filter-bar button {
    color: #591804;
}

.account__section-headline a.active, .account__section-headline button.active, .notification__filter-bar a.active, .notification__filter-bar button.active {
    color: #591804;
}

/* Toot Button */

.button {
    background-color: #591804;
}

.column-header__back-button {
    color: #591804;
}

.privacy-dropdown__option.active, .privacy-dropdown__option:hover, .privacy-dropdown.active .privacy-dropdown__value.active {
    background: #591804;
}

.icon-button.active, .text-icon-button.active, .icon-button.inverted.active {
    color: #591804;
}

/* Poll */

.poll__input {
    border: 1px solid #A5705E;
}

.poll__text input[type=text]:focus {
    border: 1px solid #591804;
}

.poll__text input[type=text] {
    background: #fff7de;
}

.compose-form__poll-wrapper .icon-button.disabled {
    color: #A5705E;
}

.compose-form__poll-wrapper, .compose-form__poll-wrapper .poll__footer {
    border-top-color: #A5705E;
}

/* Links */

.compose-form .compose-form__warning, .compose-form .compose-form__warning a, .reply-indicator__content a, .status__content a, .column-link--transparent.active {
    color: #591804;
}

/* Search Box */

.search__input, .search__input:focus {
    background: #fff7de;
}

/* Search Box Border */

.box-widget input[type=email], .box-widget input[type=password], .box-widget input[type=text], .box-widget textarea, .compose-form .spoiler-input__input, .compose-form__autosuggest-wrapper, .compose-form__poll-wrapper select, .poll__text input[type=text], .search__input, .setting-text, .statuses-grid .detailed-status {
    border: 1px solid #A5705E;
}

/* Toot Config */

.compose-form .autosuggest-textarea__suggestions, .compose-form .compose-form__buttons-wrapper {
    background: #fff7de;
    border: 1px solid #A5705E;
}

/* Toot Input Box*/

.compose-form .autosuggest-textarea__textarea, .compose-form .spoiler-input__input {
    background: #fff7de;
}

.compose-form .compose-form__modifiers {
    background: #fff7de;
}

.reply-indicator {
    background: #fff1cb;
    border: 1px solid #A5705E;
}

/* Emoji Panel */

.emoji-mart-bar:first-child {
    background: #fff1cb;
}

.emoji-picker-dropdown__menu, .emoji-mart-scroll, .emoji-mart-category-label span, .emoji-mart-search {
    background: #fff7de;
}

.emoji-mart-anchor-selected, .emoji-mart-anchor-selected:hover {
    color: #591804;
}

.emoji-mart-anchor-bar {
    background: #591804;
}

.emoji-mart-search input {
    border: 1px solid #A5705E;
    background: #fff7de;
}

.emoji-mart-category .emoji-mart-emoji:hover:before {
    background: #A5705E;
}

/* Column Header */

.column-back-button {
    background: #fff7de;
    border-color: #A5705E;
    color: #A5705E;
}

.column-header {
    background: #fff7de;
    border: 1px solid #A5705E;
}

.column-header__wrapper.active:before {
    background: radial-gradient(ellipse,#A5705E 0,rgba(43,144,217,0) 60%);
}

.column-header.active {
    box-shadow: 0 1px 0 #A5705E;
}

.notification__filter-bar, .account__section-headline, .notification__filter-bar {
    border-color: #A5705E;
}

/* Column Button */

.account__header__bar {
    border-color: #A5705E;
}

.account__header__bar, .column-header__back-button, .column-header__button, .column-header__button.active {
    background: #fff7de;
    color: #591804;
}

.column-header>.column-header__back-button, .notification__message .fa {
    color: #591804;
}

.column-header__button.active, .column-header__button:hover {
    background: #591804;
    color: #fff7de;
}

.column-header__button.active:hover {
    background: #591804;
    color: #fff7de;
}

/* Column Settings */

.column-header__collapsible-inner {
    background: #fff7de;
    border: 1px solid #A5705E;
    border-top: 0;
}

.react-toggle--checked .react-toggle-track {
    background: #591804;
}

.react-toggle.react-toggle--checked:hover:not(.react-toggle--disabled) .react-toggle-track {
    background: #591804;
}

.react-toggle:hover:not(.react-toggle--disabled) .react-toggle-track {
    background: #591804;
}

.react-toggle--checked .react-toggle-thumb {
    border-color: #591804;
}

.account__section-headline button, .notification__filter-bar button, .account__section-headline, .notification__filter-bar {
    background: #fff1cb;
}

.account__section-headline a.active:after, .notification__filter-bar button.active:after {
    border-color: transparent transparent #fff7de;
}

.account__section-headline a.active:before, .account__section-headline button.active:before, .notification__filter-bar a.active:before, .notification__filter-bar button.active:before {
    border-color: transparent transparent #A5705E;
}

/* Confirmation */

.actions-modal, .boost-modal, .confirmation-modal, .embed-modal, .error-modal, .mute-modal, .onboarding-modal, .report-modal {
    background: #fff1cb;
}

.boost-modal__action-bar, .confirmation-modal__action-bar, .error-modal__footer, .mute-modal__action-bar, .onboarding-modal__paginator {
    background: #fff7de;
}

/* Status */

.status, .status.status-direct, .status.status-direct:not(.read) {
    background: #fff7de;
    border-bottom: 1px solid #A5705E;
}

.status__content__read-more-button {
    color: #591804;
}

.status-card, .status-card.compact, .status-card strong, .status-card.compact strong, a.status-card:hover .status-card__image, a.status-card.compact:hover .status-card__image {
    border-color: #591804;
    color: #591804;
}

a.status-card:hover, a.status-card.compact:hover, a.status-card:hover strong, a.status-card.compact:hover strong {
    background: #591804;
    color: #fff;
}

.media-spoiler, .media-spoiler:hover {
    background: #fff1cb;
    color: #591804;
}

.media-gallery__preview, .status-card__image {
    background: #fff1cb;
}

.detailed-status, .detailed-status__action-bar {
    background: #fff7de;
}

.load-gap {
    border-bottom: 1px solid #A5705E;
}

.load-more:hover {
    background: #fff1cb;
}

.icon-button.overlayed {
    background: hsla(0,0%,100%,.6);
    border-radius: 4px;
    color: rgba(0,0,0,.7);
}

.icon-button.overlayed:hover {
    background: hsla(0,0%,100%,.9);
}

.column-inline-form, .column>.scrollable, .getting-started {
    background: #fff7de;
    border-left: 1px solid #A5705E;
    border-right: 1px solid #A5705E;
    border-bottom: 1px solid #A5705E;
}

.detailed-status__action-bar {
    border-top: 1px solid #A5705E;
    border-bottom: 1px solid #A5705E;
}


/* Scroll Bar */

::-webkit-scrollbar-thumb {
    background: #A5705E;
}

::-webkit-scrollbar-track {
    background: transparent;
}

::-webkit-scrollbar-thumb:hover {
    background: #591804;
}

::-webkit-scrollbar-track:hover {
    background: #fff1cb;
}
}
