/* ==UserStyle==
@name           WoWCircle - BlueStyle by Raxaji - 12.06.2019
@namespace      https://forum.wowcircle.com/member.php?u=258948
@version        1.1.0
@description    WoWCircle - BlueStyle
@author         Raxaji
==/UserStyle== */

@-moz-document domain("forum.wowcircle.com") {
/* Сделано Raxaji */

/* Кнопка форум ФОН */
.navtabs li.selected a.navtab {
    color: #ffffff;
    background: var(--color1) none repeat-x;
}
.navtabs li.selected a.navtab:hover {
    color: #ffffff;
    background: var(--color1) none repeat-x;
}
/* Альбом Группы Звания Что Новго ФОН */
.navtabs li a.navtab {
    background: var(--color2);
    color: #fff;
}
.navtabs li a.navtab:hover {
    background: #78c2ff;
    color: #fff;
}
/* Новые сообщения Личные сообщения Справка Календарь Сообщество Опции форума Навигация ФОН*/
.navtabs ul {
    background: var(--color1);
    color: #78c2ff;
}
/* Новые сообщения Личные сообщения Справка Календарь Сообщество Опции форума Навигация ЦВЕТ ШРИФТА */
.navtabs:hover ul.floatcontainer li a:hover, .navtabs:hover ul.floatcontainer li:hover a.popupctrl:hover {
    color: #78c2ff;
}
.navtabs ul.floatcontainer li a, .navtabs ul.floatcontainer li a.popupctrl {
    color: #fff;
}
/* Расширенный поиск ЦВЕТ ШРИФТА */
.navbar_advanced_search li a, .navbar_advanced_search li a:hover {
    color: #fff;
}
/* Общий ФОН */
.body_wrapper {
    background: #94cfffd9 none;
/*    background: linear-gradient(130deg, #fff, #94cfff, var(--color1)); */
}


/* Форум
Форум проекта WoW Circle
Добро пожаловать на Форум проекта WoW Circle. ЦЕВЕТ ШРИФТА */
body {
    color: #000;
}


/* Главный раздел Тем / Сообщений Последнее сообщение ФОН*/
.forumhead {
    background: #002340 none;
    color: #fff;
}


/* Главный раздел ЦВЕТ ШРИФТА */
.forumbit_nopost .forumhead .forumtitle a {
    color: #fff;
}


/* Тем / Сообщений Последнее сообщение ЦВЕТ ШРИФТА */
.forumbit_nopost .forumhead .forumtitle, .forumbit_nopost .forumhead span, .forumbit_nopost .forumhead .collapse, .forumbit_post .forumhead h2 span {
    color: #ffffff;
}


/* Разделители Разделов */
.forumbit_nopost .forumbit_nopost .forumrow, .forumbit_post .forumrow {
    border-bottom: 2px solid #94cfff;
    border-left: 1px solid #94cfff;
    border-right: 1px solid #94cfff;
}


/* Разделы 1 */
.forumbit_nopost .forumbit_nopost:nth-child(odd) .forumrow, .forumbit_post:nth-child(odd) .forumrow {
    background: #fff !important;
}
/* Разделы 2 */
.forumbit_nopost .forumbit_nopost:nth-child(even) .forumrow, .forumbit_post:nth-child(even) .forumrow {
    background: #fff !important;
}






/* Раздел предназначен для описания игровых багов */

.forumbit_nopost .subforumdescription {
    color: #fff; /* Цвет Шрифта */
    background: #0167bb none repeat-x; /* Цвет Фона */
}






/* Цвет Шрифта Разделов Ссылок*/

body a {
    color: #004e8f;
    text-decoration: none;
}
body a:hover {
    color: #44abff;
    text-decoration: none;
}






/* Цвет Шрифта Разделов */

.forumbit_nopost .forumbit_nopost .forumrow, .forumbit_post .forumrow {
    color: #000c;
}






/* Цвет Шрифта Имя Пользователя */

#forums a.username {
    color: #0167bb;
}
#forums a.username:hover {
    color: #94cfff;
}






/* Статистика Сообщений Просмотров */

.threadbit .threadstats li:first-child, .forumbit_post .forumstats li:first-child, .forumbit_post .forumstats_2 li:first-child {
    background: #0167bb;
}
.threadbit .threadstats li, .forumbit_post .forumstats li, .forumbit_post .forumstats_2 li {
    background: #94cfff;
    color: #fff;
}






/* Все разделы прочитаны | Руководство сайта ФОН */

.navlinks {
    background: #fff none;
    border-top: 1px solid #fff;
}






/* Кто на сайте */

.wgo_block .blockhead {
    color: #fff; /* ТЕКСТ */
    background: #002340 none; /* ФОН */
}






/* Кто на сайте ФОН */

.wgo_block .blockbody {
    background: #fff;
}
.wgo_block .blockbody {
    border: 1px solid #fff;
}






/* Кто на сайте Текст 1 */

.wgo_block .blocksubhead {
    color: #000c;
}
.wgo_block .section {
    color: #000c;
}
.shade {
    color: #94cfff;
}






/* Выбор стиля Обратная связь */

.footer {
    background: #002340 none;
    border: 1px solid #002340;
}






/* Форум Раздел */

.toolsmenu {
    background: #002340 none;
}






/* Опции раздела Поиск по разделу */

.toolsmenu .popupgroup a.popupctrl, .toolsmenu .popupgroup .popupmenu a.popupctrl, .toolsmenu .popupgroup .popupmenu:hover a.popupctrl, .toolsmenu .popupgroup .popupmenu:hover .popupctrl a.popupctrl.active {
    background: #0167bb;
    border: 1px solid #0167bb;
}
#forumbits.forumbits .forumhead {
    border-top: 1px solid #0167bb;
    border-top-color: #0167bb;
}






/* Показаны темы с 0 по 0 из 0 */

.above_threadlist .threadpagenav .threadpagestats {
    color: #fff;
}






/* Время */

.time {
    color: #000c;
}






/* Информация о разделе и настройки отображения */

.forum_info .blockhead {
    color: #fff;
    background: #002340 none;
}
.forum_info .blockbody {
    border-bottom: 1px solid #fff;
    border-left: 1px solid #fff;
    border-right: 1px solid #fff;
    background: #fff 
}
.forum_info {
    color: #000c;
}






/* Игровой раздел Вверх */

.navpopupmenu.popupmenu.nohovermenu a.textcontrol, .navpopupmenu.popupmenu.nohovermenu a.popupctrl {
    color: #fff;
    background: #002340 none;
}
.navpopupmenu.popupmenu.nohovermenu a.textcontrol:hover, .navpopupmenu.popupmenu.nohovermenu a.popupctrl:hover {
    color: #fff;
    background: #0167bb none;
}






/* Заголовок / Автор Ответов / Просмотров Последнее сообщение от */

.threadlisthead {
    border-top: 1px solid #78c2ff;
}
.threadlisthead {
    background: #0167bb none;
    color: #ffffff;
}
.threadbit .nonthread {
    background: #0167bb none;
}
.threadbit {
    color: #000c;
}






/* Отобразить темы КНОПКА */

.button {
    color: #fff;
    background: #002340 none;
}
.button:hover {
    color: #fff;
    background: #0167bb none;
}






/* Страницы 1-99 */

.pagination span a.popupctrl {
    background: #002340 
}
.pagination span a.popupctrl:hover {
    background: #0167bb 
}
.pagination span a {
    border: 1px solid #002340;
    background: #002340 none;
    color: #fff;
}
.pagination span a:hover {
    border: 1px solid #fff;
    background: #002340 none;
    color: #fff;
}
.pagination span.selected a {
    background: #0167bb none repeat-x;
    color: #fff;
    border: 1px solid #fff;
}
.popupbody li.formsubmit {
    background: #002340 none;
}






/* Страницы мелкие кнопки */

.threadbit .pagination a {
    color: #fff;
}






/* Важные темы Посты */

.threadbit:nth-child(odd) .nonsticky, .threadbit:nth-child(odd) .discussionrow, .threadbit:nth-child(odd) .sticky {
    background: #fff;
}
.threadbit:nth-child(even) .nonsticky, .threadbit:nth-child(even) .discussionrow, .threadbit:nth-child(even) .sticky {
    background: #fff;
}
    /*
.threadbit .nonsticky, .threadbit .deleted, .threadbit .discussionrow, .threadbit .ignored, .searchbits .icon0.rating0.sticky, .imodselector.threadbit.hot.lock.new  .icon4.rating0.sticky {
    border-bottom: 2px solid #78c2ff;
}

*/
.threadbit .nonsticky, .threadbit .deleted, .threadbit .discussionrow, .threadbit .ignored, .rating0.sticky, .rating1.sticky, .rating2.sticky, .rating3.sticky, .rating4.sticky, .rating5.sticky, .rating6.sticky, .rating7.sticky, .rating8.sticky, .rating9.sticky {
    border-bottom: 2px solid #78c2ff;
}



/* Кнопка НОВАЯ ТЕМА */

.newcontent_textcontrol {
    background: #002340 none repeat-x left top;
    color: rgb(255, 255, 255);
}
.newcontent_textcontrol:hover {
    background: #0167bb none repeat-x left top;
    color: #fbfbfb;
}






/* Показано с 1 по 5 из 5 */

.above_postlist .pagination_top .pagination, .above_postlist .pagination_top .postpagestats {
    color: #fff;
}






/* Сообщение пользователя */

.postbit .posthead, .postbitlegacy .posthead, .eventbit .eventhead {
    background: #0167bb none repeat-x; /* Лента */
    color: #fff; /* Текст */
}
.tools_title h1 {
    color: #fff; /* Темы */
}
.postbit .postfoot .textcontrols, .postbitlegacy .postfoot .textcontrols, .eventbit .eventfoot .eventcontrols {
    background: #002340 none;
}






/* Спасибо */

.postbitlegacy .postfoot .textcontrols a, .postbit .postfoot .textcontrols a, .eventbit .eventfoot .eventcontrols a, .postbitlegacy .postfoot .textcontrols span.mobile, .postbit .postfoot .textcontrols span.mobile {
    background: #fff0;
    color: #fff !important;
}
.postbitlegacy .postfoot .textcontrols a:hover, .postbit .postfoot .textcontrols a:hover, .eventbit .eventfoot .eventcontrols a:hover, .postbitlegacy .postfoot .textcontrols span.mobile:hover, .postbit .postfoot .textcontrols span.mobile:hover {
    background: #fff0;
    color: #91ecff !important;
}






/* Жалоба */

.postbitlegacy .postfoot .textcontrols a.infraction, .postbit .postfoot .textcontrols a.infraction {
    background: url(https://forum.wowcircle.com/images/wow-circle/buttons/add-infraction_sm.png) no-repeat #e9e9e900 left;
}
.postbitlegacy .postfoot .textcontrols a.redcard, .postbit .postfoot .textcontrols a.redcard {
    background: url(https://forum.wowcircle.com/images/wow-circle/buttons/red-card_sm.png) no-repeat #e9e9e900 left;
}





/* Ответ */

.postbitlegacy .postfoot .textcontrols a.quickreply, .postbit .postfoot .textcontrols a.quickreply, .postbitlegacy .postfoot .textcontrols a.forwardpost, .postbit .postfoot .textcontrols a.forwardpost {
    background: url(https://forum.wowcircle.com/images/wow-circle/buttons/reply_40b.png) no-repeat #e9e9e900 left;
}






/* Ответ с цетированием */

.postbitlegacy .postfoot .textcontrols a.newreply, .postbit .postfoot .textcontrols a.newreply {
    background: url(https://forum.wowcircle.com/images/wow-circle/buttons/quote_40b.png) no-repeat #e9e9e900 left;
}






/* Мультицитирование */

.postbitlegacy .postfoot .textcontrols a.multiquote, .postbit .postfoot .textcontrols a.multiquote {
    background: url(https://forum.wowcircle.com/images/wow-circle/buttons/multiquote-back_40b.png) no-repeat #e9e9e900 left;
}







/* Вверх ▲ */
/*
.postbitlegacy .postfoot .textcontrols a:hover, .postbit .postfoot .textcontrols a:hover, .eventbit .eventfoot .eventcontrols a, .postbitlegacy .postfoot .textcontrols span.mobile, .postbit .postfoot .textcontrols span.mobile {
    background: #e9e9e900;
    color: #fff !important;
}
.postbitlegacy .postfoot .textcontrols a, .postbit .postfoot .textcontrols a, .eventbit .eventfoot .eventcontrols a, .postbitlegacy .postfoot .textcontrols span.mobile, .postbit .postfoot .textcontrols span.mobile {
    background: #e9e9e900;
    color: #91ecff !important;
}
*/






/* Редактировать */

.postbitlegacy .postfoot .textcontrols a.editpost, .postbit .postfoot .textcontrols a.editpost, .eventbit .eventfoot .textcontrols a.editevent {
    background: url(https://forum.wowcircle.com/images/wow-circle/buttons/edit_40b.png) no-repeat #e9e9e900 left;
}







/* Номер поста */

.postbit .posthead .nodecontrols a, .postbitlegacy .posthead .nodecontrols a, .eventbit .eventhead .nodecontrols a {
    color: #fff;
}
.postbitlegacy .posthead .time {
    color: #fff;
}







/* ТЕКСТ ВНУТРИ ПОСТА */

.postbitlegacy .postrow, .eventbit .eventrow, .postbitdeleted .postrow, .postbitignored .postrow {
    color: #000;
}







/* Обводка */

.toolsmenu {
    border-left: 1px solid #78c2ff;
    border-right: 1px solid #78c2ff;
    border-top: 1px solid #78c2ff;
}
.postbit, .postbitlegacy, .eventbit {
    border-left: 1px solid #78c2ff;
    border-right: 1px solid #78c2ff;
}







/* Информация пользователя ПОЛЕ */

.postbitlegacy .userinfo {
    background: #edf6ff none;
}
.postbitlegacy .postdetails, .eventbit .eventdetails {
    background: #edf6ff none;
}







/* Информация пользователя ИМЯ */

.postbitlegacy .userinfo a.username, .eventbit .userinfo a.username {
    color: #0167bb  !important;
}







/* Информация пользователя ТЕКСТ */

.postbit, .postbitlegacy, .eventbit {
    color: #000;
}
.postbitlegacy dl.userinfo_extra dt, .postbitlegacy dl.user_rep dt {
    color: #000;
}
.postbitlegacy dl.userinfo_extra dd, .postbitlegacy dl.userinfo_extra dd a, .postbitlegacy dl.user_rep dd {
    border-bottom: 1px solid #78c2ff;
    color: #000;
}
/*
#postlist li {
    color: #ffffff;
    background: #250000 none;
}
*/






/* Поле с сообщением */

.postbitlegacy .postbody, .eventbit .eventdetails .eventbody {
    border-left: 1px solid #78c2ff;
    background: #fff none;
}







/* Цитата */

.bbcode_postedby {
    background: #78c2ff;
}
.bbcode_container div.bbcode_quote div.message {
    background: #edf6ff;
}
.bbcode_container div.bbcode_quote {
    background: #edf6ff none;
    border: 1px solid #78c2ff;
}







/* Редактирование сообщения Предпросмотр */

.blockbody {
    background: #fff none no-repeat;
}
blockquote.preview.forumcontent {
    color: #000;
}
blockquote.preview .bbcode_container div.bbcode_code, blockquote.preview .bbcode_container pre.bbcode_code {
    background: #fff none repeat-x;
    color: #000;
}







/* Предварительный Просмотр */

.blockhead {
    color: #fff;
    background: #002340 none;
}
.blocksubhead {
    color: #fff;
    background: #0167bb none repeat-x;
}
.blockfoot, .blocksubfoot {
    background: #fff none;
}







/* Редактирование сообщения Поле Редакта */

.formcontrols .blockrow {
    background: #fff none repeat-x center bottom;
    border-top: solid 1px #78c2ff;
}
.formcontrols .blockrow .description {
    color: #000c;
}







/* Редактирование сообщения Кнопки */

.actionbuttons .group .button {
    color: #fff;
    background: #002340 none;
}
.actionbuttons:hover .group:hover .button:hover {
    color: #fff;
    background: #0167bb none;
}
legend {
    color: #000;
}







/* Ваши Права */

#forumrules {
    background: #fff 
}
.formcontrols {
    border-right: 1px solid #78c2ff;
    border-left: 1px solid #78c2ff;
    border-bottom: 1px solid #78c2ff;
}







/* Редактирование сообщения */

.texteditor.content .cke_skin_kama textarea.cke_source, .texteditor textarea {
    color: #000;
    background: #fff none;
}
span.cke_skin_kama {
    border: 1px solid #78c2ff;
}
.texteditor.forum .cke_skin_kama textarea.cke_source, .texteditor.forum textarea {
    color: #000;
    background: #fff none;
}
.cke_skin_kama .cke_wrapper.cke_ltr, .cke_skin_kama .cke_wrapper.cke_rtl {
    background: #fff repeat-x 0px -1950px;
}
.cke_skin_kama .cke_wrapper {
    background-color: #78c2ff !important;
}
.cke_skin_kama .cke_editor .cke_rcombo a, .cke_skin_kama .cke_editor .cke_toolgroup {
    background: url(https://forum.wowcircle.com/attachment.php?attachmentid=766826&d=1560400626) repeat-x 0px -500px;
}
.cke_skin_kama .cke_rcombo a, .cke_skin_kama .cke_rcombo a:active, .cke_skin_kama .cke_rcombo a:hover {
    border-bottom: 1px solid #78c2ff;
    border-right: 1px solid #78c2ff;
}
.textbox, textarea, select {
    color: #000;
    background: #fff none;
    border: 1px solid #78c2ff;
}
.formcontrols .blockrow input.textbox:focus, .formcontrols .blockrow textarea:focus {
    background: #78c2ff none;
}







/* Спойлер */

.spoiler {
    background: #edf6ff none;
    border: 1px solid #78c2ff;
}
.spoiler > div {
    border-top: 1px solid #78c2ff;
}







/* Лента...под постом */

.postbit .postfoot, .postbitlegacy .postfoot, .eventbit .eventfoot {
    background: #fff;
}







/* Информация о теме Права Темы */

.thread_info .blockbody {
    background: #fff;
    border-bottom: 1px solid #78c2ff;
    border-left: 1px solid #78c2ff;
    border-right: 1px solid #78c2ff;
}
.thread_info {
    color: #000c;
}
.thread_info h4 {
    background: #002340 none;
}







/* Справка */

.faqlinks {
    background: #fff none repeat-x center bottom;
}
.faqtext + .faqlinks {
    border-top: solid 1px #78c2ff;
}
.faqblock .blockbody {
    border-left: 1px solid #78c2ff;
    border-right: 1px solid #78c2ff;
    border-bottom: 1px solid #78c2ff;
}







/* Выпадающий список */

.popupbody li a, .popupbody li label {
    background: #002340 none;
}
.popupbody li a:hover, .popupbody li label:hover {
    background: #fff none;
    color: #78c2ff;
}







/* Календарь */

dl.tabslight dd a {
    color: #fff;
    border: 1px solid #002340;
    background-color: #002340;
}
dl.tabslight dd a:hover {
    color: #000;
    border: 1px solid #fff;
    background-color: #fff;
}
html>body dl.tabslight dd.selected a, html>body dl.tabslight dd.selected a:hover {
    background: #fff none;
    color: #000;
}







/* Календарь кнопки */

.cke_skin_kama .cke_dialog_contents a.cke_dialog_ui_button_ok, .cke_skin_kama .cke_dialog_contents a.cke_dialog_ui_button_cancel, .textcontrols a, a.textcontrol {
    color: #fff;
    background: #002340 none;
}
.cke_skin_kama .cke_dialog_contents a.cke_dialog_ui_button_ok, .cke_skin_kama .cke_dialog_contents a.cke_dialog_ui_button_cancel, .textcontrols a, a.textcontrol:hover {
    color: #fff;
    background: #0167bb none;
}







/* Календарь текст 1 */

.popupbody li a, .popupbody li label {
    color: #000c;
}







/* Календарь начинка */

.month .othermonth {
    background: #0167bb none;
}
.calendar .events {
    border-right: 1px solid #78c2ff;
    border-bottom: 1px solid #78c2ff;
}
.calendar .events a.weeklink, .calendar .events a.daynum {
    color: #fff;
}
.calendar.mini td.othermonth a {
    border-right: 1px solid #78c2ff;
    border-bottom: 1px solid #78c2ff;
}
.mini .othermonth a {
    background: #0167bb none;
}
.calendar .othermonth a {
    color: #000c;
}
dl.tabslight {
    border-bottom: solid 1px #78c2ff;
}
a.daynum {
    color: #000c !important;  
    }
a.daynum:hover {
    color: #78c2ff !important;  
    }







/* Календарь начинка день рождения у пользователей: */

.birthdays .posthead {
    border: 0 none;
    background: #78c2ff none repeat-x;
    color: #000;
}
.birthdays .eventrow {
    background: #fff none;
}







/* Личное сообщение */

.pmlist .blockrow {
    border-top: solid 1px #78c2ff;
    background: #fff none repeat-x center bottom;
}
.pmbit, .visitormessage {
    margin: 0 -1px;
    border-right: 1px solid #78c2ff;
    border-left: 1px solid #78c2ff;
}
.sortrow {
    border-bottom: 1px solid #78c2ff;
}
.blockbody.settings_form_border {
    border-right: 1px solid #78c2ff;
    border-left: 1px solid #78c2ff;
    border-bottom: 1px solid #78c2ff;
}







/* Мои сообщения */

#usercp_nav .blockrow .active {
    background-color: #fff;
}
#usercp_nav .blockbody {
    border: 1px solid #78c2ff;
    background: #fff none;
}
#usercp_nav .blockrow * a {
    background: #fff;
}
#usercp_nav .blockrow * a:hover {
    background: #0167bb;
    color: #fff;
}
#usercp_nav .blocksubhead {
    color: #fff;
    background: #0167bb none repeat-x;
}
#usercp_nav li a.usercp_folder-left {
    background: #0167bb url(https://forum.wowcircle.com/images/wow-circle/cms/sections.png) no-repeat 10px 4px;
    color: #fff;
