css:
@-moz-document domain("pluspora.com") {
/* Set some basic rules */
body,
html {
    background-color: #1234!important;
    color: #000 !important;
    /*
    background: #1234!important;
    color: #7A4F38!important;
    */
}

body #main-stream .stream-element {
    /* background-color: #535151;
    color: #c9cfc9!important; */
    background-color: #b9b9b9;
    color: #000;
    padding: 10px;
}

body .opengraph-container .og-description {

    font-weight: bold;
    color: #000;
}

body .timeago {
    color: #112233c2;
}

a {
    color: #1c5d75;
}

.media-left,
.media-right,
.media-body {
    color: #32a7d2
}

.sidebar,
.framed-content {
    background-color: #222222;
    color: #32a7d2;
    border: 1px solid #0f0f0f;
    border-top-color: rgb(15, 15, 15);
    border-top-style: solid;
    border-top-width: 1px;
    border-top: 0;
    box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.16), 0 2px 10px 0 rgba(0, 0, 0, 0.12);
}

.page-conversations .stream-element.message {
    color: #32a7d2;
    border: 1px solid #0f0f0f;
    box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.16), 0 2px 10px 0 rgba(0, 0, 0, 0.12);
    margin-bottom: 20px;
}

body .timeago {
    color: #32a7d2;
}

.form-control {
    color: #32a7d2;
    background-color: #222222;
}

.publisher form #publisher-textarea-wrapper {
    background-color: #090909;
    border-radius: 3px;
    border: 1px solid #999;
}


.btn-toolbar::after {

    clear: both;
}
.btn-toolbar::before,
.btn-toolbar::after {

    display: table;
    content: " ";
}
::before,
::after {

    box-sizing: border-box;
}
.md-header.btn-toolbar {
    background-color: #222222;
    color: #32a7d2;
}


}
