css:
@-moz-document url-prefix("https://www.instagram.com/p/") {
#react-root>section:first-child> main {
	height: calc(100vh - 10px) !important;
}
#react-root>section:first-child> main>div {
	max-width: unset !important;
	height: calc(100vh - 10px) !important;
	width: calc(100vw - 10px) !important;
	left: 2px !important;
	top: 0px !important;
	padding: 0 0 !important;
	margin: 0 0 !important;
	position: absolute !important;
}
#react-root>section:first-child> main>div>div {
	max-width: unset !important;
}
#react-root>section:first-child> main div[role="dialog"]>div>div {
	max-height: calc(100vh - 30px) !important;
}
#react-root#react-root>section:first-child> main div {
	max-height: calc(100vh - 10px) !important;
}
#react-root>section:first-child {
	max-height: 100vh !important;
}

#react-root>section:first-child> main img {
	object-fit: contain !important;
}
#react-root>section:first-child> main div[style*="padding-bottom"] {
	margin-bottom: 0 !important;
	padding-bottom: 0 !important;
	height: calc(100vh - 30px) !important;
}
#react-root>section:first-child> main div>ul div {
	max-height: unset !important;
}

#react-root>section:first-child> main + nav:not(:hover) {
	height: 15px !important;
	margin-bottom: -10px !important;
	overflow: hidden !important;
}
#react-root>section:first-child> main + nav:not(:hover) * {
	display: none !important;
}
}
