css:
@document domain(klavogonki.ru)
{
/*
@import url(https://fonts.googleapis.com/css?family=Roboto&subset=latin,cyrillic);
@import url(https://fonts.googleapis.com/css?family=Roboto+Mono&subset=latin,cyrillic);

@import url(https://fonts.googleapis.com/css?family=Ubuntu&subset=latin,cyrillic);

@import url(https://fonts.googleapis.com/css?family=PT+Sans&subset=latin,cyrillic);
@import url(https://fonts.googleapis.com/css?family=PT+Sans+Caption&subset=latin,cyrillic);
*/

#fontsize_cont,
input {
    font-family: 'PT Sans Caption', sans-serif!important;
}
#beforefocus {
    color: #ccc!important;
    /* Цвет набранного текста */
}
#typefocus {
    text-decoration: none!important;
    /*  background: #fff!important; */
    
    color: #4CAF50!important;
    /* Цвет подсветки (слова, символа) */
    
    text-shadow: 1px 1px #fff!important;
}
#inputtext.error {
    color: #fff!important;
    background-color: #E91E63!important;
}
.popup_message .back {
    opacity: .7!important;
}
#typeblock,
.chat-content {
    background: #F5F5F5!important;
}
}
