css:
@-moz-document domain("indie-mmo.net") {
/* Written by Michael Cox, June 2019 */
/* Indie-MMO Dark Theme ver 1.1 */
    
/* Written under Creative Commons CC-BY-4.0 https://creativecommons.org/licenses/by/4.0/ */
/* You are free to modify, distribute, and build upon this style freely. */
/* Attribution, as listed in lines 1-2, are required if using this style for your own work */

body {
    background: #111;
    color: #fff;
}

pre {
    background:#222;
}

a, a:active, a:focus, a:hover, a:visited {
    color: green;
}

a {
    color: #0072b5;
}

h1 {
    color:#000;
}

.page-title {
    color:white;
}

.top-bar, .top-bar ul {
    background:#111;
}

.top-bar-title .site-title a {
    color: #fff;
}

.comments-area, .hentry, .page-content, .read-comments, .site-main .posts-navigation, .write-comments {
    background-color: #333;
}

h1.entry-title {
    color: #fff;
}

.login form {
    color:#000;
}

code, kbd {
    background-color:transparent;
}

code {
    border: 0;
}

#secondary .widget {
    background: #333;
}

.widget-title {
    color: #fff;
}

.site-main .post-navigation {
    background: #333;
}

.wp-block-image figcaption {
    color: #a1b4c9;
}

#secondary a, #secondary li a {
    color:#fff;
}

h2.entry-title a {
    color: #fff;
}

.blog .paging-navigation li a:hover, .blog .pagination li a:hover, .blog .paging-navigation li span.page-numbers, .blog .pagination li span.page-numbers, .paging-navigation li a, .pagination li a {
    color:#fff;
}

.pagination li span.page-numbers, .paging-navigation li span.page-numbers {
    color: #000;
}

.pagination ul, .paging-navigation ul {
    background: #333;
}

.pagination li span.page-numbers.dots {
    color:#fff;
}
}
