css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
        url-prefix("http://www.dgs.dk/kort.html"),
        url-prefix("http://www.degulesider.dk/kort.html"),
        url-prefix("http://www.degulesider.dk/map"),
        url-prefix("http://www.dgs.dk/mapalocation"),
        url-prefix("http://www.degulesider.dk/mapalocation"),
        url-prefix("http://en.degulesider.dk/mapalocation"), 

        url-prefix("http://www.dgs.dk/rute"),
        url-prefix("http://www.degulesider.dk/rute"),
        url-prefix("http://en.degulesider.dk/rute"),
        url-prefix("http://en.degulesider.dk/finddirections")      

{
/* ======================= START BELOW ========================== */


/* Header */
#hdr {
   height: 0px;
}

/* DGS logo */
#hdr a.logo {
   display:none !important;
}

/* my dgs links */
#userLinks {
  clear: none !important;
  float: right !important;
  margin: 10px 50px 50px 5px !important;
  overflow: visible !important;
}

/* header tabs.. */
#hdr .hdrNav {
  padding-top: 0px !important;
  float: right;
  width: auto !important;
}

/* Location input box */
#searchFields.locationSearchFields .formInp input {
  width: auto !important;
}

/* header height */
div.searchForm {
  border-top: 2px solid #FFFFFF;
  height: 75px;
  position: relative;
  padding:0 !important;
  z-index:200 !important;
}

/* Footer */
#footCont {
   display:none !important;
}

/* Go full screen :) */

.dsMap .viewport {
  background-color: #F2F0E1;
  bottom: 0;
  left: 0;
  position: absolute !important;
  right: 0;
  top: 0;
  z-index:2 !important;
}

/* site bar add */
.sideAd {
   display:none !important;
}
/* ======================= END BELOW ========================== */ 
}
